INSERT INTO `wineries` (`id`, `title`, `signature`, `description`, `header_image`, `logo_image`, `catalog_image`, `region_id`, `meta_title`, `type_id`, `layout_id`, `is_nominal`, `nominal_image`, `slug`, `coordinate_lon`, `coordinate_lat`, `status`, `created_at`, `updated_at`, `meta_description`, `meta_keywords`) VALUES
(1, 'Имение Сикоры', 'Краснодарский край, г. Новороссийск, х. Семигорский', '<p>Винодельня, расположенная рядом с Новороссийском, абсолютного нового формата. Ставка в развитии проекта была сделана на создание условий для производства вин самого высокого качества, в которых раскроется терруар Семигорья. Хозяйство ориентировалось на французский опыт классического виноделия, заручившись поддержкой винодела из Франции Гаэль Брюллон.</p>', 'wineries\\July2020\\x6ORh5YDYxFqANeQlg2D.jpg', 'wineries\\July2020\\sKU6o7gFdoYK09J4tKF5.png', 'wineries\\July2020\\KBl9tuXpqb26PJSS6PDq.jpg', 2, 'Купить вино Имение Сикоры, рислинг, каберне совиньон семейный резерв', 1, 0, 0, NULL, 'imenie-sikory', '37.612936', '44.883282', 'ACTIVE', NULL, '2020-07-30 17:06:45', 'Александр Сикорский основал Имение Сикоры и сделал ставку на вина рислинг Имение Сикоры, каберне совиньон семейный резерв, пино нуар, шардоне. Имение Сикоры получило высокую оценку у Паркера.', NULL),
(5, 'Винный Дом Раевское', 'Краснодарский край, г. Новороссийск, ст. Натухаевская', '<p>Проект, который можно назвать&amp;nbsp;Возрождением&amp;nbsp;Российского виноделия, берет начало в 2005 году. Именно тогда основатель компании Eastfield Agro Holding Limited бизнесмен из Словакии Игорь Ледецки не только понял, что Российские вина могут и будут соперничать с лучшими образцами старосветских вин из Италии, Испании и Франции, но и предугадал бурное развитие Российского виноделия через несколько лет.</p>', 'wineries\\July2020\\rbSDdJz0IKxKuWrGtmxW.jpg', 'wineries\\July2020\\8q43pngEJE8imTUs8ZBy.png', 'wineries\\July2020\\ABBnnR009FrNNIaphfEh.png', 2, 'Купить вино Раевское, ренессанс, русское вино', 1, 0, 0, NULL, 'Винный Дом Раевское', '37.560679', '44.899904', 'ACTIVE', NULL, '2020-07-09 12:29:26', 'Винодельня Раевское и знаменитое русское вино ренессанс. Узнать о винодельне Раевское можно на сайте russianvine.ru и в магазине русское вино в Москве, на Расплетина 21', NULL),
(6, 'Абрау-Дюрсо', 'Краснодарский край, г. Новороссийск, с. Абрау-Дюрсо', '<p>Крупнейший в России производитель игристого вина премиального сегмента. \"Абрау-Дюрсо\" ведёт историю от даты указа императора Александра II - 25 ноября 1870 года, когда у озера Абрау и речки Дюрсо было создано удельное имение \"Абрау-Дюрсо\", которое стало принадлежать царской семье. Первый виноград был собран в 1877 году, а через пять лет бочки с вином размещались уже в оборудованном по всем правилам погребе. А в 1891 году на пост главного винодела назначен Лев Голицын. Он уже отстроил завод и оборудовал 5 туннелей для выдержки вина.</p>', 'wineries\\July2020\\kAWuI40wSrEtnkWXlStE.jpg', 'wineries\\July2020\\uXUd8rpFfZeZljUZ5KeI.png', 'wineries\\July2020\\XHbBsKo1M2gfqL94e4Jb.png', 2, 'Купить вино Абрау Дюрсо, блан де блан, империал', 1, 0, 0, NULL, 'Абрау-Дюрсо', '37.599558', '44.701792', 'ACTIVE', NULL, '2020-07-09 12:09:29', 'Шампанское Абрау Дюрсо - блан де блан, империал, Виктор Дравеньи. Всё игристое Абрау Дюрсо в винотеке русское вино, на улице Расплетина 21', NULL),
(7, 'Лефкадия', 'Краснодарский край, Крымский район, с. Молдаванское', '<p>Этот проект действительно уникален: он сочетает в себе необыкновенную природу Кубани, неповторимый терруар, инновационные технологии и, в первую очередь, выдающихся профессионалов, объединенных и вдохновлённых общим делом.</p>', 'wineries\\July2020\\cUICb57gt8YgGek2f31q.jpg', 'wineries\\July2020\\n1KaoPCzKbSY8ZqdRvpa.png', 'wineries\\July2020\\AjRczGjFTL40Wt29vT5u.jpg', 2, 'Купить вино Лефкадия, Лефкадия резерв, Темелион', 0, 9, 0, NULL, 'Лефкадия', '37.870700', '44.946087', 'ACTIVE', NULL, '2020-07-22 08:01:55', 'Долина Лефкадия и Николаев и сыновья. Лекадия создала премиальные вина - шардоне, мальбек, Лефкадия резерв, лучшее игристое России - Темелион Лефкадия.', NULL),
(8, 'Цимлянское', 'Ростовская область, г. Цимлянск', '<p>Столица донского правобережья Цимла дала наименование производимым здесь винам, напоминавшим своими качествами знаменитые рейнские. В старину Цимлянское вино готовилось на Дону и поставлялось в больших количествах петербургской и московской знати. Среди любителей его были Пушкин, Лермонтов, Чехов, Тургенев.&amp;nbsp; А Кутузов отмечал победу над французами именно цимлянским, в Париж, на площадь Свободы были поставлены 3 тысячи бутылок игристого вина.&amp;nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', 'wineries\\July2020\\jVo0gmorM3IzPOdpPlGC.jpg', 'wineries\\July2020\\Ak6CV49op9P6th3jMZII.png', 'wineries\\July2020\\CRv3NLv02jOs9kZldVQ3.png', 3, 'Цимлянское шампанское купить, Казачка', 0, 0, 0, NULL, 'Цимлянское', '42.105853', '47.658879', 'ACTIVE', NULL, '2020-07-22 08:55:09', 'Цимлянские вина - старейший завод игристого вина. Шампанское Цимлянское приготовленное старым казачьим - знаменитая Казачка. Цимлянские вина - донское шампанское.', NULL),
(9, 'Вилла Виктория', 'Краснодарский край, Новороссийск, х. Ленинский Путь', '<p><span style=\"text-align: justify;\">Реализованная идея семьи Яновых создать хозяйство бургундско-луарского стиля. Самая крайняя и самая высоко расположенная винодельня Анапской долины по пути к Новороссийску. Консультантом хозяйства выступил Джон Ворончак. Первые вина делались в &laquo;Мысхако&raquo; Романом Неборским.</span></p>', 'wineries\\July2020\\jymlwVUBoGPodJiLL60S.jpg', 'wineries\\July2020\\2lMAvdz7gLcD8LzhfGtB.png', 'wineries\\July2020\\7sxa682MnopkwxPgsjPS.png', 2, 'Купить вино Вилла Виктория, каберне фран, рислинг, шардоне', 1, 0, 0, NULL, 'Вилла Виктория', '37.627732', '44.862104', 'ACTIVE', NULL, '2020-07-09 12:12:37', 'Винодельня Вилла Виктория и её основатель Сергей Янов на сайте русское вино russinvine.ru Вилла Виктория под Новороссийском.', NULL),
(11, 'Винодельня Узунов', 'Краснодарский край, Темрюкский район, п. Сенной', '<p>Семейный проект Ярослава Узунова, винодела в четвёртом поколении. Авторские вина из уникального терруара Таманского полуострова. Все вина производятся в небольших количествах, годовой тираж вин ограничен и составляет всего 50 000 бутылок в год.</p>', 'wineries\\July2020\\LWS3RpQAewi6RFWsx7MW.jpg', 'wineries\\July2020\\nzRbHOsjhBNCuOHy3sp5.png', 'wineries\\July2020\\2gTVR19QjcsriZIj1Lyu.png', 2, 'Купить вино Узунов, семь морей, каберне совиньон', 1, 0, 0, NULL, 'vinodel-nya-uzunov', '36.994249', '45.286223', 'ACTIVE', NULL, '2020-07-14 08:12:15', 'Винодельня Узунов, семейная винодельня четы Узуновых. Вино семь морей белое и красное и розовое Семь Морей, а также саперави Узунов, каберне совиньон и РАШ Семь Морей на сайте russianvine.ru', NULL),
(12, 'Вилла Звезда', 'Ростовская область, хутор Малая Мартыновка', '<p>Винодельня нового поколения, основанная в 2012 году Игорем Губиным, в винодельческой зоне Долина Дона. Это первый проект на Дону, осуществленный за два года с \"нуля\", в уникальном терруаре междуречья рек Сал и Дон.</p>', 'wineries\\July2020\\RuHqnqW8TfMilkA5JI83.jpg', 'wineries\\July2020\\vmn01Bgujj5sCT8eFXH1.jpg', 'wineries\\July2020\\DdXpxMtkf03etSu6HuN5.png', 3, 'Вилла Звезда купить вино, Атаман, красностоп Вилла Звезда', 1, 0, 0, NULL, 'Вилла Звезда', '41.834431', '47.335713', 'ACTIVE', NULL, '2020-08-17 06:46:26', 'Донская винодельня Вилла Звезда. Вино Вилла Звезда это Атаман, красностоп, Шато Южное, Вилла Звезда. Все о винодельне Вилла Звезда на сайте русское вино.', NULL),
(13, 'Шато Пино', 'Краснодарский край, г. Новороссийск, с. Федотовка', '<p>Новое прочтение известного терруара у мыса Мысхако. Молодое хозяйство, расположенное недалеко от черноморского курорта Широкая Балка, в живописном месте на склонах горы Колдун. Великолепная лоза и уникальное расположение уже сегодня позволило выпускать как свежие минеральные белые вина, так и мощные красные, особенно интересен Шираз и Пино Нуар. &amp;nbsp;</p>', 'wineries\\July2020\\5710MZl8VDOKcIwYadu8.jpg', 'wineries\\July2020\\bQ8WbjoGwJxSXX6pI4Xy.png', 'wineries\\July2020\\HKmLyv22kJfQfYWCRaz5.png', 2, 'Купить вино Шато Пино', 0, 0, 0, NULL, 'Шато Пино', '37.708531', '44.683432', 'ACTIVE', NULL, '2020-07-22 08:52:33', 'Шато Пино и Усадьба Мысхако это один проект в терруаре Мысхако, под Новороссийском. Вина Шато Пино пример соотношения цены и качества. Выбрать вино онлайн на сайте russianvine.ru - пино гри и пино нуар красное и розовое Шато Пино.', NULL),
(14, 'Винодельня Дмитрия Гусева', 'Волгоградская область, г. Дубовка', '<p>Микровинодельня из Волгоградской области (Нижняя Волга), уникальна и единственная в своем роде в этом регионе. Хозяйство было основано в 1990 году. Выпускает вина как из автохтонных сортов винограда, так и работает с гибридами советской селекции. Дмитрий Гусев сегодня является самым ярким и интересным виноделом Нижней Волги и добился серьёзных результатов в этом весьма не простом винодельческом регионе.</p>', 'wineries\\July2020\\S07wyjGJdNOfe5mUSSkQ.jpg', 'wineries\\July2020\\dUpxNUa6XqSys2K11LTV.jpg', 'wineries\\July2020\\qU2sbsPLd4f7P22SQ2Sx.png', 5, 'Дмитрий Гусев вино купить, вино из Волгограда, винодел из Дубовки, красностоп, мариновский', 1, 0, 0, NULL, 'vinodel-nya-dmitriya-guseva', '44.820623', '49.051470', 'ACTIVE', NULL, '2020-07-14 07:35:34', 'Вино Дмитрия Гусева - винодельня КФХ Лоза из Волгограда. Потомственный винодел Дмитрий Гусев под Дубовкой выпускает вина из  автохтонов. Мариновский, августовский, красностоп, сибирьковый - это вина Дмитрия Гусева.', NULL),
(15, 'Винодельня Константина Дзитоева', 'Северная Осетия, г. Владикавказ', '<p>Единственная микровинодельня из региона Долина Терека. Авторский проект винодела - энтузиаста Константина Дзитоева из Владикавказа. Одна из первых лицензированных фермерских виноделен в России.</p>', 'wineries\\July2020\\ff4SwQBs3JgpdximLmBE.jpg', 'wineries\\July2020\\ZEZMX9SLhgZTjrO6ekZo.png', 'wineries\\July2020\\TXw9gMCJGuFHYXL1SWBU.png', 8, 'Вина Константина Дзитоева, вино из Осетии, 60/40, Красностоп, Русское Вино', 1, 0, 1, 'wineries\\August2020\\mBEXOp0ykFFjEU8MqXT3.jpg', 'vinodel-nya-konstantina-dzitoeva', '44.650939', '43.028420', 'ACTIVE', NULL, '2020-08-04 01:25:26', 'Винодельня Дзитоева - вино из Осетии. Вина Дзитоева это красностоп, саперави, ркацители, вино 60/40. Особенно интересно шампанское Дзитоева белое и брют. Все вина Дзитоева есть на сайте русское вино.', NULL),
(16, 'Собер Баш', 'Краснодарский край, Северский район, с.п. Смоленское', '<p>Винодельня получила своё название в честь горы Собер Баш, вид на которую открывается с виноградников хозяйства. Переводится как \"Ведьмина Гора\", и часть вин также называются в честь богов. Не тех, что некогда сидели на Олимпе, а малых, которые по легенде собирались именно здесь, чтобы пировать и проказничать. &amp;nbsp; &amp;nbsp;</p>', 'wineries\\July2020\\XmZaENCmc9ji5KkK03TF.jpg', 'wineries\\July2020\\GcahHgH3YBlVmpNRKTI1.png', 'wineries\\July2020\\VPQf8QDrN6eh3ybxMnhP.png', 2, 'Купить вино Собер Баш, Афа, Красностоп', 1, 0, 0, NULL, 'sober-bash', '38.835131', '44.868581', 'ACTIVE', NULL, '2020-07-30 17:09:25', 'Винодельня Собер Баш и ее основатель Андрей Куличков. Все вина Собер Баш - Афа, Адау, Агуна, красностоп, саперави, аврора, каберне фран Собер Баш можно найти в винотеке русское вино', NULL),
(17, 'Винодельня Гунько', 'Краснодарский край, Крымский район, ст. Нижнебаканская', '<p>Gunko Winery - новая точка на винной карте России. Хозяйство начало свою историю в 2017 году. Руководит проектом Сергей Коротков, винодел с большим опытом работы в различных винных хозяйствах России, начинавший свой путь под руководством Патрика Леона. Винодельня рассчитана на производство не более 50 000 бутылок в год.</p>', 'wineries\\July2020\\1XBAQAovexW3xQQeuhQs.jpg', 'wineries\\July2020\\0YqAjM0ejosIRqO0F0X0.png', 'wineries\\July2020\\Jhk27hLEdswu9JzfrYr3.png', 2, 'Вино Гунько, совиньон блан Гунько, анкор мальбек и анкор розе Гунько', 1, 0, 0, NULL, 'vinodel-nya-gun-ko', '37.866648', '44.866544', 'ACTIVE', NULL, '2020-07-14 07:32:49', 'Винодельня Гунько под Крымском. Знаменитый совиньон блан Гунько, а также анкор мальбек и анкор розе Гунько. А какое крутое саперави Гунько. Всё есть на сайте русского вина russianvine.ru', NULL),
(18, 'Дом Захарьиных', 'Республика Крым, с. Кочергино / с. Плодородное', '<p>Автор идеи,&amp;nbsp; инициатор создания, владелец и руководитель хозяйства - Валерий Захарьин, человек чьи энергия и убеждённость стали надёжным фундаментом проекта. При том что у проекта есть только собственные виноградники - 88 га и нет собственной производственной площадки.&amp;nbsp;</p>', 'wineries\\July2020\\zTlfBqVUpMDLIJzpoTNI.jpg', 'wineries\\July2020\\o2c3W6gjV7HMWe0zTO5g.png', 'wineries\\July2020\\Y4Ip99B5GLIsgj64wml9.png', 4, 'Купить вино Захарьина, Крым, кефесия, сары пандас, кокур брют', 0, 0, 0, NULL, 'Дом Захарьиных', '33.749023', '44.838799', 'ACTIVE', NULL, '2020-07-30 17:02:31', 'Вина Захарьина из Крыма. Кефесия и сары пандас автохтонные вина Захарьина. Всё о винах Валерия Захарьина на сайте русское вино russianvine.ru', NULL),
(19, 'Эльбузд', 'Ростовская область, Азовский район, х. Эльбузд', '<p>Имя хозяйство получило по названию посёлка и реки, на склонах которой расположились её виноградники. Река Эльбузд служит линией территориального разграничения Краснодарского края и Ростовской области. Её название переводится с тюрских наречий как \"Ледяная река\".</p>', 'wineries\\July2020\\kWOL2TZlhYavXSPsNPG6.jpg', 'wineries\\July2020\\Ko7o55SMDqknvrw82tqn.png', 'wineries\\July2020\\yBb7UDg4BmBkWOg7WhNq.png', 3, 'Вина Эльбузд купить, каберне фран Эдьбузд, гевюрцтраминер, рислинг', 0, 0, 0, NULL, 'el-buzd', '39.715732', '48.870318', 'ACTIVE', NULL, '2020-07-30 17:15:49', 'Винодельня Эльбузд из Ростова-на-Дону. Лучшие вина Эльбузд - каберне фран, рислинг, гевюрцтраминер, мерло. Донская винодельня расположена в поселке Эльбузд.', NULL),
(34, 'Винодельня Сергея Бескоровайного', 'Республика Крым, г. Севастополь, п. Бельбек', '<p>Основал винодельческое хозяйство семейного типа Сергей Бескоровайный. Он в 2012 году загорелся идеей выпускать авторское вино и в 2014 году были заложены собственные виноградники, площадью 3,5 га, в северной части Севастополя. Небольшой тираж выпускаемой продукции позволяет гарантировать именно тот результат, который поставил перед собой винодел, отсюда и получается по-настоящему \"авторское\" вино.</p>', 'wineries\\July2020\\x3IgXCXSb9IVJ0KfzPC1.jpg', 'wineries\\July2020\\i1nnWvwQJGcJ4MjTroKH.png', 'wineries\\July2020\\4h7oB8xglffNuHEacYm0.jpg', 4, 'Бельбек Сергей Бескоровайный вино купить, Крым, рислинг Бельбек', 1, 1, 0, NULL, 'vinodel-nya-sergeya-beskorovajnogo', '33.820807', '44.642199', 'ACTIVE', NULL, '2020-07-14 08:05:01', 'Бельбек - винодельня Сергея Бескоровайного в Крыму. Вина Сергея Бескоровайного вызывают интерес - рислинг, мускат, каберне фран и каберне совиньон Бельбек. Все о винах Бельбек на сайте russianvine.ru', NULL),
(35, 'Сатера', 'Республика Крым, Бахчисарайский район, с. Долинное', '<p>Хозяйство отсчитывает свою историю от 2000 года и принадлежит Игорю Самсонову. Занимаясь в 90-е годы поставками вин и крепких спиртных напитков в качестве дистрибьютера, он задался целью создания собственного производства вина. Формирование и становление \"Сатеры\" произошло на базе типового совхоза-завода, существовавшего в этом регионе с 1978 года.</p>', 'wineries\\July2020\\HS1S4wWWvJU0AzFjpycr.jpg', 'wineries\\July2020\\2rIj6aa5bEzEbdVMSaQs.png', 'wineries\\July2020\\trG2bPhe9tmPPjDlzLou.png', 4, 'Купить вино Эссе, Кача Вэлли, Сатера', 0, 24, 0, NULL, 'Сатера', '33.765986', '44.753936', 'ACTIVE', NULL, '2020-07-30 04:41:58', 'Винодельня Игоря Самсонова выпустила вина Эссе, Кача Вэлли и шампанское Эссе. Крымская винодельня Сатера и вина Unpluged цимлянский чёрный, оражевое ркацители Unpluged', NULL),
(36, 'Золотая Балка', 'Республика Крым, г. Севастополь, Балаклава', '<p>Все начиналось с винного подвала генерала Александра Витмера, подаренного в 1889 году городу Балаклаве вместе с виноградниками. На сегодня Агрофирма Золотая Балка крупнейший в Крыму производитель игристого вина из собственного винограда. &amp;nbsp; &amp;nbsp;&amp;nbsp;</p>', 'wineries\\July2020\\3Awq4m48YhqvT243XXyr.jpg', 'wineries\\July2020\\rRkbmpbikYqQtT4uerWl.png', 'wineries\\July2020\\5bfBLduZbfY9g5v0NFmW.png', 4, 'Купить вино Золотая Балка, Крым, Балаклава', 0, 5, 0, NULL, 'Золотая Балка', '33.600000', '44.501414', 'ACTIVE', NULL, '2020-07-17 12:13:54', 'Золотая Балка - завод крымского шампанского. Большой ассортимент игристых вин Золотая Балка и шампанского Балаклава на сайте русское вино.', NULL),
(37, 'Вина Арпачина', 'Ростовская область, Багаевский район, ст. Маныческая', '<p>Совершенно новый проект из Ростовской области, который полностью сконцентрировался на выпуске вин из донских автохтонов. Винодельня принадлежит семье Юрия Малика. Многие хозяйства создаются целенаправленно, но возникновение в станице Маныческой винодельни стало итогом совпавших обстоятельств, результат которых превзошёл самые смелые чаяния его участников.&amp;nbsp;</p>', 'wineries\\July2020\\Ifbe0ohkV4OjHyv5UWFt.jpg', 'wineries\\July2020\\ioDfyA2AzarvFxxbBd8Z.png', 'wineries\\July2020\\JYJso5v4R6OPiNRj5obc.png', 3, 'Вина Арпачина купить, Вино Арпачин, вино из Ростова, шампанское Арпачино', 1, 0, 0, NULL, 'Вина Арпачина', '40.257335', '47.234659', 'ACTIVE', NULL, '2020-07-09 12:19:42', 'Вина Арпачина - ростовская винодельня, выпускает вина из автохтонов. Арпачино - шампанское из Ростова. Вина Арпачина - это сибирьковый, пухляковский, кумшацкий, красностоп и цимлянский чёрный.', NULL),
(40, 'Массандра', 'Республика Крым, г. Ялта, пгт. Массандра', '<p>Первым владельцем Массандры был французский маршал Карл Нассау-Зиген. Красота мест произвела неизгладимое впечатление. А уже после, в 19 веке земли Массандры прошли череду смен собственников и оказались в числе недвижимых имуществ Императорского дома, под управлением Льва Голицына. За три года было построено одно из лучших погребных хозяйств в мире на глубине до 50 м с тоннелями.</p>\r\n<p>&amp;nbsp;&lt;br&gt;</p>', 'wineries\\July2020\\8WDhjlY6S5WyOllwatev.jpg', 'wineries\\July2020\\dGRaofQb9Y38z7LAcAzs.png', 'wineries\\July2020\\NIC2lJsz4MfAH7D0QdLy.jpg', 4, 'Купить вино Массандра, мускат позднего сбора, кефесия', 0, 6, 0, NULL, 'Массандра', '34.187588', '44.517315', 'ACTIVE', NULL, '2020-07-22 09:25:04', 'Вина Массандра связаны с именем Лев Голицын. Лучшие вина Массандра - мускат позднего сбора, кефесия, Массандра каберне совиньон. Более подробно о Массандра на сайте русское вино.', NULL),
(41, 'Шато Сан Даниль', 'Республика Крым, пгт. Гурзуф, пос. Даниловка', '<p>История появления хозяйства, расположенного под Ялтой, имеет старинные корни. В начале 19 века винодельню Ай-Даниль приобрёл генерал-губернатор, граф Воронцов. Он привлёк иностранных виноделов, в частности на винодельне трудился француз Гаузер. А в 1891 году хозяйство перешло в Главное управление уделов при Министерстве императорского двора, под предводительством князя Льва Сергеевича Голицына. &amp;nbsp;</p>', 'wineries\\July2020\\UrTvXI1KvGvm7tVwWxuP.jpg', 'wineries\\July2020\\OgFc9pvWf6QNnbC5veO4.png', 'wineries\\July2020\\D6Uhkl3IOl3GgkpBQGmU.png', 4, 'Купить вино Шато Сан Даниль, бомонд, трамонте, мускат', 0, 3, 0, NULL, 'Шато Сан Даниль', '34.259760', '44.521494', 'ACTIVE', NULL, '2020-07-15 13:32:40', 'Винодельня Шато Сан Даниэль в Крыму. Крымские сладкие вина от Шато Сан Диниэль мускат и трамонте и белое сухое вино Бомонд на сайте русское вино russianvine.ru', NULL),
(42, 'Alma Valley', 'Западно-Предгорный Крым, долина реки Альма, пос. Вилино', '<p>Первый суперсовременный винодельческий проект созданный с нуля в Крыму. Поражает своим размахом и тщательным подходом к выпуску премиальных вин нового уровня в современной истории виноделия. Помимо сухих вин с высоким потенциалом, предприятие создаёт моду на потребление вин природно-сладких, выпуская вина с остаточным виноградным сахаром.</p>', 'wineries\\July2020\\UOviQXDskeqOOyO897MH.jpg', 'wineries\\July2020\\ggZCrwrEbkXyfEybMBXx.png', 'wineries\\July2020\\7z3mQPIyn2atl5niICJP.jpg', 4, 'Купить вино Альма Валлей / Alma Valley, Крым, айсвайн', 0, 8, 0, NULL, 'Alma Valley', '33.701052', '44.844308', 'ACTIVE', NULL, '2020-07-24 05:28:54', 'Альма Валлей (Alma Valley) современная винодельня Крыма. Вина Альма Валлей резерв - шираз, мерло, шардоне. Айсвайн Альма Валлей петит арвин на сайте русское вино.', NULL),
(43, 'Дом Бюрнье  (Domanie Burnier)', 'Краснодарский край, ст. Натухаевская', '<p>Уникальная история создания гравитационной винодельни в России потомственным виноделом из Швейцарии. В 2003 году были заложены первые виноградники, а в 2009 году первая партия вина уходит на экспорт в Европу. &amp;nbsp;</p>', 'wineries\\July2020\\njbSF8B2nwFzjteN1H9U.jpg', 'wineries\\July2020\\rNYQh76wxWjwyuZogoJO.png', 'wineries\\July2020\\FUG6f8xKPCB60lRYtjyi.jpg', 2, 'Купить вино Бюрнье, красностоп, люблю, каберне фран', 0, 4, 0, NULL, 'Дом Бюрнье  (Domanie Burnier)', '37.581455', '44.916450', 'ACTIVE', NULL, '2020-07-16 12:55:04', 'Швейцарец Рено Бюрнье выпустил лучшие вина в России. Дом Бюрнье производит красностоп, люблю, каберне фран, каберне совиньон, шардоне, мерло под маркой Бюрнье.', NULL),
(44, 'Солнечная Долина', 'Республика Крым, г. Судак, с. Солнечная Долина', '<p>История хозяйства началось с 1888 года, когда Лев Голицын приобрёл Токлукское имение у статской советницы Эссен, а чуть позднее заочно продает часть имения князю Горчакову. Символом заключенного договора явилась новая марка вина \"Каберне князя Горчакова 1888 года\", это вино было подарено лично Голицыным императору Николаю II.</p>', 'wineries\\July2020\\SMN9phu160vhJA8W9xIY.jpg', 'wineries\\July2020\\JBneFkipEOwCJBSn6HIT.png', 'wineries\\July2020\\ahrhHO26yZ0dCh5Fq9td.png', 4, 'Купить вино Солнечная Долина, меганом, чёрный доктор, чёрный полковник', 0, 7, 0, NULL, 'Солнечная Долина', '35.102640', '44.874778', 'ACTIVE', NULL, '2020-07-24 02:45:29', 'Вина Солнечная Долина это - меганом и меганом резерв, чёрный доктор и чёрный полковник. Белое десертное вино Солнечная Долина всегда есть в магазине русское вино.', NULL),
(45, 'Шато Ле Гран Восток', 'Краснодарский край, Крымский район, х. Садовый', '<p>Первое в нашей стране винодельческое хозяйство полного цикла - от выращивания винограда до розлива вина в бутылку, созданное по образцу французского шато. Уникальный российско-французский проект стартовал под руководством Николая Пинчука, где главным виноградарем и виноделом выступил Франк Дюссенер.</p>', 'wineries\\July2020\\r0ewos2pRlscz6Ye7lTi.jpg', 'wineries\\July2020\\Fw1QrxGEBTSxPaOfbFph.png', 'wineries\\July2020\\ask8TriPPux2XX4pCZmw.png', 2, 'Купить вина Шато ле гран Восток', 0, 2, 0, NULL, 'Шато Ле Гран Восток', '37.753460', '45.015946', 'ACTIVE', NULL, '2020-07-15 10:00:57', 'Королевскйи дуб Шато ле гран Восток и винодел Франк Дюссенер. Побробно на сайте russianvine.ru Выбрать вино онлайн и купить русское вино в Москве на Расплетина 21', NULL),
(47, 'Винодельня Павла Швеца UPPA', 'Республика Крым, г. Севастополь, с. Родное', '<p>Винодельческое хозяйство UPPA основано в 2008 году Павлом Швецом, человеком по-настоящему влюбленным в вино и в свой родной край. UPPA Winery единственная винодельня в России, которая производит вино биодинамическим способом. &amp;nbsp;</p>', 'wineries\\July2020\\WGWTqgLIXvhlINHf2xy5.jpg', 'wineries\\July2020\\0zBSk4IwhwEHW0ExDA6Y.png', 'wineries\\July2020\\oA5CkvkIPNDx4dvY3eYV.png', 4, 'Вино Павел Швец UPPA купить, Севастополь, мускат амбер Павел Швец, пино нуар карбоник, кокур баррель', 1, 10, 1, 'wineries\\August2020\\0SRnohcpi3t4ETxYQHDy.jpg', 'Винодельня Павла Швеца UPPA', '33.739959', '44.553909', 'ACTIVE', NULL, '2020-08-04 01:24:50', 'Винодельня Павел Швец UPPA - биодинамика в Севастополе. Винодел Павел Швец UPPA создатель лучшего пино нуара в России. Вина Павла Швеца UPPA Жека - неббиоло, барбера, мускат амбер, кокур баррель, пино нуар чан.', NULL),
(48, 'Кубань-Вино', 'Краснодарский край, Темрюкский район, станица Старотитаровская', '<p>Таманский полуостров в Краснодарском крае издревле был центром виноделия. Еще в античные времена виноторговлю отсюда вели с другими регионами причерноморья и нынешней Европы.</p>', 'wineries\\July2020\\vA8CvHiSZI23XmEhKeQy.jpg', 'wineries\\July2020\\rIsFJzv7WhIe9PyHxIko.png', 'wineries\\July2020\\xCHKzgm7D1JQBZmR56cn.png', 2, 'Кубань-Вино купить вина Кубани, мадера, кюве Александр', 0, 11, 0, NULL, 'Кубань-Вино', '37.159512', '45.224697', 'ACTIVE', NULL, '2020-07-24 12:42:15', 'Винодельня Кубань-Вино, самый крупный производитель вина в России. Все о Кубань-Вино на сайте русское вино russianvine.ru', NULL),
(49, 'Винодельня Ведерниковъ', 'Ростовская область, хутор Ведерников', '<p>Название хозяйства связано с наименованием хутора Ведерников, который расположен на правом берегу реки Дон, в 160 километрах от Ростова-на-Дону. Казаки, заселившие берега Дона более 500 лет назад смогли возродить виноградарство и виноделие.</p>\r\n<p>Создатель хозяйства Валерий Тройчук достиг серьёзных результатов, работая с автохтонными сортами, которые высоко оценили иностранные эксперты и после этого они заговорили о Русском Вине.</p>', 'wineries\\July2020\\2rCJNDt2p1I1gVRG4aoY.jpg', 'wineries\\July2020\\kuLELK5YeJ22JTc5TaOC.png', 'wineries\\July2020\\PSGIow4txp6R6Mfj2NH8.png', 3, 'Ведерников винодельня, вино Ведерников, Красностоп Ведерников', 0, 12, 0, NULL, 'Винодельня Ведерниковъ', '41.145993', '47.582082', 'ACTIVE', NULL, '2020-07-27 00:43:17', 'Вино Ведерников это автохтоны Дона - Красностоп Ведерников, цимлянский чёрный Ведерников, сибирьковый Ведерников. Донская винодельня Ведерников была первой, выпустившая автохтон Красностоп Золотовский.', NULL),
(50, 'Винтрест Саук-Дере', 'Краснодарский край, Крымский район, п. Саук-Дере', '<p>Винтрест &laquo;Саук-Дере&raquo; &mdash; уникальное место со знаменательной историей. Первые виноградники появились тут в далеком 1926 году в период зарождения советского виноделия, исторические каменоломни стали превращаться в место для выдержки вин. В 50-х годах предприятие &laquo;Саук-Дере&raquo; уже считалось жемчужиной советского виноделия.</p>', 'wineries\\July2020\\wtQIUuv0uvGnZUzplqNO.jpg', 'wineries\\July2020\\pw2FXhE3txlZarM7292W.png', 'wineries\\July2020\\YEmyE4YSSr4cJZs45sT7.png', 2, 'Купить вина Саук-Дере, ледяное вино рислинг', 0, 13, 0, NULL, 'vintrest-sauk-dere', '37.887159', '44.900288', 'ACTIVE', NULL, '2020-07-14 08:16:23', 'Легендарное Саук-Дере с подземными тоннелями. Вина Саук-Дере - ледяное вино рислинг, авторский ассамбляж, шардоне поздний сбор. Все о Саук-Дере на сайте russianvine.ru', NULL),
(51, 'Усадьба Дивноморское', 'Краснодарский край, Геленджикский район,  с. Дивноморское', '<p>Винодельня премиального уровня, поставила себе задачу на создание вин высокого уровня. Для этого хозяйство пригласило мэтра итальянской энологии Риккардо Катарелла и талантливого энолога Маттео Колетти. Относительно молодое предприятие успешно конкурирует с европейскими винами и завоёвывает престижные награды на международных конкурсах.</p>', 'wineries\\July2020\\PBpnqpJXzflzA34E8IF3.jpg', 'wineries\\July2020\\OpVEQ3FrzFz7cNJzOZrG.png', 'wineries\\July2020\\yta684HzNqW7jfxSed7m.png', 2, 'Купить вино Усадьба Дивноморское, Ребо и Марселан можно в магазине русское вино, на Расплетина 21', 0, 14, 0, NULL, 'Усадьба Дивноморское', '38.137311', '44.499768', 'ACTIVE', NULL, '2020-07-27 09:38:56', 'Усадьба Дивноморское это вина географического наименования, Усадьба Дивноморское ребо, марселан, траминер, мускат оттонель, пино блан, западный склон и восточный склон - всё вина можно найти на сайте russianvine.ru', NULL),
(52, 'Винодельня Юбилейная', 'Краснодарский край, Темрюкский район, п. Красноармейский', '<p>Винодельня &laquo;Юбилейная&raquo; расположена в поселке Красноармейский Краснодарского Края. История винодельни началась в 1966 году, когда на базе винпункта Сенновского винзавода было образовано самостоятельное предприятие. За прошедшие полвека появились новые технологии производства, современное оборудование и лаборатория, а территория урожайных земель компании увеличилась до 2500 гектаров. Росло и качество производимых вин. Винодельня &laquo;Юбилейная&raquo; всегда была новатором и оснащалась самым современным оборудованием.</p>', 'wineries\\July2020\\waYFdYj5T2RMp088VOhB.jpg', 'wineries\\July2020\\Aaa0mSl26ew7EkH767os.png', 'wineries\\July2020\\ijnPiLnlpg6slryUhlSV.png', 2, 'Купить вино Юбилейная, достойный, селект', 0, 15, 0, NULL, 'vinodel-nya-yubilejnaya', '36.871414', '45.395061', 'ACTIVE', NULL, '2020-07-14 08:13:59', 'Винодельня Юбилейная - пример достойного вина на каждый день. Особенно интересны советские сорта - кристалл, виорика, достойный, цитронный магарача, первенец магарача, рубин голодриги.', NULL),
(53, 'Инкерман', 'Республика Крым, г. Севастополь, Инкерман', '<p>Международный холдинг, который объединил крупнейшие хозяйства региона, юридически сложился в 2011 году. Его штаб-квартира обосновалась в Стокгольме (Швеция). В состав холдинга вошли два ООО:&amp;nbsp; &laquo;Инкерманский завод марочных вин&raquo; и &laquo;Качинский+&raquo;, а также мощная агрофирма &laquo;Черноморец&raquo;.&amp;nbsp; В результате во владениях холдинга оказались огромные площади (3 тысячи га) живописных и благоприятных для виноградарства долинах рек Кача и Альма, и один из самых развитых и знаменитых крымских винзаводов - Инкерманский.&amp;nbsp;</p>', 'wineries\\July2020\\MK2j3l8nUhzLy3eXmPLh.jpg', 'wineries\\July2020\\ra218ISKdrd7T7Kj7sSG.png', 'wineries\\July2020\\UPSWPlfJ7xuKBpzLt4Pp.png', 4, 'Купить вино Инкерман, Севастополь, Крым', 0, 16, 0, NULL, 'Инкерман', '33.610071', '44.613588', 'ACTIVE', NULL, '2020-07-28 08:30:45', 'Инкерман старейший завод Севастополя. Вина Инкермана это марочные вина Севастополь 1994 и серия Эритаж Инкерман. Подробно об Инкерман на сайте русское вино.', NULL),
(54, 'Вилла Романов', 'Краснодарский край, Темрюкский район, ст. Голубицкая', '<p>Андрей Романов, чья фамилия и послужила основой названия хозяйства, почти 10 лет последовательно воплощает в жизнь свою мечту. В 2007 году были приобретены земли недалеко от г. Темрюк Краснодарского края. С 2009 года здесь высаживают новые виноградники и одновременно ведется строительство здания винодельни и туристического комплекса. В 2016 году &laquo;Вилла Романов&raquo; принимает свой первый урожай.</p>', 'wineries\\July2020\\qEM4srfqIpzbttE7Hk2q.jpg', 'wineries\\July2020\\Y5bJoPBLkXq8rrWmd69v.png', 'wineries\\July2020\\dsO0DWyG5HonMQ084JPH.png', 2, 'Купить вино вилла Романов, Голубицкое поместье', 0, 17, 0, NULL, 'Вилла Романов', '37.274955', '45.323314', 'ACTIVE', NULL, '2020-07-28 22:27:23', 'Винодельню Вилла Романов купила Белуга и теперь это винодельня Голубицкое поместье. Более подробно на сайте russianvine.ru Выбрать вино онлайн и купить вино в Москве, в магазине вина на Расплетина 21.', NULL),
(55, 'Гай-Кодзор', 'Краснодарский край, г. Анапа, с. Гай-Кодзор', '<p>Современная история хозяйства \"Гай-Кодзор\" берет начало в 2002 году. Известные французские виноделы Алэн Дюга и Ноэль Рабо стали консультантами и \"двигателями идей\" для коллектива единомышленников.</p>\r\n<p>Первый из них к этому времени больше 30 лет был управляющим, второй - свыше двух десятилетий - советником-энологом Шато ля Нерт. Это хозяйство одно из самых знаменитых во французском регионе Шатонеф-дю-Пап, в долине Роны.&amp;nbsp;</p>', 'wineries\\July2020\\6dssqwIFuDdAK1Mpp7fl.jpg', 'wineries\\July2020\\lNBqgeEttXn35g8wUylf.png', 'wineries\\July2020\\1gsD88dK98c1bWOEdS47.png', 2, 'Купить вино Гай-Кодзор, вионье, мускат, розе, гевюрцтраминер', 0, 18, 0, NULL, 'gaj-kodzor', '37.437739', '44.854398', 'ACTIVE', NULL, '2020-07-14 08:18:14', 'Винодельня Гай-Кодзор это в первую очередь белые вина - вионье, руссан, гевюрцтраминер, мускат, терруар блан. Очень редкое розе Гай-Кодзор в магазине русское вино на Расплетина 21', NULL),
(56, 'Фанагория', 'Краснодарский край, Темрюкский р-н, п. Сенной', '<p>Более 6 веков назад на Таманском полуострове, омываемом водами Керченского пролива, располагались владения древнего Боспорского царства. Основанная в 500-х годах до н.э., Фанагория была вторым, после столицы царства - Пантикопея, городом в государстве, который именовали \"азиатской\" столицей. И уже тогда славилась Фанагория своими винами, которыми вела торговлю со всем миром.</p>', 'wineries\\July2020\\7tVVApOetqjkJuFjU6m0.jpg', 'wineries\\July2020\\kDNl2PNRJwMDYN51O9E7.png', 'wineries\\July2020\\CH8l9CgDJ2MQ4fBT1aoq.png', 2, 'Купить вина Фанагория', 0, 19, 0, NULL, 'Фанагория', '36.994249', '45.286223', 'ACTIVE', NULL, '2020-07-29 03:44:16', '100 оттенков  Фанагория и пет нат Винодел и Сомелье - это хиты Фанагория, а также каберне фран и совиньон блан, особенно петнат розовый можно найти на сайте russianvine.ru', NULL),
(68, 'Новый Cвет', 'Республика Крым, г. Судак, пгт. Новый Свет', '<p>Организованных Голицыным питомник вскоре стал обладателем большой коллекции сортов, в котором произрастали свыше 600 наименований. Долгие годы работы привели Голицына к осознанию, что как сам виноград, так и рожденное им вино - \"продукт местности\", по выражению самого князя.</p>', 'wineries\\July2020\\KB1VAoBZINFduxdZ6YzR.jpg', 'wineries\\July2020\\16wQAg6Pd27rm1jOnR7z.png', 'wineries\\July2020\\ZRHr4j1yBLgvQtxhGzVy.png', 4, 'Купить шампанское Новый Свет, крымское шампанское', 0, 20, 0, NULL, 'Новый Cвет', '34.914779', '44.825611', 'ACTIVE', NULL, '2020-07-29 21:29:56', 'Крымское шампанское Новый Свет одно из лучших в Крыму. Классическое крымское шампанское Новый Свет на сайте russianvine.ru', NULL),
(69, 'Мысхако', 'Краснодарский край, г. Новороссийск, с. Мысхако', '<p>На черноморском побережье знаменитая Цемесская бухта - вторая по значимости после Севастопольской. Здесь расположен город Новороссийск, и здесь же, раскинулись живописные виноградники \"Мысхако\". Агрофирма \"Мысхако\" входит в число старейших виноградарских и винодельческих хозяйств России. Своё начало история хозяйства отсчитывает с 1869 года, когда здесь были высажены первые лозы.&amp;nbsp;</p>', 'wineries\\July2020\\dDGo4Tto101H5Ox9qVEF.jpg', 'wineries\\July2020\\URnST9g5rm5Kg8MUAbLq.png', 'wineries\\July2020\\6wE2RcYpHVIF1JY1FpUJ.jpg', 2, 'Купить вино Усадьба Мысхако, Шато Пино', 0, 21, 0, NULL, 'Мысхако', '37.760521', '44.657294', 'ACTIVE', NULL, '2020-07-29 23:24:47', 'На сайте русское вино всё о Мысхако и главном виноделе Сергее Дубовике и винах Мысхако - Гранд Резерв, Южный Терруар, Кюве, Мысхако Кюве.', NULL),
(70, 'Винное подворье старого грека', 'Краснодарский край, город-курорт Анапа, с. Витязево', '<p>Валерий Ставрович Асланов (Асланидис) - спортсмен, чемпион мира по дзюдо и профессиональный винодел решил основать в 2011 году под Анапой винодельню. Название - это дань уважения грекам поселенцам, которые организовали первую винодельню в этом же месте в 1843 году. Сохранился винный погреб 1857 года постройки, он и сейчас используется на хозяйстве. Старый грек - это один из тех, кто построил как раз ту, первую винодельню.</p>', 'wineries\\July2020\\gbpYoB8hzSWTQn4iKAM5.jpg', 'wineries\\July2020\\CeiUDweYCdokS5uehliB.png', 'wineries\\July2020\\fTxAKdu4RPSTxsDSmb6D.jpg', 2, 'Купить вино старого грека Анапа, Витязево, красностоп понтийское', 1, 22, 0, NULL, 'Винное подворье старого грека', '37.283839', '45.002352', 'ACTIVE', NULL, '2020-07-09 12:26:48', 'Старый грек выпускает вино в Анапе. Винное подворье старого грека в Анапе (Витязево) - каберне понтийское, красностоп понтийское, мерло понтийское, пино нуар понтийское.', NULL),
(71, 'Усадьба Перовских', 'Республика Крым, г. Севастополь, п. Любимовка', '<p>В 1889 году негоциант Федор Оттонович Шталь, который вел в Санкт-Петербурге торговлю винами южных пределов России, выкупил имение ради амбициозных планов по созданию собственного российского вина. С этой целью новый владелец уже год спустя расширил площади виноградников новыми посадками, и выстроил дом с вместительными подвалами для хранения и выдержки вин. Так &laquo;Приморское&raquo; превратилось в &laquo;Алькадар&raquo; (в переводе с персидского - божье соизволение).&amp;nbsp;</p>', 'wineries\\July2020\\EEeVpijLdEBwWeEng16k.jpg', 'wineries\\July2020\\zA1f3kGbUf6D7eGXs87E.png', 'wineries\\July2020\\ugekH6qw1uFmX4Mpqf8t.png', 4, 'Купить вино Усадьба Перовских, мерло, пино нуар 1860', 0, 23, 0, NULL, 'Усадьба Перовских', '33.548716', '44.667340', 'ACTIVE', NULL, '2020-07-30 01:48:19', 'Винодельня Усадьба Перовских старинный завод в Крыму. Каберне совиньон и мерло Усадьба Перовских и хорошее крымское игристое белое и розовое брют.', NULL),
(72, 'Негоциантский проект YAIYLA', 'Республика Крым, г. Севастополь, п. Любимовка', '<p>Крымский негоциантский проект YAIYLA это новое явление в современном российском виноделии. Негоцианство это не просто бизнес, это определённая философия, через которую Виталий Маринчук старается выразить самобытную природу Крыма и доказать, что в этом регионе можно выпускать вина не только высокого качества, но и вина из редких, автохтонных сортов винограда. &amp;nbsp;&amp;nbsp;</p>', 'wineries\\July2020\\uHoZYspQm9WGRH0xrYUT.jpg', 'wineries\\July2020\\rouaR4meSkeymCmm7la1.png', 'wineries\\July2020\\keZk1aSC7sCP4JfKHWgQ.jpg', 4, 'Купить крымское вино Яйла (YAIYLA), оранжевое вино, кокур, марселан, мальбек, пино менье', 1, 0, 0, NULL, 'negociantskij-proekt-yaiyla', '33.548716', '44.667340', 'ACTIVE', NULL, '2020-07-30 17:10:30', 'Крымская винодельня Яйла (YAIYLA) это - негоциантский проект. Лучшие вина Яйла кокур, марселан, мальбек, пино менье. И оранжевые вина Яйла - мускат, ркацители, кокур и бленд Яйла.', NULL),
(73, 'Каракезиди', 'Краснодарский край, г. Анапа, х. Куток', '<p>Трудолюбие, честность, открытость и непосредственность, умение направить все силы собственные и окружающей среды в одно русло, следование биодинамическим принципам и опыт предков - всё это про Яниса Каракезиди. Ничего лишнего, только тонкое созвучие виноградарства и виноделия в гармонии с природой и окружающими людьми.&amp;nbsp;</p>', 'wineries\\July2020\\1FAINEytRVTbHGQytPcb.jpg', 'wineries\\July2020\\bg2sJFCQLwF99SADGAcu.png', 'wineries\\July2020\\x6f7NFvoGBEisAGM6wyL.jpg', 2, 'Вино Каракезиди Стретто, каберне фран, красностоп', 1, 0, 1, 'wineries\\August2020\\0llUIdzRnXJzTWcgSEFs.jpg', 'karakezidi', '37.459828', '44.956249', 'ACTIVE', NULL, '2020-08-04 01:23:43', 'Винодельня Яниса Каракезиди - это вина без серы, биодинамика. Лучшие вина Каракезиди под маркой Стретто - каберне фран, красностоп, каберне совиньон, саперави, мерло Каракезиди.', NULL),
(74, 'Коктебель', 'Республика Крым, г. Феодосия, пгт. Коктебель', '<p>Появление предприятия по производству вин на юго-восточных берегах Крыма вполне закономерно. Сотни лет на этих землях выращивались уникальные сорта винограда, а виноделы края снискали особое расположение покровителя Диониса. Достойным продолжателем древних традиций стал А.Э. Юнге, открывший в 1906 году винодельню с погребами, построенными по всем правилам европейской винодельческой практики того времени.</p>', 'wineries\\July2020\\2E5T3IDxufHtwPyOkCRY.jpg', 'wineries\\July2020\\GzSjKBwAhAGcHefSb0Z0.png', 'wineries\\July2020\\UFUaBM4WWOaKSHwMOql0.png', 4, 'Купить вино Коктебель, Крым', 0, 0, 0, NULL, 'koktebel', '35.239471', '44.960031', 'ACTIVE', NULL, '2020-07-30 17:04:27', 'Завод марочных вин Коктебель в Крыму. Гордость завода Коктебель - мадерная площадка, равных которой нет в Европе. Вина Коктебель известны по всему миру.', NULL),
(76, 'Винодельня Олега Репина', 'Республика Крым, г. Севастополь, п. Любимовка', '<p>Его вполне можно назвать символом сегодняшнего дня в виноделии России: небольшое терруарное хозяйство, где высокообразованный энолог создает коллекционные авторские вина. Олег Репин имеет два образования по профилю энолога и немалый опыт работы на винодельнях Крыма. Работу любого рода, от изучения почвы до проектирования и поставок оборудования, Олег предпочитает выполнять самостоятельно. Всегда рядом с ним - верный друг и помощница, вдохновитель всех идей, супруга Людмила.</p>', 'wineries\\July2020\\jtdHhb63ReUQc5lrhXUN.jpg', 'wineries\\July2020\\yh04Mg8u8HWtGofTnIiS.png', 'wineries\\July2020\\ehM53YiE4lCA6W1pEEVv.jpg', 4, 'Вино Олега Репина купить, Крым, сира листва, шенен блан, пино нуар Олег Репин', 1, 0, 0, NULL, 'Винодельня Олега Репина', '33.548716', '44.667340', 'ACTIVE', NULL, '2020-07-14 07:53:08', 'Винодельня Олега Репина. Терруарное вино Крым Олег Репин - шенен блан, пино нуар, сира листва и рислинг Олег Репин. Все вина Олег Репин онлайн есть на сайте русское вино russianvine.ru', NULL),
(77, 'Дербентская винодельческая компания', 'Дагестан, г. Дербент', '<p>Новое винодельческое предприятие в Дербенте, которое стало результатом необыкновенно амбициозного проекта, сочетающего в себе самые высокие существующие на данный момент технологии, и знания зарубежных специалистов.</p>', 'wineries\\July2020\\58XyheVvkoARd0QLi0Ex.jpg', 'wineries\\July2020\\pgc8U2QmWIokCsSD2l59.png', 'wineries\\July2020\\TI6KoqMp8j5zFnZoAj41.png', 7, 'Дербентская винодельческая компания', 0, 0, 0, NULL, 'Дербентская винодельческая компания', '48.288766', '42.057669', 'ACTIVE', NULL, '2020-07-30 17:01:19', NULL, NULL),
(79, 'Дача Сердюка', 'Ростовская область, г. Новочеркасск', '<p>Всё началось в 2003 году, с переработки первых 180 кг Каберне Совиньона. Уже тогда были заложены основные традиции, которые и сейчас соблюдает винодельня: никакого диоксида серы и не использовать пластиковую тару.&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;</p>', 'wineries\\July2020\\gbw0jux2zgcLbfZrjCnR.jpg', 'wineries\\July2020\\o39bKPEfQYLXcjz52d2H.png', 'wineries\\July2020\\XEUP18dlKSVxC1jqJ3jB.jpg', 3, 'Дача Сердюка вино купить, вино без серы Дача Сердюка, биовино из Ростова', 1, 0, 0, NULL, 'dacha-serdyuka', '40.093725', '47.422052', 'ACTIVE', NULL, '2020-07-14 08:37:47', 'Винодельня Дача Сердюка из Ростова. Вина Дача Сердюка производятся без серы, вино Дача Сердюка это биовино из Ростова. Казачье, поречное, саперави и каберне Дача Сердюка.', NULL),
(84, 'Два сердца', 'Республика Крым, с. Геройское', '<p>&laquo;Два сердца&raquo; - семейный винодельческий проект. В основе его создания лежит романтическая история отношений Павла Пестова и его жены Оксаны. Всё начиналось как хобби. Вина &laquo;Два сердца&raquo; Павел и Оксана придумали для себя и близких друзей.</p>', 'wineries\\July2020\\nTww0GfcN9imPPOZgRQi.jpg', 'wineries\\July2020\\MYKkO60cJILcaIWE1xem.png', 'wineries\\July2020\\WM1KYMbq9961cgfRRjkr.jpg', 4, 'Купить вино Два Сердца, Павел Пестов, Крым, пти мансенг, вионье, пино гри блаш', 1, 0, 0, NULL, 'Два сердца', '33.746489', '45.138086', 'ACTIVE', NULL, '2020-07-30 16:59:59', 'Вино Два Сердца - вино Севастополя. Основатель Два Сердца - Павел Пестов и его лучшие крымские вина - пино гри блаш, вионье, каберне фран, саперави и сладкое вино пти мансенг Два Сердца.', NULL),
(85, 'Вина Бани', 'Ростовская область, х. Малая Мартыновка', '<p>Вина Бани - семейная винодельня Юрия Химичева. Расположена в Ростовской области в хуторе Малая Мартыновка. Название винодельни связано с её расположением, она была организована в помещении бывшей хуторской бани.&amp;nbsp;</p>', 'wineries\\July2020\\3i9zCfSxLFlwysZtk3zG.jpg', 'wineries\\July2020\\A56G6ZEXJxgedBuUprtX.png', 'wineries\\July2020\\XwaorthFVGqCGAB9a6xd.jpg', 3, 'Вина Бани, купить ростовское вино, Отец и 33,3', 1, 0, 0, NULL, 'Вина Бани', '41.831956', '47.332458', 'ACTIVE', NULL, '2020-07-09 12:21:57', NULL, NULL),
(86, 'Сорт', 'Краснодарский край, г. Новороссийск, ст. Раевская', '<p>Первый виноград был заложен в 2017 году. Саженцы прибыли из питомника Раушедо. В то же время началось строительство винодельни, которое было окончено в 2018 году.</p>', 'wineries\\July2020\\9JPqJYgtzVH9dVnVWxcX.jpg', 'wineries\\July2020\\hQY6zQH2caCSdbgQcui5.png', 'wineries\\July2020\\ODyXk2HAXsijER7pRP52.png', 2, 'Винодельня Сорт Sort кубанская винодельня, расположенная на горе Сухая.', 1, 0, 0, NULL, 'sort', '37.515679', '44.837890', 'ACTIVE', NULL, '2020-07-30 17:07:48', 'Сорт - винодельня, построенная недалеко от Новороссийска. Сорт - новая кубанская винодельня.', NULL),
(87, 'Шумринка', 'Краснодарский край, г. Анапа, с. Гай-Кодзор', '<p>Шумринка - новый винодельческий проект, расположенный недалеко от села Гай-Кодзор. Название винодельни связано с рекой Шумринкой, протекающей недалеко от виноградников.</p>', 'wineries\\July2020\\NabAlYZSirDcIzankPrq.jpg', 'wineries\\July2020\\Ia2O5ScjYo3eOnQkYubr.png', 'wineries\\July2020\\jHeLJFraECR0Mtprb568.jpg', 2, 'Шумринка винодельня, Петрикор и Семисам вина винольни Шумринка', 0, 0, 0, NULL, 'shumrinka', '37.437403', '44.854518', 'ACTIVE', NULL, '2020-07-30 17:14:13', 'Шумринка новая винодельня под Анапой. Вина Шумринки - Петрикор белое и Петрикор красное, Семисам и Спелая роза.', NULL);
INSERT INTO `winery_images` (`id`, `winery_id`, `numbering`, `type_id`, `image`, `description`, `addition`, `created_at`, `updated_at`) VALUES
(1, 50, 1, 0, 'winery-images\\July2020\\g44EQxlZHuOOg2Apgpcu.jpg', '<p><span style=\"color: #000000; font-family: \'Open Sans\', sans-serif; font-size: 13px;\">Винтрест &laquo;Саук-Дере&raquo; &mdash; уникальное место со знаменательной историей. Первые виноградники появились тут в далеком 1926 году в период зарождения советского виноделия, исторические каменоломни стали превращаться в место для выдержки вин. В 50-х годах предприятие &laquo;Саук-Дере&raquo; уже считалось жемчужиной советского виноделия. Здесь были построены уникальные по глубине и протяжённости тоннели, посажен легендарный рислинг, а также заложена государственная коллекция лучших вин и коньяков со всех республик СССР. Коллекция сохранилась до сих пор, сейчас это более 250 наименований и около 82 000 бутылок.&nbsp;</span></p>', NULL, '2020-07-14 13:34:39', '2020-07-14 13:34:39'),
(2, 50, 2, 0, 'winery-images\\July2020\\H67WdBKPJCm4SLWkzz2z.jpg', '<p>Сейчас, благодаря новым международным методам, технологиям и преданности сотрудников своему делу, здесь возрождают предприятие и закладывают новые посадки виноградников. В 2015 году у &laquo;Саук-Дере&raquo; появились новые хозяева &mdash; семейное предприятие &laquo;Николаев и сыновья&raquo;, которые сразу решили заняться реконструкцией винодельни. Конечная цель проводимой реконструкции &mdash; полное обновление устаревших построек, создание современного предприятия полного цикла, на базе которого будут производиться, выдерживаться и разливаться тихие вина, игристое и коньяки.&nbsp;</p>', '<p>&laquo;Саук-Дере&raquo; &mdash; это не просто производственное предприятие. Это прежде всего традиции, передающиеся от одного винодела к другому.&nbsp;</p>', '2020-07-14 14:07:06', '2020-07-14 14:07:06'),
(3, 50, 1, 3, 'winery-images\\July2020\\ic9bOXYbLprpGyTnJjan.jpg', '<p>Винодельческое хозяйство &laquo;Саук-Дере&raquo; находится на возвышенности, откуда открывается замечательный вид на черноморское побережье, горы и склоны холмов, покрытые лозами. Виноградники расположены в предгорной части Крымского района Краснодарского края.</p>', NULL, '2020-07-14 14:15:41', '2020-07-14 14:15:41'),
(4, 79, 1, 0, 'winery-images\\July2020\\3FwZqGqyTzAE2E2aUDIs.jpg', '<p>Дача Сердюка - семейная винодельня, расположенная в Новочеркасске (Ростовская область), в которой традицию виноделия заложил глава семейства Сердюк Сергей Иванович, а продолжили уже сыновья Сергей и Алексей.</p>', NULL, '2020-07-15 06:19:47', '2020-07-15 06:19:47'),
(5, 79, 1, 3, 'winery-images\\July2020\\6jorJYPoabKIszTE9wzN.jpg', '<p>Виноградники располагаются на правом берегу реки Дон, в хуторе Каныгин, Усть-Донецкого района.</p>', '<p>Виноградники окружены небольшими холмами. У терруара свой особенный микроклимат. Летом тут жарче и суше. Почвы разные, от песчаных до суглинистых, местами немного известняка.</p>', '2020-07-15 06:30:02', '2020-07-15 06:30:02'),
(6, 79, 1, 1, 'winery-images\\July2020\\Fmy6kQIBEzkC6vy7y8oJ.jpg', '<p>Собственные виноградники расположены в зоне рискованного виноделия, поэтому они являются укрывными. Сегодня у хозяйства около 1 га плодоносящих лоз.</p>\r\n<p>Красностоп Золотовский 0,2 га</p>\r\n<p>Саперави 0,7 га</p>\r\n<p>Сибирьковый 0,2 га</p>', '<h2>1,1 га</h2>\r\n<p>виноградников</p>', '2020-07-15 06:39:30', '2020-07-15 06:39:30'),
(7, 79, 1, 2, 'winery-images\\July2020\\f6DGHbmC8Gqg4H26b9X9.jpg', '<p>Винодельня находится в городской зоне, но при этом имеет полный цикл переработки винограда. В арсенале есть итальянские емкости из нержавеющей стали и бочки из кавказского дуба. Общий годовой объём выпуска вина составляет 5000 бутылок.</p>', NULL, '2020-07-15 06:44:56', '2020-07-15 06:44:56'),
(8, 79, 2, 2, 'winery-images\\July2020\\1mES1538HTxEbIFvCNxk.jpg', NULL, NULL, '2020-07-15 06:45:25', '2020-07-15 06:45:25'),
(9, 34, 1, 0, 'winery-images\\July2020\\RYKnrd8iHJkgoWicezjr.jpg', '<p>Под таким названием винодельческое хозяйство семейного типа создал в 2012 году Сергей Бескоровайный. Небольшой объем выпускаемой продукции позволяет гарантировать именно тот результат, который поставил перед собой винодел: выпускать настоящее &laquo;авторское&raquo; вино.</p>', NULL, '2020-07-15 07:39:15', '2020-07-15 07:39:15'),
(10, 34, 1, 3, 'winery-images\\July2020\\jBkU3rSzgcF3dFUOcbUH.jpg', '<p>В целом &laquo;бедный&raquo; состав почвы на основе известняка содержит включения гальки, песка, вкрапления мелких ракушек и их фрагментов.</p>', NULL, '2020-07-15 07:46:01', '2020-07-15 07:46:01'),
(11, 34, 2, 3, 'winery-images\\July2020\\i7uPY4vJB5FLspwNarD2.jpg', '<p>Географически земли хозяйства принадлежат Западно-прибрежному региону виноградарских площадей Республики Крым. В составе земель Севастополя они расположены в их северной части. Поистине сравнимый со средиземноморским, климат этой части крымского побережья, от Балаклавы до Евпатории, уникален благодаря защите гряды крымских гор. Открывая доступ теплых потоков с юга, они преграждают путь холодным континентальным массам. \"Грелка\" из вод Черного моря уравновешивает сезонный температурный режим, одновременно создавая оптимальные для виноградарства суточные перепады.</p>', '<p>Основная экспозиция склонов, на которых выращивается виноград &ndash; северо-западная. Это один из важных факторов, позволяющий обходится без дополнительного орошения, т.к. солнечные лучи в свои пиковые летние дни, не имеют критичных последствий для ягод.</p>', '2020-07-15 07:59:52', '2020-07-15 08:12:58'),
(12, 34, 1, 1, 'winery-images\\July2020\\GeS1KTduoxIU4ZONmmKb.jpg', '<h3 style=\"font-size: 24px;\">Плотность посадки 4000 лоз/га</h3>\r\n<h3 style=\"font-size: 24px;\">Продуктивность 20 гл/га</h3>\r\n<p style=\"font-size: 18px;\">Собственные виноградники неукрывного типа были заложены в 2014 году, в большинстве своем классические сорта, саженцы которых были завезены из Италии - питомник &laquo;Раушедо&raquo;. Несмотря на относительную молодость, сегодня лозы дают уверенный урожай, с каждым годом становясь все ярче и индивидуальнее.</p>\r\n<div>&nbsp;</div>', '<div class=\"vine_info\">\r\n<div class=\"plod\">\r\n<p class=\"plod_title\">3,5 га</p>\r\n<span class=\"plod_desc\">плодоносящих виноградников</span></div>\r\n<div class=\"wine_ul\">\r\n<table id=\"wine_species\">\r\n<tbody>\r\n<tr>\r\n<td>&bull; Пино Нуар</td>\r\n<td>1 га</td>\r\n</tr>\r\n<tr>\r\n<td>&bull; Каберне Фран</td>\r\n<td>1,5 га</td>\r\n</tr>\r\n<tr>\r\n<td>&bull; Мерло</td>\r\n<td>1 га</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>', '2020-07-15 08:43:19', '2020-07-15 08:56:04'),
(13, 34, 2, 1, 'winery-images\\July2020\\hJnS7dQsMeCXbfu0rwc8.jpg', '<p>Дополнительно производятся закупки винограда в соседних хозяйствах, после тщательного отбора.</p>\r\n<p>Для работ по обработке почвы на винограднике используется механизация, а сбор, обрезка, подвязка и т.д. - только вручную.</p>\r\n<p>И у собственного винограда, и у приобретаемого каждая ягода находится под непрерывным пристальным контролем и вниманием винодела, благодаря которому обрабатывается в момент максимальной фенольной зрелости винограда.</p>', NULL, '2020-07-15 08:58:28', '2020-07-15 08:58:28'),
(14, 34, 3, 1, 'winery-images\\July2020\\fs0PoaAdUZnsDWL6iIcr.jpg', NULL, NULL, '2020-07-15 09:05:54', '2020-07-15 09:05:54'),
