INSERT INTO `wineries` (`id`, `title`, `signature`, `description`, `header_image`, `logo_image`, `catalog_image`, `region_id`, `meta_title`, `type_id`, `layout_id`, `subscribe_status`, `slug`, `coordinate_lon`, `coordinate_lat`, `status`, `created_at`, `updated_at`) VALUES
(1, 'Имение Сикоры', 'Краснодарский край, г. Новороссийск, х. Семигорский', '&lt;p&gt;Винодельня, расположенная рядом с Новороссийском, абсолютного нового формата. Ставка в развитии проекта была сделана на создание условий для производства вин самого высокого качества, в которых раскроется терруар Семигорья. Хозяйство ориентировалось на французский опыт классического виноделия, заручившись поддержкой винодела из Франции Гаэль Брюллон.&lt;br&gt;&lt;/p&gt;', 'catalog/sikory_header.jpg', 'catalog/sikory_header.jpg', 'catalog/sikory_header.jpg', 2, 'Купить вино Имение Сикоры, рислинг, каберне совиньон семейный резерв', 1, 0, 0, 'Имение Сикоры', '37.612936', '44.883282', 'ACTIVE', NULL, NULL),
(5, 'Винный Дом Раевское', 'Краснодарский край, г. Новороссийск, ст. Натухаевская', '&lt;p&gt;Проект, который можно назвать&amp;nbsp;Возрождением&amp;nbsp;Российского виноделия, берет начало в 2005 году. Именно тогда основатель компании Eastfield Agro Holding Limited бизнесмен из Словакии Игорь Ледецки не только понял, что Российские вина могут и будут соперничать с лучшими образцами старосветских вин из Италии, Испании и Франции, но и предугадал бурное развитие Российского виноделия через несколько лет.&lt;br&gt;&lt;/p&gt;', 'catalog/dopolnitelno/раевское главная.jpg', 'catalog/dopolnitelno/раевское главная.jpg', 'catalog/dopolnitelno/раевское главная.jpg', 2, 'Купить вино Раевское, ренессанс, русское вино', 0, 0, 0, 'Винный Дом Раевское', '37.560679', '44.899904', 'ACTIVE', NULL, NULL),
(6, 'Абрау-Дюрсо', 'Краснодарский край, г. Новороссийск, с. Абрау-Дюрсо', '&lt;p&gt;Крупнейший в России производитель игристого вина премиального сегмента. &quot;Абрау-Дюрсо&quot; ведёт историю от даты указа императора Александра II - 25 ноября 1870 года, когда у озера Абрау и речки Дюрсо было создано удельное имение &quot;Абрау-Дюрсо&quot;, которое стало принадлежать царской семье. Первый виноград был собран в 1877 году, а через пять лет бочки с вином размещались уже в оборудованном по всем правилам погребе. А в 1891 году на пост главного винодела назначен Лев Голицын. Он уже отстроил завод и оборудовал 5 туннелей для выдержки вина.&lt;/p&gt;', 'catalog/abrau/abrau-main.jpg', 'catalog/abrau/abrau-main.jpg', 'catalog/abrau/abrau-main.jpg', 2, 'Купить вино Абрау Дюрсо, блан де блан, империал', 0, 0, 0, 'Абрау-Дюрсо', '37.599558', '44.701792', 'ACTIVE', NULL, NULL),
(7, 'Лефкадия', 'Краснодарский край, Крымский район, с. Молдаванское', '&lt;p&gt;Этот проект действительно уникален: он сочетает в себе необыкновенную природу Кубани, неповторимый терруар, инновационные технологии и, в первую очередь, выдающихся профессионалов, объединенных и вдохновлённых общим делом.&lt;/p&gt;', 'catalog/lefcadia/lefcadia_bg.jpg', 'catalog/lefcadia/lefcadia_bg.jpg', 'catalog/lefcadia/lefcadia_bg.jpg', 2, 'Купить вино Лефкадия, Лефкадия резерв, Темелион ', 0, 9, 0, 'Лефкадия', '37.870700', '44.946087', 'ACTIVE', NULL, NULL),
(8, 'Цимлянское', 'Ростовская область, г. Цимлянск', '&lt;p&gt;Столица донского правобережья Цимла дала наименование производимым здесь винам, напоминавшим своими качествами знаменитые рейнские. В старину Цимлянское вино готовилось на Дону и поставлялось в больших количествах петербургской и московской знати. Среди любителей его были Пушкин, Лермонтов, Чехов, Тургенев.&amp;nbsp; А Кутузов отмечал победу над французами именно цимлянским, в Париж, на площадь Свободы были поставлены 3 тысячи бутылок игристого вина.&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;', 'catalog/dopolnitelno/коллаж2s.jpg', 'catalog/dopolnitelno/коллаж2s.jpg', 'catalog/dopolnitelno/коллаж2s.jpg', 3, 'Цимлянское шампанское купить, Казачка', 0, 0, 0, 'Цимлянское', '42.105853', '47.658879', 'ACTIVE', NULL, NULL),
(9, 'Вилла Виктория', 'Краснодарский край, Новороссийск, х. Ленинский Путь', '&lt;p&gt;&lt;span style=&quot;text-align: justify;&quot;&gt;Реализованная идея семьи Яновых создать хозяйство бургундско-луарского стиля. Самая крайняя и самая высоко расположенная винодельня Анапской долины по пути к Новороссийску. Консультантом хозяйства выступил Джон Ворончак. Первые вина делались в «Мысхако» Романом Неборским.&lt;/span&gt;&lt;br&gt;&lt;/p&gt;', 'catalog/dopolnitelno/_ULK3048.jpg', 'catalog/dopolnitelno/_ULK3048.jpg', 'catalog/dopolnitelno/_ULK3048.jpg', 2, 'Купить вино Вилла Виктория, каберне фран, рислинг, шардоне', 0, 0, 0, 'Вилла Виктория', '37.627732', '44.862104', 'ACTIVE', NULL, NULL),
(11, 'Винодельня Узунов', 'Краснодарский край, Темрюкский район, п. Сенной', '&lt;p&gt;Семейный проект Ярослава Узунова, винодела в четвёртом поколении. Авторские вина из уникального терруара Таманского полуострова. Все вина производятся в небольших количествах, годовой тираж вин ограничен и составляет всего 50 000 бутылок в год.&lt;br&gt;&lt;/p&gt;', 'catalog/auzunov/P16202622.jpg', 'catalog/auzunov/P16202622.jpg', 'catalog/auzunov/P16202622.jpg', 2, 'Купить вино Узунов, семь морей, каберне совиньон', 1, 0, 0, 'Винодельня Узунов', '36.994249', '45.286223', 'ACTIVE', NULL, NULL),
(12, 'Вилла Звезда', 'Ростовская область, хутор Малая Мартыновка', '&lt;p&gt;Винодельня нового поколения, основанная в 2012 году Игорем Губиным, в винодельческой зоне Долина Дона. Это первый проект на Дону, осуществленный за два года с &quot;нуля&quot;, в уникальном терруаре междуречья рек Сал и Дон.&amp;nbsp;&lt;br&gt;&lt;br&gt;&lt;/p&gt;', 'catalog/вилла звезда/villa.jpg', 'catalog/вилла звезда/villa.jpg', 'catalog/вилла звезда/villa.jpg', 3, 'Вилла Звезда купить вино, Атаман, красностоп Вилла Звезда', 0, 0, 0, 'Вилла Звезда', '41.834431', '47.335713', 'ACTIVE', NULL, NULL),
(13, 'Шато Пино', 'Краснодарский край, г. Новороссийск, с. Федотовка', '&lt;p&gt;Новое прочтение известного терруара у мыса Мысхако. Молодое хозяйство, расположенное недалеко от черноморского курорта Широкая Балка, в живописном месте на склонах горы Колдун. Великолепная лоза и уникальное расположение уже сегодня позволило выпускать как свежие минеральные белые вина, так и мощные красные, особенно интересен Шираз и Пино Нуар. &amp;nbsp;&lt;br&gt;&lt;/p&gt;', 'catalog/balka/усадьба мысхакообщий вид.jpg', 'catalog/balka/усадьба мысхакообщий вид.jpg', 'catalog/balka/усадьба мысхакообщий вид.jpg', 2, 'Купить вино Шато Пино', 0, 0, 0, 'Шато Пино', '37.708531', '44.683432', 'ACTIVE', NULL, NULL),
(14, 'Винодельня Дмитрия Гусева', 'Волгоградская область, г. Дубовка', '&lt;p&gt;Микровинодельня из Волгоградской области (Нижняя Волга), уникальна и единственная в своем роде в этом регионе. Хозяйство было основано в 1990 году. Выпускает вина как из автохтонных сортов винограда, так и работает с гибридами советской селекции. Дмитрий Гусев сегодня является самым ярким и интересным виноделом Нижней Волги и добился серьёзных результатов в этом весьма не простом винодельческом регионе.&lt;/p&gt;', 'catalog/dopolnitelno/Гусев 3 бут.jpg', 'catalog/dopolnitelno/Гусев 3 бут.jpg', 'catalog/dopolnitelno/Гусев 3 бут.jpg', 5, 'Дмитрий Гусев вино купить, вино из Волгограда, винодел из Дубовки, красностоп, мариновский', 1, 0, 0, 'Винодельня Дмитрия Гусева', '44.820623', '49.051470', 'ACTIVE', NULL, NULL),
(15, 'Винодельня Константина Дзитоева', 'Северная Осетия, г. Владикавказ', '&lt;p&gt;Единственная микровинодельня из региона Долина Терека. Авторский проект винодела - энтузиаста Константина Дзитоева из Владикавказа. Одна из первых лицензированных фермерских виноделен в России. &amp;nbsp; &amp;nbsp;&lt;br&gt;&lt;/p&gt;', 'catalog/dzitoev/ДолинаТерека главная — копия Дзитоев.jpg', 'catalog/dzitoev/ДолинаТерека главная — копия Дзитоев.jpg', 'catalog/dzitoev/ДолинаТерека главная — копия Дзитоев.jpg', 8, 'Вина Константина Дзитоева, вино из Осетии, 60/40, Красностоп, Русское Вино', 1, 0, 1, 'Винодельня Константина Дзитоева', '44.650939', '43.028420', 'ACTIVE', NULL, NULL),
(16, 'Собер Баш', 'Краснодарский край, Северский район, с.п. Смоленское', '&lt;p&gt;Винодельня получила своё название в честь горы Собер Баш, вид на которую открывается с виноградников хозяйства. Переводится как &quot;Ведьмина Гора&quot;, и часть вин также называются в честь богов. Не тех, что некогда сидели на Олимпе, а малых, которые по легенде собирались именно здесь, чтобы пировать и проказничать. &amp;nbsp; &amp;nbsp;&lt;br&gt;&lt;/p&gt;', 'catalog/avtorskoe/IMG_1004(2).jpg', 'catalog/avtorskoe/IMG_1004(2).jpg', 'catalog/avtorskoe/IMG_1004(2).jpg', 2, 'Купить вино Собер Баш, Афа, Красностоп', 1, 0, 0, 'Собер Баш', '38.835131', '44.868581', 'ACTIVE', NULL, NULL),
(17, 'Винодельня Гунько', 'Краснодарский край, Крымский район, ст. Нижнебаканская', '&lt;p&gt;Gunko Winery - новая точка на винной карте России. Хозяйство начало свою историю в 2017 году. Руководит проектом Сергей Коротков, винодел с большим опытом работы в различных винных хозяйствах России, начинавший свой путь под руководством Патрика Леона. Винодельня рассчитана на производство не более 50 000 бутылок в год.&lt;/p&gt;', 'catalog/gunko/gunkomainfoto.jpg', 'catalog/gunko/gunkomainfoto.jpg', 'catalog/gunko/gunkomainfoto.jpg', 2, 'Вино Гунько, совиньон блан Гунько, анкор мальбек и анкор розе Гунько ', 1, 0, 0, 'Винодельня Гунько', '37.866648', '44.866544', 'ACTIVE', NULL, NULL),
(18, 'Дом Захарьиных', 'Республика Крым, с. Кочергино / с. Плодородное', '&lt;p&gt;Автор идеи,&amp;nbsp; инициатор создания, владелец и руководитель хозяйства - Валерий Захарьин, человек чьи энергия и убеждённость стали надёжным фундаментом проекта. При том что у проекта есть только собственные виноградники - 88 га и нет собственной производственной площадки.&amp;nbsp;&lt;/p&gt;', 'catalog/dopolnitelno/EDDY7877.jpg', 'catalog/dopolnitelno/EDDY7877.jpg', 'catalog/dopolnitelno/EDDY7877.jpg', 4, 'Купить вино Захарьина, Крым, кефесия, сары пандас, кокур брют', 0, 0, 0, 'Дом Захарьиных', '33.749023', '44.838799', 'ACTIVE', NULL, NULL),
(19, 'Эльбузд', 'Ростовская область, Азовский район, х. Эльбузд', '&lt;p&gt;Имя хозяйство получило по названию посёлка и реки, на склонах которой расположились её виноградники. Река Эльбузд служит линией территориального разграничения Краснодарского края и Ростовской области. Её название переводится с тюрских наречий как &quot;Ледяная река&quot;.&lt;br&gt;&lt;/p&gt;', 'catalog/эльбузд/дикие животные на винограднике.JPG', 'catalog/эльбузд/дикие животные на винограднике.JPG', 'catalog/эльбузд/дикие животные на винограднике.JPG', 3, 'Вина Эльбузд купить, каберне фран Эдьбузд, гевюрцтраминер, рислинг', 0, 0, 0, 'Эльбузд', '39.715732', '48.870318', 'ACTIVE', NULL, NULL),
(34, 'Винодельня Сергея Бескоровайного', 'Республика Крым, г. Севастополь, п. Бельбек ', '&lt;p&gt;Основал винодельческое хозяйство семейного типа Сергей Бескоровайный. Он в 2012 году загорелся идеей выпускать авторское вино и в 2014 году были заложены собственные виноградники, площадью 3,5 га, в северной части Севастополя. Небольшой тираж выпускаемой продукции позволяет гарантировать именно тот результат, который поставил перед собой винодел, отсюда и получается по-настоящему &quot;авторское&quot; вино.&lt;/p&gt;&amp;nbsp;&lt;b&gt;&lt;/b&gt;&lt;i&gt;&lt;/i&gt;&lt;u&gt;&lt;/u&gt;&lt;sub&gt;&lt;/sub&gt;&lt;sup&gt;&lt;/sup&gt;&lt;strike&gt;&lt;/strike&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;', 'catalog/belbek/belbek_bg.jpg', 'catalog/belbek/belbek_bg.jpg', 'catalog/belbek/belbek_bg.jpg', 4, 'Бельбек Сергей Бескоровайный вино купить, Крым, рислинг Бельбек', 1, 1, 0, 'Винодельня Сергея Бескоровайного', '33.820807', '44.642199', 'ACTIVE', NULL, NULL),
(35, 'Сатера', 'Республика Крым, Бахчисарайский район, с. Долинное', '&lt;p&gt;Хозяйство отсчитывает свою историю от 2000 года и принадлежит Игорю Самсонову. Занимаясь в 90-е годы поставками вин и крепких спиртных напитков в качестве дистрибьютера, он задался целью создания собственного производства вина. Формирование и становление &quot;Сатеры&quot; произошло на базе типового совхоза-завода, существовавшего в этом регионе с 1978 года.&lt;/p&gt;', 'catalog/satera/main_bg.jpg', 'catalog/satera/main_bg.jpg', 'catalog/satera/main_bg.jpg', 4, 'Купить вино Эссе, Кача Вэлли, Сатера   ', 0, 24, 0, 'Сатера', '33.765986', '44.753936', 'ACTIVE', NULL, NULL),
(36, 'Золотая Балка', 'Республика Крым, г. Севастополь, Балаклава', '&lt;p&gt;Все начиналось с винного подвала генерала Александра Витмера, подаренного в 1889 году городу Балаклаве вместе с виноградниками. На сегодня Агрофирма Золотая Балка крупнейший в Крыму производитель игристого вина из собственного винограда. &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;', 'catalog/gb_winery/background_gb.jpg', 'catalog/gb_winery/background_gb.jpg', 'catalog/gb_winery/background_gb.jpg', 4, 'Купить вино Золотая Балка, Крым, Балаклава', 0, 5, 0, 'Золотая Балка', '33.600000', '44.501414', 'ACTIVE', NULL, NULL),
(37, 'Вина Арпачина', 'Ростовская область, Багаевский район, ст. Маныческая', '&lt;p&gt;Совершенно новый проект из Ростовской области, который полностью сконцентрировался на выпуске вин из донских автохтонов. Винодельня принадлежит семье Юрия Малика. Многие хозяйства создаются целенаправленно, но возникновение в станице Маныческой винодельни стало итогом совпавших обстоятельств, результат которых превзошёл самые смелые чаяния его участников.&amp;nbsp;&lt;/p&gt;', 'catalog/dopolnitelno/Арпачин IMG_48711.JPG', 'catalog/dopolnitelno/Арпачин IMG_48711.JPG', 'catalog/dopolnitelno/Арпачин IMG_48711.JPG', 3, 'Вина Арпачина купить, Вино Арпачин, вино из Ростова, шампанское Арпачино', 1, 0, 0, 'Вина Арпачина', '40.257335', '47.234659', 'ACTIVE', NULL, NULL),
(40, 'Массандра', 'Республика Крым, г. Ялта, пгт. Массандра', '&lt;p&gt;Первым владельцем Массандры был французский маршал Карл Нассау-Зиген. Красота мест произвела неизгладимое впечатление. А уже после, в 19 веке земли Массандры прошли череду смен собственников и оказались в числе недвижимых имуществ Императорского дома, под управлением Льва Голицына. За три года было построено одно из лучших погребных хозяйств в мире на глубине до 50 м с тоннелями.&lt;/p&gt;&amp;nbsp;&lt;br&gt;', 'catalog/massandra/massandra_bg.jpg', 'catalog/massandra/massandra_bg.jpg', 'catalog/massandra/massandra_bg.jpg', 4, 'Купить вино Массандра, мускат позднего сбора, кефесия', 0, 6, 0, 'Массандра', '34.187588', '44.517315', 'ACTIVE', NULL, NULL),
(41, 'Шато Сан Даниль', 'Республика Крым, пгт. Гурзуф, пос. Даниловка', '&lt;p&gt;История появления хозяйства, расположенного под Ялтой, имеет старинные корни. В начале 19 века винодельню Ай-Даниль приобрёл генерал-губернатор, граф Воронцов. Он привлёк иностранных виноделов, в частности на винодельне трудился француз Гаузер. А в 1891 году хозяйство перешло в Главное управление уделов при Министерстве императорского двора, под предводительством князя Льва Сергеевича Голицына. &amp;nbsp;&lt;/p&gt;', 'catalog/SaintDaniel/background_sd.jpg', 'catalog/SaintDaniel/background_sd.jpg', 'catalog/SaintDaniel/background_sd.jpg', 4, 'Купить вино Шато Сан Даниль, бомонд, трамонте, мускат ', 0, 3, 0, 'Шато Сан Даниль', '34.259760', '44.521494', 'ACTIVE', NULL, NULL),
(42, 'Alma Valley', 'Западно-Предгорный Крым, долина реки Альма, пос. Вилино', '&lt;p&gt;Первый суперсовременный винодельческий проект созданный с нуля в Крыму. Поражает своим размахом и тщательным подходом к выпуску премиальных вин нового уровня в современной истории виноделия. Помимо сухих вин с высоким потенциалом, предприятие создаёт моду на потребление вин природно-сладких, выпуская вина с остаточным виноградным сахаром.&lt;/p&gt;', 'catalog/allmavalley/allma_bg.jpg', 'catalog/allmavalley/allma_bg.jpg', 'catalog/allmavalley/allma_bg.jpg', 4, 'Купить вино Альма Валлей / Alma Valley, Крым, айсвайн', 0, 8, 0, 'Alma Valley', '33.701052', '44.844308', 'ACTIVE', NULL, NULL),
(43, 'Дом Бюрнье  (Domanie Burnier)', 'Краснодарский край, ст. Натухаевская', '&lt;p&gt;Уникальная история создания гравитационной винодельни в России потомственным виноделом из Швейцарии. В 2003 году были заложены первые виноградники, а в 2009 году первая партия вина уходит на экспорт в Европу. &amp;nbsp;&lt;/p&gt;', 'catalog/burnier/burnier_bg.jpg', 'catalog/burnier/burnier_bg.jpg', 'catalog/burnier/burnier_bg.jpg', 2, 'Купить вино Бюрнье, красностоп, люблю, каберне фран', 0, 4, 0, 'Дом Бюрнье  (Domanie Burnier)', '37.581455', '44.916450', 'ACTIVE', NULL, NULL),
(44, 'Солнечная Долина', 'Республика Крым, г. Судак, с. Солнечная Долина', '&lt;p&gt;История хозяйства началось с 1888 года, когда Лев Голицын приобрёл Токлукское имение у статской советницы Эссен, а чуть позднее заочно продает часть имения князю Горчакову. Символом заключенного договора явилась новая марка вина &quot;Каберне князя Горчакова 1888 года&quot;, это вино было подарено лично Голицыным императору Николаю II.&lt;/p&gt;', 'catalog/sunnyvalley/sunny_bg.jpg', 'catalog/sunnyvalley/sunny_bg.jpg', 'catalog/sunnyvalley/sunny_bg.jpg', 4, 'Купить вино Солнечная Долина, меганом, чёрный доктор, чёрный полковник', 0, 7, 0, 'Солнечная Долина', '35.102640', '44.874778', 'ACTIVE', NULL, NULL),
(45, 'Шато Ле Гран Восток', 'Краснодарский край, Крымский район, х. Садовый', '&lt;p&gt;Первое в нашей стране винодельческое хозяйство полного цикла - от выращивания винограда до розлива вина в бутылку, созданное по образцу французского шато. Уникальный российско-французский проект стартовал под руководством Николая Пинчука, где главным виноградарем и виноделом выступил Франк Дюссенер.&lt;/p&gt; &amp;nbsp; &amp;nbsp;&lt;br&gt;', 'catalog/legrand/legran_bg.jpg', 'catalog/legrand/legran_bg.jpg', 'catalog/legrand/legran_bg.jpg', 2, 'Купить вина Шато ле гран Восток', 0, 2, 0, 'Шато Ле Гран Восток', '37.753460', '45.015946', 'ACTIVE', NULL, NULL),
(47, 'Винодельня Павла Швеца UPPA', 'Республика Крым, г. Севастополь, с. Родное', '&lt;p&gt;Винодельческое хозяйство UPPA основано в 2008 году Павлом Швецом, человеком по-настоящему влюбленным в вино и в свой родной край. UPPA Winery единственная винодельня в России, которая производит вино биодинамическим способом. &amp;nbsp;&lt;/p&gt;', 'catalog/shvec/background_main.jpg', 'catalog/shvec/background_main.jpg', 'catalog/shvec/background_main.jpg', 4, 'Вино Павел Швец UPPA купить, Севастополь, мускат амбер Павел Швец, пино нуар карбоник, кокур баррель', 1, 10, 1, 'Винодельня Павла Швеца UPPA', '33.739959', '44.553909', 'ACTIVE', NULL, NULL),
(48, 'Кубань-Вино', 'Краснодарский край, Темрюкский район, станица Старотитаровская ', '&lt;p&gt;Таманский полуостров в Краснодарском крае издревле был центром виноделия. Еще в античные времена виноторговлю отсюда вели с другими регионами причерноморья и нынешней Европы.&lt;br&gt;&lt;/p&gt;', 'catalog/kuban/main_bg.jpg', 'catalog/kuban/main_bg.jpg', 'catalog/kuban/main_bg.jpg', 2, 'Кубань-Вино купить вина Кубани, мадера, кюве Александр ', 0, 11, 0, 'Кубань-Вино', '37.159512', '45.224697', 'ACTIVE', NULL, NULL),
(49, 'Винодельня Ведерниковъ', 'Ростовская область, хутор Ведерников', '&lt;p&gt;Название хозяйства связано с наименованием хутора Ведерников, который расположен на правом берегу реки Дон, в 160 километрах от Ростова-на-Дону. Казаки, заселившие берега Дона более 500 лет назад смогли возродить виноградарство и виноделие.&amp;nbsp;&lt;/p&gt;&lt;p&gt;Создатель хозяйства Валерий Тройчук достиг серьёзных результатов, работая с автохтонными сортами, которые высоко оценили иностранные эксперты и после этого они заговорили о Русском Вине. &amp;nbsp;&amp;nbsp;&lt;br&gt;&lt;/p&gt;', 'catalog/vedernikov/maiin_bg.jpg', 'catalog/vedernikov/maiin_bg.jpg', 'catalog/vedernikov/maiin_bg.jpg', 3, 'Ведерников винодельня, вино Ведерников, Красностоп Ведерников', 0, 12, 0, 'Винодельня Ведерниковъ', '41.145993', '47.582082', 'ACTIVE', NULL, NULL),
(50, 'Винтрест Саук-Дере', 'Краснодарский край, Крымский район, п. Саук-Дере', '&lt;p&gt;Винтрест «Саук-Дере» — уникальное место со знаменательной историей. Первые виноградники появились тут в далеком 1926 году в период зарождения советского виноделия, исторические каменоломни стали превращаться в место для выдержки вин. В 50-х годах предприятие «Саук-Дере» уже считалось жемчужиной советского виноделия.&lt;br&gt;&lt;/p&gt;', 'catalog/saukdere/main_bg.jpg', 'catalog/saukdere/main_bg.jpg', 'catalog/saukdere/main_bg.jpg', 2, 'Купить вина Саук-Дере, ледяное вино рислинг', 0, 13, 0, 'Винтрест Саук-Дере', '37.887159', '44.900288', 'ACTIVE', NULL, NULL),
(51, 'Усадьба Дивноморское', 'Краснодарский край, Геленджикский район,  с. Дивноморское', '&lt;p&gt;Винодельня премиального уровня, поставила себе задачу на создание вин высокого уровня. Для этого хозяйство пригласило мэтра итальянской энологии Риккардо Катарелла и талантливого энолога Маттео Колетти. Относительно молодое предприятие успешно конкурирует с европейскими винами и завоёвывает престижные награды на международных конкурсах.&lt;br&gt;&lt;/p&gt;', 'catalog/divnomorskoe/main_bg.jpg', 'catalog/divnomorskoe/main_bg.jpg', 'catalog/divnomorskoe/main_bg.jpg', 2, 'Купить вино Усадьба Дивноморское, Ребо и Марселан можно в магазине русское вино, на Расплетина 21', 0, 14, 0, 'Усадьба Дивноморское', '38.137311', '44.499768', 'ACTIVE', NULL, NULL),
(52, 'Винодельня Юбилейная', 'Краснодарский край, Темрюкский район, п. Красноармейский', '&lt;p&gt;Винодельня «Юбилейная» расположена в поселке Красноармейский Краснодарского Края. История винодельни началась в 1966 году, когда на базе винпункта Сенновского винзавода было образовано самостоятельное предприятие. За прошедшие полвека появились новые технологии производства, современное оборудование и лаборатория, а территория урожайных земель компании увеличилась до 2500 гектаров. Росло и качество производимых вин. Винодельня «Юбилейная» всегда была новатором и оснащалась самым современным оборудованием.&lt;br&gt;&lt;/p&gt;', 'catalog/anniversary/main_bg.jpg', 'catalog/anniversary/main_bg.jpg', 'catalog/anniversary/main_bg.jpg', 2, 'Купить вино Юбилейная, достойный, селект', 0, 15, 0, 'Винодельня Юбилейная', '36.871414', '45.395061', 'ACTIVE', NULL, NULL),
(53, 'Инкерман', 'Республика Крым, г. Севастополь, Инкерман', '&lt;p&gt;Международный холдинг, который объединил крупнейшие хозяйства региона, юридически сложился в 2011 году. Его штаб-квартира обосновалась в Стокгольме (Швеция). В состав холдинга вошли два ООО:&amp;nbsp; «Инкерманский завод марочных вин» и «Качинский+», а также мощная агрофирма «Черноморец».&amp;nbsp; В результате во владениях холдинга оказались огромные площади (3 тысячи га) живописных и благоприятных для виноградарства долинах рек Кача и Альма, и один из самых развитых и знаменитых крымских винзаводов - Инкерманский.&amp;nbsp;&lt;br&gt;&lt;/p&gt;', 'catalog/inkerman/main_bg.jpg', 'catalog/inkerman/main_bg.jpg', 'catalog/inkerman/main_bg.jpg', 4, 'Купить вино Инкерман, Севастополь, Крым ', 0, 16, 0, 'Инкерман', '33.610071', '44.613588', 'ACTIVE', NULL, NULL),
(54, 'Вилла Романов', 'Краснодарский край, Темрюкский район, ст. Голубицкая', '&lt;p&gt;Андрей Романов, чья фамилия и послужила основой названия хозяйства, почти 10 лет последовательно воплощает в жизнь свою мечту. В 2007 году были приобретены земли недалеко от г. Темрюк Краснодарского края. С 2009 года здесь высаживают новые виноградники и одновременно ведется строительство здания винодельни и туристического комплекса. В 2016 году «Вилла Романов» принимает свой первый урожай.&lt;br&gt;&lt;/p&gt;', 'catalog/romanov/main_bg.jpg', 'catalog/romanov/main_bg.jpg', 'catalog/romanov/main_bg.jpg', 2, 'Купить вино вилла Романов, Голубицкое поместье', 0, 17, 0, 'Вилла Романов', '37.274955', '45.323314', 'ACTIVE', NULL, NULL),
(55, 'Гай-Кодзор', 'Краснодарский край, г. Анапа, с. Гай-Кодзор', '&lt;p&gt;Современная история хозяйства &quot;Гай-Кодзор&quot; берет начало в 2002 году. Известные французские виноделы Алэн Дюга и Ноэль Рабо стали консультантами и &quot;двигателями идей&quot; для коллектива единомышленников.&lt;/p&gt;&lt;p&gt;Первый из них к этому времени больше 30 лет был управляющим, второй - свыше двух десятилетий - советником-энологом Шато ля Нерт. Это хозяйство одно из самых знаменитых во французском регионе Шатонеф-дю-Пап, в долине Роны.&amp;nbsp;&lt;/p&gt;', 'catalog/kodzur/main_bg.jpg', 'catalog/kodzur/main_bg.jpg', 'catalog/kodzur/main_bg.jpg', 2, 'Купить вино Гай-Кодзор, вионье, мускат, розе, гевюрцтраминер', 0, 18, 0, 'Гай-Кодзор', '37.437739', '44.854398', 'ACTIVE', NULL, NULL),
(56, 'Фанагория', 'Краснодарский край, Темрюкский р-н, п. Сенной', '&lt;p&gt;Более 6 веков назад на Таманском полуострове, омываемом водами Керченского пролива, располагались владения древнего Боспорского царства. Основанная в 500-х годах до н.э., Фанагория была вторым, после столицы царства - Пантикопея, городом в государстве, который именовали &quot;азиатской&quot; столицей. И уже тогда славилась Фанагория своими винами, которыми вела торговлю со всем миром.&lt;br&gt;&lt;/p&gt;', 'catalog/fanagoria/main_bg.jpg', 'catalog/fanagoria/main_bg.jpg', 'catalog/fanagoria/main_bg.jpg', 2, 'Купить вина Фанагория', 0, 19, 0, 'Фанагория', '36.994249', '45.286223', 'ACTIVE', NULL, NULL),
(68, 'Новый Cвет', 'Республика Крым, г. Судак, пгт. Новый Свет', '&lt;p&gt;Организованных Голицыным питомник вскоре стал обладателем большой коллекции сортов, в котором произрастали свыше 600 наименований. Долгие годы работы привели Голицына к осознанию, что как сам виноград, так и рожденное им вино - &quot;продукт местности&quot;, по выражению самого князя.&lt;br&gt;&lt;/p&gt;', 'catalog/new_world/main_bg.jpg', 'catalog/new_world/main_bg.jpg', 'catalog/new_world/main_bg.jpg', 4, 'Купить шампанское Новый Свет, крымское шампанское', 0, 20, 0, 'Новый Cвет', '34.914779', '44.825611', 'ACTIVE', NULL, NULL),
(69, 'Мысхако', 'Краснодарский край, г. Новороссийск, с. Мысхако', '&lt;p&gt;На черноморском побережье знаменитая Цемесская бухта - вторая по значимости после Севастопольской. Здесь расположен город Новороссийск, и здесь же, раскинулись живописные виноградники &quot;Мысхако&quot;. Агрофирма &quot;Мысхако&quot; входит в число старейших виноградарских и винодельческих хозяйств России. Своё начало история хозяйства отсчитывает с 1869 года, когда здесь были высажены первые лозы.&amp;nbsp;&lt;br&gt;&lt;/p&gt;', 'catalog/myshako/main_bg.jpg', 'catalog/myshako/main_bg.jpg', 'catalog/myshako/main_bg.jpg', 2, 'Купить вино Усадьба Мысхако, Шато Пино', 0, 21, 0, 'Мысхако', '37.760521', '44.657294', 'ACTIVE', NULL, NULL),
(70, 'Винное подворье старого грека', 'Краснодарский край, город-курорт Анапа, с. Витязево', '&lt;p&gt;Валерий Ставрович Асланов (Асланидис) - спортсмен, чемпион мира по дзюдо и профессиональный винодел решил основать в 2011 году под Анапой винодельню. Название - это дань уважения грекам поселенцам, которые организовали первую винодельню в этом же месте в 1843 году. Сохранился винный погреб 1857 года постройки, он и сейчас используется на хозяйстве. Старый грек - это один из тех, кто построил как раз ту, первую винодельню.&lt;/p&gt;', 'catalog/greek/main_bg.jpg', 'catalog/greek/main_bg.jpg', 'catalog/greek/main_bg.jpg', 2, 'Купить вино старого грека Анапа, Витязево, красностоп понтийское', 1, 22, 0, 'Винное подворье старого грека', '37.283839', '45.002352', 'ACTIVE', NULL, NULL),
(71, 'Усадьба Перовских', 'Республика Крым, г. Севастополь, п. Любимовка', '&lt;p&gt;В 1889 году негоциант Федор Оттонович Шталь, который вел в Санкт-Петербурге торговлю винами южных пределов России, выкупил имение ради амбициозных планов по созданию собственного российского вина. С этой целью новый владелец уже год спустя расширил площади виноградников новыми посадками, и выстроил дом с вместительными подвалами для хранения и выдержки вин. Так «Приморское» превратилось в «Алькадар» (в переводе с персидского - божье соизволение).&amp;nbsp;&lt;br&gt;&lt;/p&gt;', 'catalog/perovskyh/main_bg.jpg', 'catalog/perovskyh/main_bg.jpg', 'catalog/perovskyh/main_bg.jpg', 4, 'Купить вино Усадьба Перовских, мерло, пино нуар 1860', 0, 23, 0, 'Усадьба Перовских', '33.548716', '44.667340', 'ACTIVE', NULL, NULL),
(72, 'Негоциантский проект YAIYLA', 'Республика Крым, г. Севастополь, п. Любимовка', '&lt;p&gt;Крымский негоциантский проект YAIYLA это новое явление в современном российском виноделии. Негоцианство это не просто бизнес, это определённая философия, через которую Виталий Маринчук старается выразить самобытную природу Крыма и доказать, что в этом регионе можно выпускать вина не только высокого качества, но и вина из редких, автохтонных сортов винограда. &amp;nbsp;&amp;nbsp;&lt;/p&gt;', 'catalog/ayaila/yailamain.jpg', 'catalog/ayaila/yailamain.jpg', 'catalog/ayaila/yailamain.jpg', 4, 'Купить крымское вино Яйла (YAIYLA), оранжевое вино, кокур, марселан, мальбек, пино менье', 1, 0, 0, 'Негоциантский проект YAIYLA', '33.548716', '44.667340', 'ACTIVE', NULL, NULL),
(73, 'Каракезиди', 'Краснодарский край, г. Анапа, х. Куток', '&lt;p&gt;Трудолюбие, честность, открытость и непосредственность, умение направить все силы собственные и окружающей среды в одно русло, следование биодинамическим принципам и опыт предков - всё это про Яниса Каракезиди. Ничего лишнего, только тонкое созвучие виноградарства и виноделия в гармонии с природой и окружающими людьми.&amp;nbsp;&lt;/p&gt;', 'catalog/karakezidi/yanismain3.jpg', 'catalog/karakezidi/yanismain3.jpg', 'catalog/karakezidi/yanismain3.jpg', 2, 'Вино Каракезиди Стретто, каберне фран, красностоп', 1, 0, 1, 'Каракезиди', '37.459828', '44.956249', 'ACTIVE', NULL, NULL),
(74, 'Коктебель', 'Республика Крым, г. Феодосия, пгт. Коктебель', '&lt;p&gt;Появление предприятия по производству вин на юго-восточных берегах Крыма вполне закономерно. Сотни лет на этих землях выращивались уникальные сорта винограда, а виноделы края снискали особое расположение покровителя Диониса. Достойным продолжателем древних традиций стал А.Э. Юнге, открывший в 1906 году винодельню с погребами, построенными по всем правилам европейской винодельческой практики того времени.&lt;/p&gt;', 'catalog/koktebel/koktebel-main.JPG', 'catalog/koktebel/koktebel-main.JPG', 'catalog/koktebel/koktebel-main.JPG', 4, 'Купить вино Коктебель, Крым', 0, 0, 0, 'Коктебель', '35.239471', '44.960031', 'ACTIVE', NULL, NULL),
(76, 'Винодельня Олега Репина', 'Республика Крым, г. Севастополь, п. Любимовка', '&lt;p&gt;Его вполне можно назвать символом сегодняшнего дня в виноделии России:\r\nнебольшое терруарное хозяйство, где высокообразованный энолог создает\r\nколлекционные авторские вина. Олег Репин имеет два образования по профилю энолога и немалый опыт работы на винодельнях Крыма. Работу любого рода,\r\nот изучения почвы до проектирования и поставок оборудования, Олег предпочитает\r\nвыполнять самостоятельно. Всегда рядом с ним - верный\r\nдруг и помощница, вдохновитель всех идей, супруга\r\nЛюдмила.&lt;/p&gt;', 'catalog/shvec/repin-main1.jpg', 'catalog/shvec/repin-main1.jpg', 'catalog/shvec/repin-main1.jpg', 4, 'Вино Олега Репина купить, Крым, сира листва, шенен блан, пино нуар Олег Репин', 1, 0, 0, 'Винодельня Олега Репина', '33.548716', '44.667340', 'ACTIVE', NULL, NULL),
(77, 'Дербентская винодельческая компания', 'Дагестан, г. Дербент', '&lt;p&gt;Новое винодельческое предприятие в Дербенте, которое стало результатом необыкновенно амбициозного проекта,\r\nсочетающего в себе самые высокие существующие на данный момент технологии, и\r\nзнания зарубежных специалистов.&lt;/p&gt;', 'catalog/dvk/foto-dvk-main.jpg', 'catalog/dvk/foto-dvk-main.jpg', 'catalog/dvk/foto-dvk-main.jpg', 7, 'Дербентская винодельческая компания', 0, 0, 0, 'Дербентская винодельческая компания', '48.288766', '42.057669', 'ACTIVE', NULL, NULL),
(79, 'Дача Сердюка', 'Ростовская область, г. Новочеркасск', '&lt;p&gt;Всё началось в 2003 году, с переработки первых 180 кг Каберне Совиньона. Уже тогда были заложены основные традиции, которые и сейчас соблюдает винодельня: никакого диоксида серы и не использовать пластиковую тару.&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;', 'catalog/dacha_serduka/serduk-main.jpg', 'catalog/dacha_serduka/serduk-main.jpg', 'catalog/dacha_serduka/serduk-main.jpg', 3, 'Дача Сердюка вино купить, вино без серы Дача Сердюка, биовино из Ростова', 1, 0, 0, 'Дача Сердюка', '40.093725', '47.422052', 'ACTIVE', NULL, NULL),
(84, 'Два сердца', 'Республика Крым, с. Геройское', '&lt;p&gt;«Два сердца» - семейный винодельческий проект. В основе его создания\r\nлежит романтическая история отношений Павла Пестова и его жены Оксаны. Всё\r\nначиналось как хобби. Вина «Два сердца» Павел и Оксана придумали для себя и\r\nблизких друзей.&lt;/p&gt;', 'catalog/dvaserdca/pervoe-foto-dva.JPG', 'catalog/dvaserdca/pervoe-foto-dva.JPG', 'catalog/dvaserdca/pervoe-foto-dva.JPG', 4, 'Купить вино Два Сердца, Павел Пестов, Крым, пти мансенг, вионье, пино гри блаш', 1, 0, 0, 'Два сердца', '33.746489', '45.138086', 'ACTIVE', NULL, NULL),
(85, 'Вина Бани', 'Ростовская область, х. Малая Мартыновка ', '&lt;p&gt;Вина Бани - семейная винодельня Юрия Химичева. Расположена в Ростовской области в хуторе Малая Мартыновка. Название винодельни связано с её расположением, она была организована в помещении бывшей хуторской бани.&amp;nbsp;&lt;/p&gt;', 'catalog/abani/vinabani-main.jpg', 'catalog/abani/vinabani-main.jpg', 'catalog/abani/vinabani-main.jpg', 3, 'Вина Бани, купить ростовское вино, Отец и 33,3 ', 1, 0, 0, 'Вина Бани', '41.831956', '47.332458', 'ACTIVE', NULL, NULL),
(86, 'Сорт', 'Краснодарский край, г. Новороссийск, ст. Раевская', '&lt;p&gt;Первый виноград был заложен в 2017 году. Саженцы прибыли из питомника Раушедо. В то же время началось строительство винодельни, которое было окончено в 2018 году.&lt;/p&gt;', 'catalog/sort/sort-main-foto.jpg', 'catalog/sort/sort-main-foto.jpg', 'catalog/sort/sort-main-foto.jpg', 2, 'Винодельня Сорт Sort кубанская винодельня, расположенная на горе Сухая.    ', 1, 0, 0, 'Сорт', '37.515679', '44.837890', 'ACTIVE', NULL, NULL),
(87, 'Шумринка', 'Краснодарский край, г. Анапа, с. Гай-Кодзор', '&lt;p&gt;Шумринка - новый винодельческий проект, расположенный недалеко от села Гай-Кодзор. Название винодельни связано с рекой Шумринкой, протекающей недалеко от виноградников.&lt;/p&gt;', 'catalog/shumrinka/shumrinka-main2.jpg', 'catalog/shumrinka/shumrinka-main2.jpg', 'catalog/shumrinka/shumrinka-main2.jpg', 2, 'Шумринка винодельня, Петрикор и Семисам вина винольни Шумринка ', 0, 0, 0, 'Шумринка', '37.437403', '44.854518', 'ACTIVE', NULL, NULL);
INSERT INTO `wines` (`id`, `title`, `price`, `meta_title`, `description`, `production_feature`, `combination`, `feature`, `innings`, `model`, `winery_id`, `grape_sort_id`, `edition`, `manufacturer_id`, `region_id`, `color_id`, `fortress`, `year`, `volume`, `sugar_id`, `count`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`, `excerpt_id`) VALUES
(50, 'Шардоне', 1250, 'Вино Имение Сикоры Шардоне белое сухое 2016', '', '<p>Ручной сбор винограда, тщательная селекция ягод, ферментация при контролируемой температуре 15 - 17°С с последующей выдержкой в стали на тонком осадке 6 месяцев. Частичная выдержка в бочках из французского дуба с \"белым обжигом\".<br></p>', '<p>Шардоне Сикоры станет ярким гастрономическим сопровождением к блюдам из белой рыбы, а также домашней птицы и кролика.<br></p>', '<p>Вино благородного золотисто-соломенного цвета. Сложная, комплексная ароматика белых цветов и фруктов дополнена яркой сливочной нотой, ароматом свежей выпечки с ванилью и корицей. Вкус вина насыщенный и округлый с тонким акцентом миндальной маслянистости и продолжительным послевкусием.<br></p>', '<p>Подавать при температуре 10-12С<br></p>', 'sikory-chardonnay', 1, 81, 2200, 38, 2, 2, 14, 2016, 0.75, 1, 99, 'wines/June2020/wine_sikory_shardone.jpg', 'vino-sikory-chardonnay-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 30),
(51, 'Рислинг Семейный Резерв', 1950, 'Имение Сикоры Рислинг Семейный Резерв белое сухое 2015', '', 'Использование сусла-самотека. Брожение при 14 - 16С, выдержка на тонком дрожжевом осадке с перемешиванием. Частичная выдержка в стали, частичная выдержка в дубовых бочках в течение 1 месяца.<br>', '<p>Прекрасная пара для изысканных блюд из рыбы и морепродуктов, белого мяса и овощей, мягких сыров и фруктов.<br></p>', '<p>С тонкой ароматикой белых цветов и сухофруктов, подчёркнутым стильной бензольной нотой. Гастрономическая кислотность дополнена тонами сухофруктов, айвы и ржаной корочки. Тонкое минеральное послевкусие, продолжтительное и приятное.<br></p>', '<p>Подавать при температуре 10 - 12С.<br></p>', 'sikory risling family rezerv 2015', 1, 56, 1200, 38, 2, 2, 14, 2015, 0.75, 1, 98, 'wines/June2020/SIKORY-Riesling-Semejnyj-2 (2).jpg', 'vino-sikory-risling-semeinyi-rezerv-2015', 'Рислинг семейный резерв от винодельни Имение Сикоры. Лучший рислинг России - рислинг семейный резерв Имение Сикоры.', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(52, 'Совиньон Блан', 1250, 'Вино Имение Сикоры Совиньон Блан белое сухое 2015', '', 'Ручной сбор винограда, тщательная селекция ягод. Ферментация при контролируемой температуре 15 - 17 С, с последующей выдержкой в стали на тонком осадке 6 месяцев. Частичная выдержка в бочках из французского дуба с \"белым обжигом\". <br>', 'Составит отличную гастрономическую пару блюдам из морепродуктов, мягким сырам, а также рыбе на гриле.<br>', 'Ароматика вина изобилует нотами душистой липы, почек чёрной смородины и цветущего персика. Сложный ароматический комплекс завершают тона тропических фруктов. Вкус вина насыщенный, округлый, с ярко выраженной кислотностью, с продолжителым минеральным послевкусием.<br>', 'Подавать при температуре 10 - 12 С.<br>', 'sikory-sovignon-blan', 1, 68, 1200, 38, 2, 2, 13.5, 2015, NULL, 1, 100, 'wines/June2020/SIKORY-Sauvignon-Sikory.jpg', 'vino-sikory-sovignon-blan-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 29),
(54, 'Рислинг', 1150, 'Рислинг Имение Сикоры белое сухое 2015', '', '<p>Использование сусла-самотека, осветление сусла в течение 24 - 28 часов, при температуре 08 - 10С. Брожение при температуре 14 - 16С, выдержка на тонком дрожжевом осадке с батонажем в течение 3 - 6 месяцев. Далее выдержка в стали.<br></p>', '<p>Хороший аперитив, великолепно подходит к белой рыбе и свинине, к пряным блюдам тайской и китайской кухонь, хорошо аккомпанирует фрукты, морепродукты, сливочные сыры.<br></p>', '<p>Отличается яркой цитрусовой ароматикой с миндальными нотками. Освежающий, с легкой сбалансированной кислотностью вкус. Продолжительное сочное послевкусие.<br></p>', '<p>Подавать при температуре 10 - 12С.<br></p>', 'sikory risling 2015', 1, 56, 8000, 38, 2, 2, 14.2, 2015, 0.75, 1, 98, 'wines/June2020/wine_sikory_risling.jpg', 'vino-sikory-risling-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(55, 'Каберне Совиньон', 1250, 'Вино Имение Сикоры Каберне Совиньон красное сухое 2014', '', '<p>Мацерация при температуре 12 - 14C. Брожение на мезге, с использованием ремонтажа и делестажа. Яблочно-молочное брожение, выдержка в стали, частичная выдержка в бочках из французского дуба средней степени обжига.<br></p>', '<p>Прекрасно дополнит мясные блюда из говядины под острыми пикантными соусами, стейк прожарки медиум, твердые сыры.<br></p>', '<p>С индивидуальной стильной ароматикой черных ягод, пряностей и благородной нотой табачного листа. Неповторимую комплексность и тельность вина дополняют шелковистые танины и ароматическое послевкусие ежевики, черешни и черной смородины. Долгое послевкусие с яркой джемовой нотой.<br></p>', 'Подавать при температуре 16-18 С , рекомендуется декантировать.<br>', 'sikory-kabernet-sovignon 2014', 1, 20, 2000, 38, 2, 1, 14, 2014, 0.75, 1, 100, 'wines/June2020/wine_sikory_cabernet_sovignon.jpg', 'vino-sikory-caberne-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 56),
(58, 'Каберне Семейный Резерв', 1950, 'Вино Имение Сикоры Каберне Совиньон Семейный Резерв красное сухое 2014', '', '<p>Мацерация при температуре 12 - 14С Брожение на мезге, с использованием ремонтажа и делестажа. Яблочно-молочное брожение, выдержка в стали, частичная выдержка в бочках из французского дуба, в течение 9 месяцев. <br></p>', '<p>Идеальная пара для изысканных блюд из ягнятины, мяса дичи и выдержанных сыров.<br></p>', '<p>С благородной ароматикой черной смородины, ежевики и чернослива, дополненных нюансами лакрицы и черного перца. Вкус яркий, гармоничный, элегантно подчеркнутый округлостью танинов. Очень долгий и сочный финал с оттенками кофе и дымной нотой.<br></p>', '<p>Подавать при температуре 16 - 18 С , рекомендуется декантировать.<br></p>', 'sikory-kaberne family rezerv 2014', 1, 20, 1400, 38, 2, 1, 14, 2014, 0.75, 1, 99, 'wines/June2020/SIKORY-Cabernet-Semejnyj.jpg', 'vino-sikory-caberne-semeinyi-rezerv-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 57),
(61, 'Кокур - Рислинг', 3200, 'Винодельня Павла Швеца UPPA Кокур Рислинг белое сухое 2016', '', '<p>Вино ферментировалось в стали с дальнейшим настаиванием на дрожжевом осадке 6 месяцев.<br></p>', '<p>Рапан запеченый, мидии, жаренная барабулька.<br></p>', '<p>Полнотелое, маслянистое вино с прекрасным балансом. Ароматное, яркое. Тона персика, абрикоса, желтого яблока.<br></p>', '<p>Подавать при температуре 10 - 12С.<br></p>', 'UPPA Kokur Risling 2016', 47, 23, 1326, 4, 4, 2, 14, 2016, 0.75, 1, 98, 'wines/June2020/image001.jpg', 'vino-UPPA-kokur-risling-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 25),
(62, 'Гевюрцтраминер', 4850, 'Вино Винодельня Павла Швеца UPPA гевюрцтраминер белое сухое 2016', '', '<p>Брожение в открытой ёмкости с настаиванием на мезге без гребней в течение 45 дней. Проводились пижажи два раза вдень. Выдержка в бочке 6 месяцев.<br></p>', '<p>Блюда из птицы, морепродуктов. Блюда азиатской кухни.<br></p>', '<p>Необычайно яркий аромат, где на фоне тонов персика и абрикоса выходит очень яркий оттенок эфирного масла розы. Во вкусе очень сильное, насыщенное вино, гармоничное с некоторой терпкостью на финише. <br></p>', '<p>Подавать при температуре 10-12С.<br></p>', 'Gewurztraminer UPPA 2016', 47, 14, 832, 4, 4, 2, 14, 2016, 0.75, 1, 97, 'wines/June2020/Gewurztraminer_Amber_0.75.jpg', 'vino-UPPA-Gewurztraminer-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 53),
(63, 'Пино Нуар', 7500, 'Павел Швеца UPPA Пино Нуар Nummulit красное сухое 2015', '', '<p>Длительная мацерация более 20 суток после окончания брожения. Выдержка в дубе 18 месяцев.<br></p>', '<p>Выдержанные сыры из овечьего и козьего молока. Телятина в духовом шкафу, пернатая дичь.<br></p>', '<p>Очень ароматное, насыщенное вино. Тона уваренной смородины, рябины, пиона и ванили. Достаточно плотное вино. Гармоничное, с отличным балансом. Танин мягкий, шелковистый. Вкус вина долгий, яркий.<br></p>', '<p>Подавать при температуре 16 - 18С.<br></p>', 'Pinot Noir nummulit UPPA 2015', 47, 47, 1741, 4, 4, 1, 14, 2015, 0.75, 1, 94, 'wines/June2020/Pinot_Noir_0.75_2015.jpg', 'vino-Pinot-Noir-nummulit-UPPA-2015', 'Винодельня Павла Швеца и его лучший пино нуар. UPPA винодельня из Крыма.', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 51),
(64, 'Шардоне', 5760, 'Вино Винодельня Павла Швеца UPPA Шардоне белое сухое 2015', '', '<p>Вино ферментировалось и выдерживалось в дубовых бочках, со всеми \"вытекающими\" последствиями.<br></p>', '<p>Хорошо само по себе, но можно сочетать с блюдами из запеченой морской рыбы, улитками, полутвердыми сырами.<br></p>', '<p>Сложный аромат: персик, ваниль, мед, белые цветы. Насыщенное, хорошо сбалансированное, в атаке маслянистое.<br></p>', 'Подавать при температуре 10-12 С.<br>', 'Chardonnay UPPA 2015', 47, 81, 1015, 4, 4, 2, 14, 2015, 0.75, 1, 98, 'wines/June2020/Chardonnay_0.75.jpg', 'vino-Chardonnay-UPPA-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 43),
(65, 'Рислинг', 4800, 'Вино Винодельня Павла Швеца UPPA Рислинг белое сухое 2016', '', '<p>Брожение шло медленно при низкой температуре, окончание брожения случилось только в конце весны. Все это время вино находилось на дрожжевом осадке с еженедельными батонажами. <br></p>', 'Блюда из белой рыбы и морепродуктов с сливочными соусами.<br>', '<p>Сложное, многогранное вино с сладкими, фруктовыми ароматами: абрикос, персик, немного акации и сена. Насыщенное, плотное вино, но с великолепным балансом. Долгое послевкусие.<br></p>', 'Подавать при температуре 10-12 С.<br>', 'Risling UPPA 2016', 47, 56, 2580, 4, 4, 2, 14, 2016, 0.75, 1, 100, 'wines/June2020/Riesling_0.75_2016.jpg', 'vino-Risling-UPPA-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 25),
(67, 'Каберне Совиньон-Мерло', 5600, 'Вино Винодельня Павла Швеца UPPA Каберне Совиньон Мерло красное сухое 2015', '', 'Вино сделано по классической технологии. Брожение на диких дрожжах и дальнейшая выдержка в дубе.<br>', '<p>Рибай стейк, говядина на гриле, твёрдые выдержанные сыры.<br></p>', '<p>Очень ароматное, яркое, с тонами черных ягод и джема. Насыщенное вино, хорошо сбалансированное, с долгим послевкусием. Классика.<br></p>', '<p>Подавать при температуре 16-18С.<br></p>', 'UPPA Cabernet Sauvignon Merlot 2015', 47, 20, 2470, 4, 4, 1, 14, 2015, 0.75, 1, 99, 'wines/June2020/Cabernet_Sauvignon_Merlot_0.75_2015.png', 'vino-UPPA-caberne-sauvignon-merlo-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 14),
(68, 'Совиньон Блан', 3300, 'Вино Винодельня Павла Швеца UPPA Совиньон Блан белое сухое 2016', '', '<p>Брожение шло в стальной ёмкости, при низкой температуре. Все это время вино находилось на дрожжевом осадке 5 месяцев. <br></p>', 'Летние свежие салаты, жаренная жирная рыба. Идеально камбала, барабулька.<br>', '<p>Очень ароматное, свежее вино с тонами зеленого яблока, лайма, листьев смородины, есть и минеральные нотки. Вино лёгкое, со свежей кислотностью в классическом стиле. Вкус средней длины.<br></p>', 'Подавать при температуре 10-12 С.<br>', 'Sauvignon Blan UPPA 2016', 47, 68, 4200, 4, 4, 2, 14, 2016, 0.75, 1, 93, 'wines/June2020/Savignon_Blan_0.75.jpg', 'vino-Sauvignon-Blan-UPPA-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 25),
(69, 'Мускат-Гевюрцтраминер', 4850, 'Вино Винодельня Павла Швеца UPPA Мускат Гевюрцтраминер белое сухое 2016 ', '', '<p>Перед ферментацией проводилась мацерация, в течение 24 часов, при температуре 9С. Брожение при температуре 16С. <br></p>', 'Японская кухня. Также блюда из мелкой жаренной черноморской рыбы.<br>', '<p>Очень ароматное, парфюмированное вино. Яркое и свежее: персик, растительные тона, роза и мёд. Хорошо сбалансированное, свежее и яркое вино.<br></p>', 'Подавать при температуре 10-12 С.<br>', 'Muscat Gewurztraminer UPPA 2016', 47, 14, 1557, 4, 4, 2, 14, 2016, 0.75, 1, 98, 'wines/June2020/Muscat_Gewurztraminer_0.75.png.jpg', 'vino-Muscat-Gewurztraminer-UPPA-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(73, 'Цимлянский Чёрный', 1200, 'Вино Винодельня Ведерников Цимлянский Чёрный розовое сухое 2015', '', '<p>Ферментация вина проходила в чанах из нержавеющей стали<br></p>', 'Вино составит отличную пару салату с креветками и руккулой, утке, десертам с ягодами и фруктам.<br>', '<p>Свежий аромат вина благоухает оттенками малины, клубники, розы, которые дополняют сливочные нотки и выраженная минеральность. Вино обладает округлым, свежим, насыщенным вкусом с яркой гаммой из оттенков красных ягод и барбариса. Послевкусие средней длины с оттенками клубники.<br></p>', 'Подавать при температуре 08 - 10 С .<br>', 'vedernikov TS CH 2015', 49, 2, 10000, 20, 3, 3, 12.5, 2015, 0.75, 1, 100, 'wines/June2020/ведерников/4_Цимлянский Черный розовое.JPG', 'vino-vedernikov-TS-CH-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 49),
(74, 'Каберне Совиньон', 1300, 'Вино Альма Валей Каберне Совиньон красное сухое 2013', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, частичная выдержка в дубовых бочках.<br>', '<p>Рекомендуется к широкой гамме жареных или тушеных блюд, приготовленных из мяса.<br></p>', '<p>Насыщенный тёмно-рубиновый цвет с красивым блеском. Гармоничный аромат с выраженными сортовыми характеристиками Каберне-Совиньона (тона чёрной смородины, черники, сафьяновой кожи, специй, сушеных степных трав) и богатый вкус с умеренной терпкостью бархатистых танинов.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma kabernet-sovignon 2013', 42, 20, 20000, 2, 4, 1, 13.5, 2013, 0.75, 1, 100, 'wines/June2020/альма/SORT_CAB_S_13.jpg', 'vino-alma-kabernet-sovignon-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(75, 'Сибирьковый', 890, 'Вино Винодельня Ведерников Сибирьковый белое сухое 2015', '', '<p>Ферментация вина проходила в нержавеющих ёмкостях.<br></p>', 'Утонченный аромат вина изобилует оттенками минералов, цитрусовых, трав, тостов и белых цветов. Вино привлекает мягким, чистым вкусом с гладкой текстурой, хрустящей кислинкой и длительным, минеральным послевкусием.<br>', 'Вино идеально в качестве аперитива, а также как сопровождение для холодных закусок из рыбы и морепродуктов, салатов из зелени, блюд из белого мяса, пиццы, пасты, мягким сырам.<br>', 'Подавать при температуре 10 - 12 С .<br>', 'vedernikov sibirkovyi 2015', 49, 65, 10000, 20, 3, 2, 12.5, 2015, 0.75, 1, 100, 'wines/June2020/ведерников/5_Сибирьковый.jpg', 'vino-vedernikov-sibirkovyi-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(76, 'Цимлянский Чёрный', 1300, 'Вино Винодельня Вина Арпачина Цимлянский Чёрный красное сухое 2017', '', 'Вино приготовлено по классической технологии красных вин из аборигенного сорта винограда Цимлянский Чёрный. Выдержка в бочке из французского дуба не менее 3 месяцев. <br>', 'Вино прекрасно сочетаться с мясными блюдами, твёрдыми сырами и лёгкими закусками.<br>', '<p>Вино рубинового цвета. Насыщенный, сложный аромат вина раскрывается тонами кожи, дуба и вишни. Аромат необычный: щекочущая кислинка накладывается на сладость мармелада и брутальность животных тонов: ирга, брусника, жимолость, сушёное яблоко, жареное мясо, скотный двор, можжевельник. Вино обладает округлым, мягким вкусом с шелковистой текстурой и мягкими танинами. Кислотность дирижирует оркестром из красных ягод (брусники, клюквы, красной смородины), гибискуса, антоновки и кожи.<br></p>', 'Подавать при температуре 14 - 16 С. Обязательно декантировать. <br>', 'arpachin chornyi red 2017', 37, 2, 5963, 14, 3, 1, 13, 2017, 0.75, 1, 94, 'wines/June2020/arpacin/malik-cherny-2017.png', 'vino-arpachin-chernyi-krasnoe-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 36),
(77, 'Ркацители', 1200, 'Вино Винодельня Ведерников Ркацители белое сухое 2015', '', '<p>Ферментация в емкостях нержавеющей стали, дальнейшая выдержка в бочках из французского дуба в течение 6 месяцев.<br></p>', 'Вино отлично подойдет к салату с курицей и орешками, утке, тушеной с сухофруктами, свинине в сливочно-грибном соусе, морской рыбе на пару или сковороде..<br>', '<p>Нос вина интенсивный, свежий, комплексный с доминантой белых цветов и фруктов, которые раскрываются тропическими тонами бананов, дыни, груши дюшес, красных яблок и сочетают в себе сливки, ваниль и выраженную минеральную основу. Бочка смягчила типичный сортовой косточковый тон, что без сомнения пошло на пользу. Во вкусе вино полнотелое, округлое, умеренно-свежее с нюансами бананов, цукатов, выпечки и кедровых орешков. Послевкусие длительное сливочное с тонами выпечки. Потенциал выдержки около трех лет, хотя вино полностью готово к употреблению.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'Vedernikov rcatsiteli 2014', 49, 57, 10000, 20, 3, 2, 12.5, 2014, 0.75, 1, 100, 'wines/June2020/ведерников/6_РКАЦИТЕЛИ ВЫДЕРЖАННОЕ белое.jpg', 'vino-Vedernikov-rcatsiteli-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 54),
(78, 'Каберне Совиньон', 2100, 'Вино Винодельня Ведерников Каберне Совиньон красное сухое 2014', '', 'Выдержка в французском дубе 20 месяцев. Потенциал выдержки не менее 5 лет.<br>', 'Вино прекрасно подойдёт к тёплым салатам с дичью, выдержанному хамону, баранине, ягнёнку с пряными травами или стейку из говядины.<br>', '<p>Вино глубокого рубинового цвета, маслянистое с великолепным блеском. Нос вина интенсивный, комплексный, с нюансами чёрной смородины, сушеной вишни, выделанной кожи, яркой животной составляющей, копченостей, кедра, розмарина, чёрного перца и ванильной выпечки. Во вкусе вино полнотелое, танинное, округлое, ощущается всей полостью рта, умеренно-свежее с нотами чернослива, вишневого варенья, чёрной смородины, пряностей и кожи. Очень длинное послевкусие тонами ягодного конфитюра и специй. <br></p>', 'Подавать при температуре 17 - 18С, обязательно декантировать, чтобы раскрыть весь потенциал вина. <br>', 'Vedernikov Cabernet Sauvignon 2014', 49, 20, 10000, 20, 3, 1, 14.5, 2014, 0.75, 1, 99, 'wines/June2020/ведерников/8_КАБЕРНЕ СОВИНЬОН ВЫДЕРЖАННОЕ красное-.JPG', 'vino-Vedernikov-Caberne-sauvignon-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 39),
(79, 'Губернаторское Голубок', 850, 'Вино Винодельня Ведерников Губернаторское Голубок красное сухое 2016', '', 'Вино ферментировалось в ёмкостях из нержавеющей стали.<br>', 'Вино прекрасно сочетается с холодными закусками из сыровяленного мяса, мясным рагу с черносливом, птицей на гриле и тушенной говядиной.<br>', '<p>Вино обладает насыщенным ароматом, сотканным из нот чёрной смородины, ежевики, вишни, сливы и пряных специй. Вкус вина мягкий, шелковистый, фруктовый, с хорошо сбалансированной кислотностью, элегантными танинами и долгим послевкусием.<br></p>', 'Подавать при температуре 16 - 18 С . <br>', 'vedernikov golubok 2016', 49, 2, 10000, 20, 3, 1, 13, 2016, 0.75, 1, 98, 'wines/June2020/ведерников/3_Губернаторское Голубок красное.jpg', 'vino-vedernikov-golubok-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(80, 'Цимлянский Чёрный', 2100, 'Вино Винодельня Ведерников Цимлянский Чёрный красное сухое 2014', '', 'Выдержка в бочках из французского дуба 12 месяцев. Потенциал выдержки не менее 5 лет.<br>', 'Вино сочетается с горячими закусками, мясом на гриле, теплым салатом с телятиной и вишнёвой заправкой, лазаньей, каре ягнёнка, медальонами из говядины с ягодным соусом, а также с овощами и грибами на гриле.<br>', '<p>Вино глубокого рубинового-красного цвета, маслянистое с выраженными пурпурными бликами. Нос вина интенсивный, комплексный, с нюансами чёрной смородины, вишни, черноплодной рябины, сливок, табака, копченостей и специй. Во вкусе вино полнотелое, танинное, округлое, умеренно-свежее с яркими тонами вишни, чернослива, копченостей, шоколада, чёрного перца с табачной горчинкой и розмарином. Послевкусие длительное с тонами вишневого варенья и эвкалипта. <br></p>', 'Подавать при температуре 17 - 18 С , предварительно обязательно декантировать. <br>', 'Vedernikov Tsimlynski Chorny red 2014', 49, 2, 10000, 20, 3, 1, 14.5, 2014, 0.75, 1, 97, 'wines/June2020/ведерников/7_ЦИМЛЯНСКИЙ ЧЕРНЫЙ ВЫДЕРЖАННЫЙ красное.jpg', 'vino-Vedernikov-Tsimlynski-Chorny-krasnoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 37),
(81, 'Губернаторское Рислинг', 850, 'Вино Винодельня Ведерников Губернаторское Рислинг белое сухое 2015', '', 'Вино ферментировалось в ёмкостях из нержавеющей стали.<br>', 'Вино прекрасно подойдёт к лёгким салатам, различным морепродуктам, белому мясу, ракообразным или чёрной треске, мясным блюдам под соусом, роллам и суши.<br>', '<p>Выразительный, свежий аромат вина раскрывается оттенками цитрусовых и белых фруктов, груши, желтого яблока, минералов и специй. Вино демонстрирует чистый, освежающий вкус с минерально-цветочными оттенками, гладкой текстурой пикантной кислинкой в сдержанном послевкусии.<br></p>', 'Подавать при температуре 10 - 12 С . <br>', 'vedernikov risling 2015', 49, 56, 10000, 20, 3, 2, 12, 2015, 0.75, 1, 100, 'wines/June2020/ведерников/1_Губернаторское Рислинг белое.jpg', 'vino-vedernikov-risling-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(83, 'Губернаторское Ркацители', 850, 'Вино Винодельня Ведерников Губернаторское Ркацители белое сухое 2015', '', 'Вино ферментировалось в ёмкостях из нержавеющей стали.<br>', 'Хорошо подойдёт к пасте с морепродуктами, зелёным салатам с белым мясом, ракообразным или чёрной треске, мясным блюдам под соусом, роллам и суши.<br>', 'Букет вина сильно интенсивный, комплексный, с яркими ароматами абрикоса, цитрусовых, дыни и минеральными тонами. Во вкусе вино полнотелое, округлое, очень свежее с нотами цитрусов, миндаля, зелёного яблока и лёгкой минеральностью. <br>', 'Подавать при температуре 09 С . <br>', 'vedernikov rcatsiteli 2015', 49, 57, 10000, 20, 3, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/ведерников/2_Губернаторское Ркацители белое.jpg', 'vino-vedernikov-rcatsiteli-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(84, 'Каберне Совиньон Резерв', 2700, 'Вино Альма Валей Каберне Совиньон Резерв красное сухое 2013', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, выдержка в дубовых бочках в течение 12 месяцев. После розлива в бутылки вино отдыхает, в течение не менее чем 3 месяцев.<br>', '<p>Для подачи к блюдам высокой кухни, приготовленным из жареного мяса, домашней птицы и дичи, с использованием тонких соусов, приправ лёгких пряностей.<br></p>', '<p>Тёмно-рубиновый цвет. Яркий сортовой аромат с тонами спелой чёрной смородины, черники, сафьяновой кожи, благородной ванили и лёгких специй, а также присущими для вин степной зоны Западного Крыма оттенками сушеных трав. Вкус насыщенный, плодовый, свежий, с бархатистой терпкостью. Послевкусие запоминается типичным для альминского терруара лёгким оттенком свежемолотого чёрного перца.<br></p>', 'Подавать при температуре 16-18С.<br>', 'alma kabernet-sovignon reserv 2013', 42, 20, 10000, 2, 4, 1, 14, 2013, 0.75, 1, 100, 'wines/June2020/альма/R_CAB_S_13.jpg', 'vino-Alma-valley-kabernet-sovignon-reserv-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(85, 'Мерло Резерв', 3300, 'Вино Альма Валей Мерло Резерв красное сухое 2013', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, выдержка в дубовых бочках в течение 12 месяцев. После розлива в бутылки вино отдыхает, в течение не менее чем 3 месяцев.<br>', '<p>Для подачи к блюдам высокой кухни, приготовленным из красного мяса и дичи.<br></p>', '<p>Исчерна-рубиновый цвет. Тонкий сортовой аромат с тонами фиалки, спелых чёрных ягод, шоколада, сафьяновой кожи и лёгких специй, а также присущими для вин степной зоны Западного Крыма оттенками сушеных трав. Вкус насыщенный, плодовый, свежий, с шелковистыми танинами. В стиле вина сочетаются весомое тело и утонченная элегантность. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 5 и более лет.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma merlot reserv 2013', 42, 31, 10000, 2, 4, 1, 14, 2013, 0.75, 1, 100, 'wines/June2020/альма/R_MERLOT_13.jpg', 'vino-Alma-valley-merlo-reserv-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(86, 'Мерло', 1200, 'Вино Альма Валей Мерло красное сухое 2014', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, частичная выдержка в дубовых бочках .<br>', '<p>Рекомендуется в сочетании с широкой гаммой блюд, приготовленных из красного мяса.<br></p>', '<p>Исчерна-рубиновый цвет. Гармоничный сортовой аромат Мерло (тона спелых чёрных ягод, сафьяновой кожи и лёгких специй), с присущими для вин степной зоны Западного Крыма оттенками сушеных бальзамических трав. Вкус насыщенный, плодовый, свежий, со средней кислотностью и шелковистой танинной структурой. Вино готово к потреблению, но обещает интересное развитие в выдержке в течение 3-5 лет.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma merlot 2014', 42, 31, 20000, 2, 4, 1, 14, 2014, 0.75, 1, 100, 'wines/June2020/альма/SORT_MERLOT_14.jpg', 'vino-alma-merlo-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(87, 'Пино Нуар Резерв', 3000, 'Вино Альма Валей Пино Нуар красное сухое 2013', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, выдержка в дубовых бочках в течение 12 месяцев. После розлива в бутылки вино отдыхает в течение не менее чем 3 месяцев.<br>', '<p>Для подачи к блюдам высокой кухни, приготовленным из жареного или тушеного мяса, домашней птицы и дичи, с использованием тонких соусов.<br></p>', '<p>Рубиновый цвет. Элегантный сортовой аромат с тонами фиалки, спелой вишни, вишнёвого варенья, бальзамических трав, шоколада, сафьяновой кожи и лёгких специй. Вкус насыщенный, плодово-пряный, с бархатистыми танинами. В послевкусии угадывается лёгкая терруарная &quot;перчинка&quot;. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 5 и более лет.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma pinot noir reserv 2013', 42, 47, 10000, 2, 4, 1, 13, 2013, 0.75, 1, 100, 'wines/June2020/альма/R_PN_13.jpg', 'vino-alma-valley-pino-noir-reserv-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(88, 'Темпранильо Резерв', 2900, 'Вино Альма Валей Темпранильо Резерв красное сухое 2013', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, выдержка в дубовых бочках в течение 12 месяцев. После розлива в бутылки вино отдыхает, в течение не менее чем 3 месяца.<br>', '<p>Для подачи к блюдам высокой кухни, приготовленным из мяса, домашней птицы и дичи.<br></p>', '<p>Насыщенный рубиновый цвет. Богатый сортовой аромат с тонами чёрных ягод, ежевики, сливы, бальзамических трав и ванили. Вкус насыщенный, плодовый, с бархатистой танинной текстурой и долгим послевкусием. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 5 и более лет.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma tempranillo reserv 2013', 42, 71, 10000, 2, 4, 1, 13, 2013, 0.75, 1, 100, 'wines/June2020/альма/R_TEMPR_13.jpg', 'vino-alma-tempranillo-reserv-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(89, 'Пино Нуар', 1100, 'Вино Альма Валей Пино Нуар красное сухое 2014', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, частичная выдержка в дубовых бочках.<br>', '<p>Рекомендуется к блюдам, приготовленных из мяса, домашней птицы и дичи.<br></p>', '<p>Рубиновый цвет. Богатый и гармоничный сортовой аромат с тонами фиалки, спелой вишни, вишнёвого варенья, бальзамических трав и сафьяновой кожи. Вкус плодово-пряный, с бархатистыми танинной структурой. Тельность выдаёт южное происхождение этого Пино Нуара, а долгое послевкусие запоминается лёгкой &quot;перчинкой&quot;. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 3-5 лет.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma pinot noir 2014', 42, 47, 10000, 2, 4, 1, 14, 2014, 0.75, 1, 100, 'wines/June2020/альма/SORT_PN_14.jpg', 'vino-alma-pinot-noir-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(90, 'Шираз Резерв', 2900, 'Вино Альма Валей Шираз Резерв красное сухое 2013', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, выдержка в дубовых бочках в течение 12 месяцев. После розлива в бутылки вино отдыхает, в течение не менее чем 3 месяцев.<br>', '<p>Для подачи к блюдам высокой кухни, приготовленным из жареного мяса, домашней птицы и дичи, с использованием тонких соусов, приправ и лёгких пряностей.<br></p>', '<p>Тёмно-рубиновый цвет. Богатый и гармоничный сортовой аромат с тонами фиалки, чёрной смородины, бальзамических трав, шоколада, сафьяновой кожи, лёгких специй и нежным сливочным оттенком. Вкус насыщенный, плодово-пряный, с освежающей кислотностью и бархатистыми танинами. Долгое послевкусие с лёгкой терруарной &quot;перчинкой&quot;. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 5 и более лет.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma shiraz reserv 2013', 42, 82, 10000, 2, 4, 1, 13, 2013, 0.75, 1, 100, 'wines/June2020/альма/R_SH_13.jpg', 'vino-Alma-Valley-shiraz-reserv-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(91, 'Шираз', 1100, 'Вино Альма Валей Шираз красное сухое 2014', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, частичная выдержка в дубовых бочках.<br>', '<p>Для подачи к блюдам, приготовленным из красного мяса, из мяса утки и дичи.<br></p>', '<p>Интенсивный тёмно-рубиновый цвет. Богатый и гармоничный сортовой аромат с тонами фиалки, чёрной смородины, бальзамических трав, сафьяновой кожи, лёгких специй. Вкус насыщенный, плодово-пряный, с освежающей кислотностью и бархатистой танинной текстурой. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 3-5 лет.<br></p>', 'Подавать при температуре 16-18С.<br>', 'alma shiraz 2014', 42, 82, 10000, 2, 4, 1, 13.5, 2014, 0.75, 1, 100, 'wines/June2020/альма/SORT_SH_14.jpg', 'vino-alma-shiraz-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(92, 'Темпранильо', 1100, 'Вино Альма Валей Темпранильо красное сухое 2014', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, частичная выдержка в дубовых бочках.<br>', '<p>Для подачи к блюдам высокой кухни, приготовленным из жареного и тушеного мяса, домашней птицы и дичи.<br></p>', '<p>Насыщенный рубиновый цвет. Богатый сортовой аромат с тонами чёрных ягод, ежевики, сливы, бальзамических трав и ванили. Вкус насыщенный, плодовый, с бархатистой танинной текстурой и долгим послевкусием. Вино готово к потреблению, но интересно для дальнейшей выдержки в течение 3-5 лет.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma tempranillo 2014', 42, 71, 20000, 2, 4, 1, 13, 2014, 0.75, 1, 100, 'wines/June2020/альма/SORT_TEMPR_14.jpg', 'vino-alma-tempranillo-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(93, 'Пино Блан Резерв', 3300, 'Вино Альма Валей Пино Блан Резерв белое сухое 2014', '', 'Частичное \"холодное\" брожение в бродильных чанах из нержавеющей стали. Частичная ферментация в дубовых бочках и последующая выдержка на дрожжевом осадке. После розлива в бутылки вино отдыхает, в течение не менее чем 3 месяца.<br>', '<p>Для подачи к блюдам из рыбы, морепродуктов и белого мяса.<br></p>', '<p>Золотистый цвет с красивым блеском. Насыщенный аромат с тонами спелой алычи, семечковых и тропических плодов, а также с ванильными и сливочными оттенками. Вкус чистый, плодово-кремовый, с умеренной кислотностью и лёгкой пряностью в послевкусии. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 2-3 лет.<br></p>', 'Подавать при температуре 09-12 С .<br>', 'alma pinot blanc reserv 2013', 42, 43, 10000, 2, 4, 2, 12.5, 2014, 0.75, 1, 100, 'wines/June2020/альма/R_PB_14.jpg', 'vino-Alma-valley-pino-blan-reserv-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(94, 'Пино Блан', 1100, 'Вино Альма Валей Пино Блан белое сухое 2014', '', 'Частичное \"холодное\" брожение в бродильных чанах из нержавеющей стали.<br>', '<p>Рекомендуется к холодным закускам и к блюдам из рыбы и морепродуктов.<br></p>', '<p>Бледно-золотистый цвет с красивым блеском. Характерный для сорта Пино Блан фруктовый аромат с нежными оттенками белых цветов. Вкус свежий, гармоничный, с умеренной кислотностью и минеральным послевкусием.</p>', 'Подавать при температуре 09-12 С .<br>', 'alma pinot blanc 2013', 42, 43, 20000, 2, 4, 2, 12.5, 2014, 0.75, 1, 100, 'wines/June2020/альма/SORT_PB_14.jpg', 'vino-alma-pino-blan-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(95, 'Шардоне Резерв', 2700, 'Вино Альма Валей Шардоне Резерв белое сухое 2014', '', 'Частичное \"холодное\" брожение в бродильных чанах из нержавеющей стали. Частичная ферментация в дубовых бочках и последующая выдержка на дрожжевом осадке. После розлива в бутылки вино отдыхает, в течение не менее чем 3 месяца.<br>', '<p>Сопровождение для широкой гаммы блюд из рыбы, морепродуктов и белого мяса.<br></p>', '<p>Золотистый цвет с красивым блеском. Насыщенный аромат с тонами жёлтых яблок, цитрусовых и других тропических плодов, а также с ванильными и сливочными оттенками. Вкус чистый, плодово-кремовый, с умеренной кислотностью и лёгкой пряностью в послевкусии. Вино готово к потреблению, но рекомендуется для дальнейшей выдержки в течение 2-3 лет.<br></p>', 'Подавать при температуре 09-12 С .<br>', 'alma chardonnay reserv 2014', 42, 81, 10000, 2, 4, 2, 13, 2014, 0.75, 1, 100, 'wines/June2020/альма/R_CH_14.jpg', 'vino-Alma-Valley-chardonnay-reserv-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(97, 'Шардоне', 1100, 'Вино Альма Валей Шардоне белое сухое 2014', '', '\"Холодное\" брожение в бродильных чанах из нержавеющей стали.<br>', '<p>Сухой аперитив и сопровождение для широкой гаммы блюд из рыбы, морепродуктов и белого мяса.<br></p>', '<p>Светло-золотистый цвет с зеленоватым оттенком. Свежий аромат белых семечковых плодов (зелёных и жёлтых яблок). Вкус лёгкий, чистый, плодовый, с умеренной кислотностью.<br></p>', 'Подавать при температуре 09 - 12 С .<br>', 'alma chardonnay 2014', 42, 81, 20000, 2, 4, 2, 12.5, 2014, 0.75, 1, 100, 'wines/June2020/альма/SORT_CH_14.jpg', 'vino-alma-valley-chardone-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(99, 'Совиньон Блан', 1150, 'Вино Альма Валей Совиньон Блан белое сухое 2016', '', 'Была использована технология холодного брожения. Ферментация происходила при контролируемой температуре в ёмкостях из нержавеющей стали. После этого вино выдерживалось на тонком дрожжевом осадке, в течение 4 месяцев.<br>', '<p>Подходит к холодным закускам, морской рыбе, устрицам и другим морепродуктам. Кроме того, умеренная кислотность делает это свежее вино замечательным аперитивом.<br></p>', '<p>Вино имеет светло-золотистый цвет, характерный для сорта Совиньон Блан, свежий аромат с оттенками полевых цветов и лугового разнотравья, а также гармоничный, освежающий плодовый вкус с умеренной кислотностью.</p>', 'Подавать при температуре 09-12 С .<br>', 'alma sauvignon blan 2016', 42, 68, 20000, 2, 4, 2, 12.5, 2016, 0.75, 1, 100, 'wines/June2020/альма/SORT_SAUV_Bl_16.jpg', 'vino-Alma-Valley-sovignon-blan-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 25),
(100, 'Рислинг', 1150, 'Вино Альма Валей Рислинг белое полусухое 2016', '', '<p>Винификация. \"Холодное\" брожение в бродильных чанах из нержавеющей стали и остановка ферментации холодом. Остаточный природный сахар 15 г/л</p>', '<p>Прекрасный полусладкий аперитив и сопровождение для широкой гаммы блюд из рыбы, морепродуктов.<br></p>', '<p>Светло-золотистый цвет. Свежий сортовой аромат с доминирующими цитрусовыми тонами и оттенками тропических плодов. Вкус лёгкий, плодовый, с умеренной кислотностью, приятной природной сладостью и минеральной нотой в послевкусии.<br></p>', 'Подавать при температуре 09-12 С .<br>', 'alma riesling 2016', 42, 56, 20000, 2, 4, 2, 12, 2016, 0.75, 9, 89, 'wines/June2020/альма/SORT_RIESLING_16.jpg', 'vino-alma-riesling-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(101, 'Летнее вино', 750, 'Вино Альма Валей Саммер Вайн розовое полусухое 2016', '', '<p>Короткое настаивание на мезге при контролируемой температуре в современных бродильных чанах из нержавеющей стали. Остаточный природный сахар 10 г/л.<br></p>', 'Идеальный летний аперитив, вино для пляжа или летней террасы.<br>', '<p>Нарядный розовый цвет с красивым блеском. Яркий и гармоничный аромат с тонами спелых красных ягод (земляники, малины и красной смородины). Вкус насыщенный, ягодный, свежий, полусухой с приятной природной сладостью. Долгое ягодное послевкусие.<br></p>', 'Подавать при температуре 08-10 С .<br>', 'alma summer wine 2016', 42, 82, 20000, 2, 4, 3, 13, 2016, 0.75, 9, 99, 'wines/June2020/альма/S_SUMMER.jpg', 'vino-alma-letnee-vino-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(102, 'Ренессанс', 1200, 'Вино Раевское Ренессанс красное сухое 2015', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, выдержка в бочках из кавказского дуба 10 месяцев.<br>', '<p>Идеально подходит к стейкам из красного мяса различной степени прожарки.<br></p>', '<p>Благородный тёмно-вишнёвый цвет. Роскошный букет из спелой сливы и ежевики с оттенками чёрной смородины и шоколада. Послевкусие мягкое, долгое.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'Raevskoe Renessans 2015', 5, 3, 40000, 47, 2, 1, 14, 2015, 0.75, 1, 99, 'wines/June2020/105455.jpg', 'vino-Raevskoe-Renessans-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 8),
(103, 'Зимнее вино (Шираз)', 1000, 'Вино Альма Валей Винтер Вайн красное полусладкое 2017', '', '<p>Брожение при контролируемой температуре в современных бродильных чанах из нержавеющей стали. Остановка брожения холодом. Остаточный природный сахар 35 г/л.<br></p>', 'Немного охлажденное, это вино послужит прекрасным полусладким аперитивом, а также удачным сопровождением для широкой гаммы сыров, шоколадных, фруктовых и прочих десертов.<br>', '<p>Красивый тёмно-гранатовый цвет с фиолетовым оттенком, свежий аромат спелых чёрных ягод, выразительные цветочные тона (фиалка) и гармоничный фруктовый вкус с умеренной кислотностью, приятной природной сладостью и бархатистыми танинами.<br></p>', 'Подавать при температуре 13 - 15 С .<br>', 'alma winter wine 2017', 42, 82, 20000, 2, 4, 1, 12, 2017, 0.75, 7, 99, 'wines/June2020/альма/S_WINTER.jpg', 'vino-alma-valley-zimnee-wino-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(104, 'Весеннее вино', 700, 'Вино Альма Валей Спринг Вайн белое полусладкое 2016', '', '<p>Брожение при контролируемой температуре в современных бродильных чанах из нержавеющей стали. Остановка брожения холодом. Остаточный природный сахар 40 г/л.<br></p>', 'Прекрасный полусладкий аперитив и удачное сопровождение для рыбных блюд, морепродуктов, сыров и лёгких закусок.<br>', '<p>Красивый светло-соломенный цвет с золотистым и зеленоватым оттенками. Выразительный, свежий аромат с тонами распускающихся тополиных почек, весенних цветов и раскрывающихся бутонов розы. В мягком и гармоничном фруктовом вкусе приятная природная сладость, сбалансированная умеренной кислотностью.<br></p>', 'Подавать при температуре 09-12 С .<br>', 'alma spring wine 2016', 42, 81, 20000, 2, 4, 2, 10, 2016, 0.75, 7, 90, 'wines/June2020/альма/S_SPRING.jpg', 'vino-alma-vesennee-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(105, 'Осеннее', 800, 'Альма Валей Осеннее 2016', '', '<p>Брожение при контролируемой температуре в современных бродильных чанах из нержавеющей стали. Остаточный природный сахар 55 г/л.<br></p>', 'Вино послужит прекрасным аперитивом, а также оригинальным сопровождением к морепродуктам, сырам и холодным закускам.<br>', '<p>Красивый светло-соломенный цвет с золотистым оттенком. Выразительный аромат с тонами розы и спелых тропических фруктов. Гармоничный, фруктовый вкус с лёгкой освежающей кислинкой.</p>', 'Подавать при температуре 09-12 С .<br>', 'alma autumn wine 2016', 42, 55, 20000, 2, 4, 2, 10.5, 2016, 0.75, 10, 100, 'wines/June2020/альма/S_AUTUMN.jpg', 'vino-Alma-valley-osenee-wine-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(106, 'Picnic (Каберне-Шираз)', 550, 'Вино Альма Валей Пикник Вайн красное сухое 2016', '', '<p>Винификация - брожение при контролируемой температуре в современных бродильных чанах из нержавеющей стали. Частичная выдержка в дубовых бочках.<br></p>', 'Вино создано специально для блюд, приготовленных на гриле и барбекю из мяса домашней птицы.<br>', '<p>Тёмно-гранатовый цвет. Яркий и гармоничный аромат с тонами спелых чёрных ягод и бальзамических трав. Вкус насыщенный, плодовый, свежий, с бархатистой терпкостью и долгим послевкусием.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'alma valley picnic wine 2016', 42, 20, 30000, 2, 4, 1, 14, 2016, 0.75, 1, 100, 'wines/June2020/альма/S_PICNIC.jpg', 'vino-alma-valley-picnic-wino-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(107, 'Красное (Каберне-Шираз)', 550, 'Вино Альма Валей Красное красное сухое 2016', '', '<p>Винификация. Брожение при контролируемой температуре в современных бродильных чанах из нержавеющей стали. Частичная выдержка в дубе.<br></p>', 'Сопровождение для мясных блюд и холодных закусок.<br>', '<p>Тёмно-гранатовый цвет. Приятный аромат с гармоничным сочетанием цветочных, ягодных и пряных оттенков. Вкус насыщенный, плодовый, с мягкой терпкостью.<br></p>', 'Подавать при температуре 16 - 18 С .<br>', 'alma red 2016', 42, 20, 50000, 2, 4, 1, 13.5, 2016, 0.75, 1, 98, 'wines/June2020/альма/BASIC_RED.jpg', 'vino-alma-valley-krasnoe-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(109, 'Белое (Рислинг-Шардоне)', 550, 'Вино Альма Валей Белое белое сухое 2016', '', '<p>\"Холодное \" брожение в современных бродильных чанах из нержавеющей стали.<br></p>', 'Прекрасный сухой аперитив и сопровождение для рыбных блюд, морепродуктов и холодных закусок.<br>', '<p>Светло-золотистый цвет. Свежий аромат белых семечковых плодов (яблок и груш). Вкус чистый, плодовый и свежий, с умеренной кислотностью и тонким минеральным окончанием.<br></p>', 'Подавать при температуре 09 - 12 С .<br>', 'alma white 2016', 42, 56, 50000, 2, 4, 2, 12, 2016, 0.75, 1, 100, 'wines/June2020/альма/BASIC_WHITE.jpg', 'vino-alma-valley-beloe-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(112, 'Виктор Дравеньи полусладкое', 900, 'Вино игристое Абрау-Дюрсо Виктор Дравеньи полусладкое белое 2012', '', '<p>Это редкая версия игристого, созданного по классической технологии, но с добавлением большего объёма сахара в экспедиционный ликёр.</p>', 'Идеальный спутник девичников, весёлых вечеринок и летних пикников с друзьями. Отлично подойдёт в качестве сопровождения к фруктам, десертам, брускетте с фруктовым конфитюром, выпечке с фруктами и ягодами. <br>', '<p>Вино обладает соломенным оттенком и элегантным перляжем. В аромате доминируют фруктовые тона: персики, абрикосы, тропические фрукты, личи, гуава. Вкус яркий и насыщенный, с прекрасно сбалансированной сладостью.</p>', 'Подавать при температуре 08-10 С.<br>', 'Abrau-Durso Victor Dravigny Semisweet white 2012', 6, 56, 25800, 6, 2, 2, 12, 2012, 0.75, 8, 98, 'wines/June2020/абрау/Victor_Dravigny_semi-sweet-2016.png', 'vino-abrau-durso-Victor-Dravigny-polusladkoe-beloe-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(113, 'Мадера Кубанская', 1000, 'Кубань-Вино мадера кубанская десертное белое 2007', '', '<p>Приготовлено по классической технологии для белых вин. Выдержка в дубовых бочках в течение 12 месяцев под солнцем, далее в нейтральной ёмкости или дубе. Сахар 40 г/дм.</p>', 'Идеально сочетается с сырами, орехами и сухофруктами, маскарпоне.<br>', '<p>Цвет от золотистого до тёмно-янтарного. Аромат свойственный типу вина, с лёгкими тонами калёного орешка и хлебной корочки. Вкус мягкий, маслянистый с приятным и долгим послевкусием.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Kuban-vino madera 2007', 48, 57, 10000, 41, 2, 2, 19, 2007, 0.75, 4, 100, 'wines/June2020/ШТР Мадера Кубанская 2007 0,75л.jpg', 'vino-kuban-vino-madera-2007', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(118, 'Мускат Оттонель', 2300, 'Вино Усадьба Дивноморское Мускат Оттонель белое сладкое 2014', '', '<p>Ручной сбор винограда, использование сусла-самотека, осветление сусла в течение 24 - 28 часов. Брожение при температуре 14 - 16 С, выдержка на тонком дрожжевом осадке с перемешиванием в течение 3 - 6 месяцев. Далее выдержка в стали.<br></p>', '<p>Хороший аперитив, великолепно подходит к белой рыбе и свинине, к пряным блюдам тайской и китайской кухонь, хорошо аккомпанирует фрукты, морепродукты, сливочные сыры.<br></p>', '<p>Отличается яркой цитрусовой ароматикой с миндальными нотками. Освежающий, с легкой сбалансированной кислотностью вкус. Яркий, мощный, типичный мускатный аромат притягивает тонами цитрусовых (мандарин, лимон), белого шоколада, фиников, сухого инжира, кураги, цукатов.<br></p>', '<p>Подавать при температуре 10 - 12 С.<br></p>', 'Divnomorskoe Muscat Ottonel 2014', 51, 36, 2000, 53, 2, 2, 14, 2014, 0.75, 10, 100, 'wines/June2020/Muskat Ottonel.png', 'vino-divnomorskoe-muscat-ottonel-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 25),
(120, 'Шардоне', 560, 'Вино Гай-Кодзор Шардоне белое сухое 2015', '', '<p>Ручной сбор винограда. Перед ферментацией проводится дебурбаж (отстаивание сусла) в течение 48 часов. Затем вино выдерживается в терморегулируемых ёмкостях из нержавеющей стали в течение 7 месяцев.<br></p>', '<p>Вино станет гастрономическим сопровождением к блюдам из морской и речной рыбы. Благодаря своей свежести прекрасно сочетается с морепродуктами.</p>', '<p>Вино светло-соломенного цвета. Аромат свежий, сочный, с тонами айвы и полевых цветов. Вкус лёгкий, чистый, с приятной свежей кислотностью, оттенками зелёных яблок и цитрусовых.<br></p>', '<p>Подавать при температуре 10 - 12 С.<br></p>', 'Gai-Kodzor Chardonnay 2015', 55, 81, 20000, 30, 2, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/гай кодзор/9903_Gai-Kodzor Chardonnay.jpg', 'vino-Gai-Kodzor-Chardonnay-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(122, 'Виктор Дравеньи розовое', 900, 'Вино игристое Абрау-Дюрсо розовый брют Виктор Дравеньи 2013', '', 'Розовый брют из коллекции Victor Dravigny, созданный из ассамбляжа классических шампанских сортов, по классической технологии выдержки в бутылке на дрожжевом осадке.<br>', 'Его можно подавать практически к любым блюдам - от холодных закусок до десертов со свежими красными ягодами. Рекомендуем попробовать его с утиной грудкой магре, красной икрой, тартаром из лосося или тунца, карпаччо или холодным ростбифом, телятиной под белым соусом, с пармезаном и свежей рукколой.<br>', 'Вино имеет редкий по красоте &quot;лососевый&quot; оттенок с коралловым отблеском, устойчивым муссом и элегантным перляжем. В свежем аромате доминируют ноты лесных ягод (красная смородина, земляника) и красных апельсинов. Вкус насыщенный, освежающий и бодрящий, с преобладанием цитрусовых и смородиновых тонов в первой волне, благородным минеральным продолжением и долгим ароматным послевкусием.<br>', 'Подавать при температуре 08 - 10 С .<br>', 'Abrau Victor Dravigny rose brut 2013', 6, 49, 10000, 6, 2, 3, 12, 2013, 0.75, 2, 100, 'wines/June2020/абрау/VD_Rose_0,75_2016.png', 'vino-Abrau-Victor-Dravigny-rose-brut-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(123, 'Айсвайн Совиньон Резерв', 3500, 'Ледяное вино Совиньон Блан Альма Валлей белое сладкое 2014', '', '<p>Производится из подвергшегося естественному замораживанию на кусте винограда, который собирают поздней осенью или зимой., когда температура воздуха опускается ниже - 8С. Остаточный природный сахар 375 г/л.<br></p>', 'Прекрасный сладкий аперитив и сопровождение для широкой гаммы десертов.<br>', '<p>Насыщенный золотистый цвет. Характерный для вин позднего сбора, яркий аромат с доминирующими медовыми оттенками. Гармоничный плодовый вкус, с умеренной кислотностью, природной тропической сладостью и минеральной нотой в долгом послевкусии.</p>', 'Подавать при температуре 09-12 С .<br>', 'alma ice wine sauvignon reserve 2014', 42, 68, 10000, 2, 4, 2, 7, 2014, 0.375, 10, 100, 'wines/June2020/альма/IW_SAUV_14.jpg', 'vino-alma-valley-isewine-sovinon-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2);
INSERT INTO `wines` (`id`, `title`, `price`, `meta_title`, `description`, `production_feature`, `combination`, `feature`, `innings`, `model`, `winery_id`, `grape_sort_id`, `edition`, `manufacturer_id`, `region_id`, `color_id`, `fortress`, `year`, `volume`, `sugar_id`, `count`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`, `excerpt_id`) VALUES
(125, 'Айсвайн Рислинг Резерв', 3500, 'Ледяное вино Рислинг Резерв Альма Валлей белое сладкое 2015', '', '<p>Производится из подвергшегося естественному замораживанию на кусте винограда, который собирают поздней осенью или зимой, когда температура воздуха опускается ниже - 8С. Остаточный природный сахар 365 г/л.<br></p>', 'Прекрасный сладкий аперитив и сопровождение для широкой гаммы десертов.<br>', '<p>Насыщенный золотистый цвет. Характерный для вин позднего сбора, яркий цветочно-фруктовый аромат с доминирующими оттенками лимона и грейпфрута, тонами цветочной пыльцы и мёда. Гармоничный сладко-плодовый вкус с выразительной, освежающей кислотностью и минеральной нотой в долгом послевкусии.</p>', 'Подавать при температуре 09-12 С .<br>', 'alma ice wine risling reserve 2015', 42, 56, 10000, 2, 4, 2, 6, 2015, 0.375, 10, 100, 'wines/June2020/альма/IW_R_15.jpg', 'vino-alma-valley-isewine-risling-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(126, 'Айсвайн Петит Арвин Резерв', 3500, 'Ледяное вино Петит Арвин Резерв Альма Валлей белое сладкое 2015', '', '<p>Производится из подвергшегося естественному замораживанию на кусте винограда, который собирают поздней осенью или зимой, когда температура воздуха опускается ниже - 8 С. Остаточный природный сахар 365 г/л.<br></p>', 'Прекрасный сладкий аперитив и сопровождение для широкой гаммы десертов.<br>', '<p>Насыщенный золотистый цвет. Характерный для вин позднего сбора, яркий цветочно-фруктовый аромат с доминирующими оттенками ананаса и тропических плодов, медовыми тонами. Гармоничный сладко-плодовый вкус с освежающей кислотностью и типичной для данного сорта солоноватой минеральной нотой в долгом послевкусии.</p>', 'Подавать при температуре 09-12 С .<br>', 'Ice Wine Petite Arvine Reserve 2015', 42, 42, 10000, 2, 4, 2, 6, 2015, 0.375, 10, 48, 'wines/June2020/альма/IW_PA_15.jpg', 'vino-alma-valley-isewine-petit-arvin-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(127, 'Красностоп', 1200, 'Вино Раевское Красностоп крансое сухое 2015', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из дуба и нержавеющей стали, выдержка в бочках из кавказского дуба 10 месяцев.<br>', '<p>Рекомендуется в сочетании с мясными блюдами кавказское кухни, бараниной на гриле со специями, твёрдыми выдержанными сырами.<br></p>', '<p>Тёмно-вишнёвый цвет. Ягодный букет из красной смородины, малины и ежевики. Полнотелое, с яркими танинами и законченной структурой. Устойчивое приятное послевкусие.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'Raevskoe Krasnostop 2015', 5, 25, 10000, 47, 2, 1, 14, 2015, 0.75, 1, 100, 'wines/June2020/105455.jpg', 'vino-Raevskoe-Krasnostop-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 8),
(128, 'Генезис', 2800, 'Вино Раевское Генезис красное сухое 2015', '', 'Сбор урожая проводится вручную, на хозяйстве ягоды подвергаются тщательному отбору. Ферментация каждого сорта винограда проходит отдельно при контролируемой температуре. Выдержка в бочках из кавказского дуба 11 месяцев.<br>', 'Вино рекомендуют подавать к сложным мясным блюдам, шашлыку с острыми и пряными кавказскими соусами, сырам средней выдержки.<br>', '<p>Насыщенный тёмно-вишнёвый цвет, с глянцевым блеском. В богатом сочном аромате доминируют ноты чёрных лесных ягод, чёрной смородины, вишнёвого джема. Яркие ягодные тона сменяются оттенками ванили, лакрицы, пряностей. Плотное и спелое во вкусе с пока ощутимыми, но прекрасно интегрированными танинами. В длительном по послевкусии приятная горчинка, специи и оттенки чёрноплодной рябины.<br></p>', 'Подавать при температуре 16 - 18 С .<br>', 'Raevskoe Genezis 2014', 5, 20, 2000, 47, 2, 1, 13, 2014, 0.75, 1, 98, 'wines/June2020/раевское/100191-Genesis-2014-Front.png', 'vino-raevskoe-genezis-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 9),
(129, 'Каберне Фран', 1100, 'Вино Раевское Каберне Фран красное сухое 2015', '', 'Ферментация при контролируемой температуре в современных бродильных ёмкостях из нержавеющей стали, выдержка в бочках из кавказского дуба 10 месяцев.<br>', '<p>Вино можно подавать к мясным закускам, мясу кролика и домашней птицы, грибным блюдам и молодым сырам.<br></p>', '<p>Тёмно-вишнёвый цвет. Богатый вкус раскрывается оттенками чёрной смородины и ванили. Сбалансированная кислотность поддержана долгим и приятным послевкусием.<br></p>', 'Подавать при температуре 16-18 С .<br>', 'Raevskoe Cabernet Franc 2015', 5, 21, 10000, 47, 2, 1, 13, 2015, 0.75, 1, 100, 'wines/June2020/раевское/103049.jpg', 'vino-Raevskoe-Caberne-Fran-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 8),
(130, 'Пино Нуар', 1000, 'Вино Раевское Пино Нуар красное сухое 2015', '', 'Традиционная ферментация в нейтральных ёмкостях с температурным контролем.<br>', '<p>Холодные мясные закуски, тар-тар или карпачо из говядины, говяжья вырезка или тунец на гриле, утиная грудка средней прожарки под ягодным соусом.<br></p>', '<p>Благородный рубиновый цвет с фиолетовым блеском. В свежем ягодном аромате раскрываются тона вишни, малины, сливы и клубники. Яркое и фруктовое во вкусе с мягкими, прекрасно интегрированными танинами.</p>', 'Подавать при температуре 16-18 С .<br>', 'Raevskoe Pinot Noir 2015', 5, 47, 10000, 47, 2, 1, 13, 2015, 0.75, 1, 100, 'wines/June2020/раевское/109968.jpg', 'vino-Raevskoe-Pino-Noir-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(131, 'Ренессанс белое', 1200, 'Раевское Ренессанс белое полусухое 2014', '', 'Полностью ручной сбор винограда. Раздельная ферментация при контролируемой температуре в барриках.<br>', 'Отлично сочетается с мясными блюдами, яркими соусами, барбекю, а также с выдержанными сырами.<br>', '<p>Вино золотисто-янтарного цвета. Насыщенный и яркий аромат с нотами спелых абрикосов, жёлтых груш, айвы, дыни и белой садовой земляники. Тело вина плотное, маслянистое с нотами сладеого ананаса и белого изюма. Высокая кислотность сбалансирована медовыми нотами. Вино обладает длительным послевкусием с нотами цитрусовых.<br></p>', 'Подавать при температуре 10-12 С .<br>', 'Raevskoe Renessans white 2014', 5, 56, 20000, 47, 2, 2, 13, 2014, 0.75, 9, 100, 'wines/June2020/раевское/103046.jpg', 'vino-Raevskoe-Renessans-beloe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(132, 'Шардоне', 1000, 'Вино Раевское Шардоне белое сухое 2015', '', 'Ручной сбор в 12-килограммовые ящики. Долгое брожение, около четырёх недель, в ёмкостях из нержавеющей стали при температуре 12 - 17С.<br>', '<p>Идеальный аперитив, сочетается с блюдами из рыбы, птицы, морепродуктов и твёрдыми сырами.<br></p>', '<p>В аромате ноты дюшеса и барбариса, с оттенками жимолости и тропических фруктов, освежающими нотами цитрусовых. Округлое и бархатистое во вкусе, с хорошим балансом и приятной кислотностью, оттенками персиков, цитрусовыми нюансами.</p>', 'Подавать при температуре 10-12 С .<br>', 'Raevskoe Chardonnay 2015', 5, 81, 15000, 47, 2, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/раевское/103045.jpg', 'vino-Raevskoe-Chardonnay-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(133, 'Вионье', 950, 'Вино Раевское Шардоне белое сухое 2015', '', 'Ручной сбор в 12-килограммовые ящики. Долгое брожение, около четырёх недель, в ёмкостях из нержавеющей стали при температуре 12 - 17С.<br>', '<p>Идеальный аперитив, сочетается с блюдами из рыбы, птицы, морепродуктов, а также к мягким сырам с белой плесенью.<br></p>', 'Цвет нежно-золотистый, с зеленоватым оттенком. Вкус насыщенный, с тонами спелых фруктов и манго. Приятная освежающая кислинка в послевкусии.<br>', 'Подавать при температуре 10-12 С .<br>', 'Raevskoe Viogner 2015', 5, 12, 15000, 47, 2, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/раевское/103053.jpg', 'vino-Raevskoe-Viogner-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(136, 'Виктор Дравеньи экстра брют', 900, 'Вино игристое Абрау-Дюрсо Виктор Дравеньи белое экстра брют 2012', '', 'Самая элегантная версия серии Victor Dravigny - \"экстра брют\", с минимальным содержанием сахара, созданная в соотвествии с последними мировыми тенденциями, по классической технологии выдержки в бутылке на дрожжевом осадке.<br>', 'Это великолепный аперитив. Хорошо подходит к морепродуктам: устрицам, мидиям, крабам, сашими из белой рыбы, тартару из гребешков. Отличное сопровождение к лёгким закускам и горячим блюдам из белой рыбы.<br>', 'Вино красивого светло-соломенного оттенка с живым блеском, устойчивым ажурным муссом и завораживающей длительной игрой пузырьков в бокале. Аромат насыщенный, со сложным сочетанием минеральных нот и цитрусовых, в котором различаются нюансы абрикосов, лимона горького апельсина. Вкус яркий освежающий, с хрустящей кислотностью, оттенками груш, яблок и цитрусовых, с таким же ярким и освежающим послевкусием. <br>', 'Подавать при температуре 08 - 10 С .<br>', 'Abrau Victor Dravigny extra brut 2012', 6, 81, 10000, 6, 2, 2, 12, 2012, 0.75, 11, 99, 'wines/June2020/абрау/VD_Extra-Brut_0,75_2016.png', 'vino-Abrau-Victor-Dravigny-extra-brut-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(137, 'Виктор Дравеньи полусухое', 900, 'Абрау-Дюрсо белое полусухое Виктор Дравеньи 2012', '', 'Созданное из ассамбляжа классических шампанских сортов, по технологии выдержки в бутылке на дрожжевом осадке. В этом вине сочетается вкус и аромат классического игристого с приятной сладостью.<br>', 'Послужит гастрономическим сопровождением к блюдам с плотной текстурой и сливочными соусами (паста, ризотто), фруктам, выдержанным и мягким сырам.<br>', 'Вино красивого золотистого цвета, с блеском и устойчивой игрой пузырьков. Яркий, насыщенный аромат сочетает тона, характерные для выдержанного игристого вина, с небольшим количеством сахара - оттенками цитрусовых цукатов, яблочного конфитюра и сладкой сдобы.<br>', 'Подавать при температуре 08 - 10 С .<br>', 'Abrau Victor Dravigny semidry 2012', 6, 81, 10000, 6, 2, 2, 12, 2012, 0.75, 9, 100, 'wines/June2020/абрау/Victor_Dravigny_semi-dry-2016.png', 'vino-Abrau-Victor-Dravigny-polusuhoe-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(138, 'Виктор Дравеньи брют', 900, 'Вино игристое Абрау-Дюрсо Виктор Дравеньи белое брют 2012', '', 'Самый известный в серии Victor Dravigny, притягивает взгляд бодрой и устойчивой игрой пузырьков и запоминается красивым насыщенным цветом и ярким блеском. Выполнен по классической технологии из европейских шампанских сортов. <br>', 'Это универсальное игристое вино, которое можно подавать в качестве аперитива перед званым ужином и в течение трапезы. Хорошо сочетается с блюдами из рыбы и белого мяса индейки или курицы.<br>', 'Тонкий и нежный букет с ароматами белых цветов, персиков, абрикосов и груш, которые раскрываются в тона бриоши и сливочного масла. Вкус - преимущественно фруктовый, приятный и полный, с тонкой минеральной горчинкой в послевкусии. <br>', 'Подавать при температуре 08 - 10 С .<br>', 'Abrau Victor Dravigny brut 2012', 6, 81, 10000, 6, 2, 2, 12, 2012, 0.75, 2, 100, 'wines/June2020/абрау/Victor Dravigny_2016.png', 'vino-Abrau-Victor-Dravigny-brut-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(139, 'Виктор Дравеньи красное', 900, 'Вино игристое Абрау-Дюрсо Виктор Дравеньи полусладкое красное 2014', '', '<p>Оригинальное моносортовое красное игристое вино из сорта Каберне-Совиньон, создано по классической технологии и обладает прекрасным балансом сладости и свежести. </p>', 'Станет лучшим сопровождением к голубым сырам, а также десертам из красных ягод и шоколада. Любители сладких аперитивов, например такого классического коктейля, ка Kir-Royal, непременно оценят достоинства этого игристого.<br>', 'Удивительно насыщенный рубиновый цвет, глянцевый блеск, устойчивый мусс, глубокий и в то же время свежий вишнёво-смородиновый аромат, характерный для этого сорта винограда. В бокале букет интересно раскрываетяся тонами клубничного конфитюра и чернослива. Вкус удивляет сочетанием сладости удивительной ягодной свежести, облагорожен выдержкой, бархатными танинами и продолжительным послевкусием.<br>', 'Подавать при температуре 08-10 С.<br>', 'Abrau Victor Dravigny Semisweet red 2014', 6, 20, 25800, 6, 2, 1, 12, 2014, 0.75, 8, 98, 'wines/June2020/абрау/Victor_Dravigny_caberne-2016.png', 'vino-Abrau-Victor-Dravigny-polusladkoe-krasnoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(140, 'Темелион розовое', 2400, 'Шампанское Лефкадия Темелион выдержанное розовое брют 2012', '', '<p>Это лимитированная версия игристого, созданного по классической технологии, с выдержкой в бутылке в течение 24 месяцев.</p>', 'Прекрасно подойдёт в качестве аперитива и идеально сочетается с рыбой и морепродуктами. <br>', '<p>Вино обладает свело-розовым цветом. Тонкий и гармоничный аромат, преобладают нотки красной смородины и земляники. Вкус полный, с преобладанием цитрусовых и плодовых тонов, с долгим минеральным послевкусием.</p>', 'Подавать при температуре 08 - 10С.<br>', 'Lefkadia temelion rose 2012', 7, 81, 3000, 42, 2, 3, 12, 2012, 0.75, 2, 99, 'wines/June2020/balka/лефкадия темелион розе (1).png', 'vino-Lefkadia-temelion-rose-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(141, 'Блан Де Блан', 1200, 'Игристое вино Абрау-Дюрсо Блан Де Блан брют белое 2012', '', 'Элегантное игристое, приготовлено по классической технологии из одного сорта винограда Шардоне. Игристое имеет большой потенциал к выдержке. <br>', 'Прекрасное сопровождением для сложных блюд из морепродуктов, лобстеров, крабов, устриц. Сложное, комплексное игристое вино, которое послужит отличным вариантом для торжественных мероприятий.<br>', 'Золотистый цвет со светлыми соломенным отблеском. Богатый, свежий, цельный букет с оттенками бриоши и сливочного масла сопровождается бодрой игрой мелких пузырьков. В глубине аромата угадываются тона белых цветов и сочных фруктов. Вкус яркий, насыщенный, прекрасного сложения, с плотной, кремовой текстурой, преимущественно фруктовый, со сложной игрой аккордов из цитрусовых и белых фруктов и приятным сбалансированным послевкусием. <br>', 'Подавать при температуре 08 - 10 С .<br>', 'Abrau Brut Blanc De Blancs 2012', 6, 81, 10000, 6, 2, 2, 12, 2012, 0.75, 2, 100, 'wines/June2020/абрау/Ready-Brut-D\'or-1.png', 'vino-Abrau-Brut-Blanc-De-Blancs-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(142, 'Империал брют винтаж', 1900, 'Игристое вино Абрау-Дюрсо Империал брют Винтаж белое 2007', '', 'Коллекционное игристое вино приготовлено классическим методом, с выдержкой в бутылке больше трёх лет. Для вин коллекции Империал используется кюве - сок первого отжима из отборного винограда.  <br>', 'Подойдёт в качестве аперитива перед торжественным ужином. Его также можно подавать к блюдами из белой рыбы (судак, форель, камбала, тюрбо), морским гребешкам, мягким сырам с белой плесенью (камамбер или бри).<br>', 'Игристое светло-золотистого оттенка, с ярким блеском и интенсивным перляжем в тардиционном русском стиле. Глубокий букет и насыщенный аромат выдержанного игристого вина с выразительными тонами корочки ржаного хлеба и яблочного варенья, топлёных сливок, подсолнечных семечек, а также освежающими оттенками цитрусовой цедры. Вкус свежий, бодрый, яблочно-цитрусовый в первой волне и минеральный в послевкусии. <br>', 'Подавать при температуре 08 - 10 С .<br>', 'Abrau-Durso Imperial brut vintage 2007', 6, 81, 10000, 6, 2, 2, 12, 2013, 0.75, 2, 98, 'wines/June2020/abrau/vintage0.75_2013.png', 'vino-Abrau-Durso-Imperial-brut-vintag-2007', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(143, 'Империал брют розе', 1900, 'Игристое вино Абрау-Дюрсо Империал брют Винтаж розовое 2007', '', 'Коллекционное игристое вино приготовлено классическим методом, с выдержкой в бутылке больше трёх лет. Для вин коллекции Империал используется кюве - сок первого отжима из отборного винограда.  <br>', 'Станет отличным сопровождением для фуа-гра, тартара или карпачо из говядины, сашими из тунца. Превосходно сочетается с холодным ростбифом, блюдами из утки (конфи, магре) и выдержанными сырами.<br>', 'Это игристое нежного &quot;лососевого&quot; оттенка с ярким коралловым отблеском и энергичной игрой мелких пузырьков. Тонкий, сдержанный аромат наполнен ягодными оттенками, среди которых наиболее отчётливо угадывается малина в сочетании с лавровым листом. Обладает плотным структурированным вкусом и идеальным балансом кислотности и имеет потенциал к выдержке. Послевкусие продолжительное, минеральное, с выраженными мягкими танинами.<br>', 'Подавать при температуре 08 - 10 С .<br>', 'Abrau Imperial brut rose 2013', 6, 81, 10000, 6, 2, 3, 12, 2013, 0.75, 2, 94, 'wines/June2020/abrau/rose0.75_2013.png', 'vino-Abrau-Imperial-brut-rose-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(145, 'Шардоне', 2300, 'Усадьба Дивноморское Шардоне белое сухое 2012', '', 'Ферментация при температуре 18 - 20С , около 30% вина ферментировано во французском дубе. Вино приготовлено в так называемом \"холодном\" стиле.<br>', '<p>Вино превосходно в качестве аперитива, как сопровождение холодных блюд и закусок из рыбы, морепродуктов.<br></p>', 'Цвет вина светло-соломенный с бледно-лимонным отливом. В аромате вина доминируют цветочные оттенки. Вкус вина яркий, с хорошо выраженной минеральностью на фоне &quot;живой&quot; кислотности.<br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'Divnomorskoe Chardonnay 2012', 51, 81, 12300, 53, 2, 2, 13.7, 2012, 0.75, 1, 98, 'wines/June2020/дивноморское/Шардоне.png', 'vino-Divnomorskoe-Chardonnay-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(146, 'Рислинг', 2300, 'вино Усадьба Дивноморское Рислинг белое сухое 2012', '', 'Низкотемпературная ферментация при температуре 18С в стальных ёмкостях.<br>', '<p>Как сопровождение для блюд из белой рыбы, морепродуктов, идеально для устриц.  <br></p>', 'Цвет вина соломенный с зеленоватым оттенком. Аромат яркий, свежий с выраженной минеральностью, с нюансами сочного абрикоса, белого нектарина и едва уловимыми нотками тропических фруктов. Вкус вина яркий, живой, с гармоничной кислотностью, сбалансированный.<br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'Divnomorskoe Risling 2012', 51, 56, 7300, 53, 2, 2, 12.5, 2012, 0.75, 1, 100, 'wines/June2020/дивноморское/Рислинг.png', 'vino-Divnomorskoe-Risling-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(147, 'Шардоне', 500, 'Вино Солнечная Долина Шардоне белое сухое 2017', '', 'Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке.<br>', 'Рекомендуется к салатам, заливному из осетрины, икре на тёплом тосте, пирогу из палтуса, жареной барабульке, креветкам под кисло-сладким соусом, перепелам со сливой, ризотто с морепродуктами и пасте.<br>', 'Вино бледно-золотистого цвета. В аромате доминируют тона экзотических фруктов: ананаса, банана и манго. Пикантность аромату придаёт оттенок лайма. Вкус полный, чуть маслянистый, с хорошей кислотностью. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'S dolina Chardonnay 2015', 44, 81, 23000, 52, 4, 2, 13, 2017, 0.75, 1, 100, 'wines/June2020/сол долина/ШАРДОНЕ.png', 'vino-S-dolina-Chardonnay-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(148, 'Каберне Совиньон', 2300, 'Вино Усадьба Дивноморское Каберне Совиньон красное сухое 2012', '', 'Ферментация проводилась 28 дней, в стальных ёмкостях, выдержка во французском дубе 12 месяцев. <br>', '<p>Идеально к красному мясу, сырам и мясным закускам. <br></p>', 'Цвет вина рубиновый с гранатовыми оттенками. Аромат свежий, тонкий, с приятными нотками мяты, табака, мускатного ореха. Вино радует изящным, элегантным вкусом со свежей кислотностью и &quot;мягкими&quot; танинами. В послевкусии этого вина наблюдается лёгкий &quot;животный&quot; оттенок и табачные нюансы.<br>', '<p>Подавать при температуре 16 -18 С.<br></p>', 'Divnomorskoe Cabernet Sauvignon 2012', 51, 20, 9350, 53, 2, 1, 13.7, 2012, 0.75, 1, 100, 'wines/June2020/дивноморское/КабенеСовиньон.png', 'vino-Divnomorskoe-Cabernet-Sauvignon-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(149, 'Восточный Склон', 2300, 'Вино Усадьба Дивноморское Восточный Склон белое сухое 2012', '', 'Низкотемпературная раздельная ферментация сортов при температуре 18С в стальных ёмкостях.<br>', '<p>Как сопровождение для блюд из белой рыбы, морепродуктов.  <br></p>', 'Жёлто-соломенный цвет с зеленоватым оттенком. Аромат чистый, элегантный, ненавязчивый. Цветущая роза, акация, жасмин, свежие тропические фрукты - маракуйя, среднеземноморские специи. Вкус яркий, сбалансированный, элегантный, обволакивающий.<br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'Divnomorskoe East Hill Blend 2012', 51, 68, 10800, 53, 2, 2, 12.8, 2012, 0.75, 1, 99, 'wines/June2020/дивноморское/East Hill Blend.png', 'vino-Divnomorskoe-vostochnyi-sklon-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(150, 'Мерло', 2300, 'Вино Усадьба Дивноморское Мерло красное сухое 2012', '', 'Брожение с постферментационой мацерацией. Выдержка во французском дубе 18 месяцев. <br>', '<p>Сопровождение к красному мясу, дичи и мягким сырам. <br></p>', 'Цвет вина яркий, интенсивный, рубиновый с фиолетовыми оттенками. Сложный аромат с выраженными нюансами фруктов, эвкалипта, фиалки, красного перца, ванили. Сопровождается округлым, элегантным, лёгким, бархатистым и гармоничным вкусом.<br>', '<p>Подавать при температуре 16 -18 С.<br></p>', 'Divnomorskoe Merlot 2012', 51, 31, 10800, 53, 2, 1, 13.5, 2012, 0.75, 1, 99, 'wines/June2020/дивноморское/Мерло.png', 'vino-Divnomorskoe-merlo-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(151, 'Пино Блан', 2300, 'Вино Усадьба Дивноморское Пино Блан белое сухое 2012', '', 'Небольшая часть вина ферментирована и выдержана в барриках из французского дуба.<br>', '<p>Вино превосходно в качестве аперитива, как сопровождение холодных блюд и закусок из рыбы, морепродуктов.  <br></p>', 'Цвет вина светло-соломенный, живой, с лёгким зеленоватым оттенком. Аромат яркий, свежий с преобладанием нюансов белых цветов и фруктов: яблока, груши. Также в аромате прослеживаются элегантные нотки тропических фруктов. Вкус вина яркий, сбалансированный с фруктовой доминантой и выраженной минеральностью. Послевкусие лёгкое, приятное.<br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'Divnomorskoe Pinot Blan 2012', 51, 43, 7800, 53, 2, 2, 12.9, 2012, 0.75, 1, 99, 'wines/June2020/дивноморское/ПиноБлан (1).png', 'vino-Divnomorskoe-Pino-Blan-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(152, 'Пино Нуар', 2300, 'Вино Усадьба Дивноморское Каберне Совиньон красное сухое 2012', '', '36 месяцев холодной мацерации сусла на мезге. Низкотемпературное брожение. Выдержка 12 месяцев в бургундской бочке из французского дуба. <br>', '<p>Превосходно к мясу утки, твёрдым сырам и мясным закускам. <br></p>', 'Вино красного цвета с розовым отливом. Аромат свежий, узнаваемый, характерный для данного сорта. Ощущаются нотки шиповника, черешни, вишни, белого шоколада. Вкус гармоничный, элегантный с преобладанием оттенка мела. Кислотность свежая, идеально сбалансированная, танины &quot;мягкие&quot; и шелковистые. <br>', '<p>Подавать при температуре 14 -16 С.<br></p>', 'Divnomorskoe Pinot Noir 2012', 51, 47, 6550, 53, 2, 1, 13.2, 2012, 0.75, 1, 100, 'wines/June2020/дивноморское/ПиноНуар.png', 'vino-Divnomorskoe-Pino-Noir-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(153, 'Западный Склон', 2300, 'Вино Усадьба Дивноморское Западный Склон красное сухое 2012', '', 'Раздельная ферментация и выдержка во французском дубе 18 месяцев. После купажирования, дополнительная выдержка в буте 2500 л. <br>', '<p>Идеально к красному мясу, сырам и мясным закускам. <br></p>', 'Вино рубинового цвета. Аромат свежий, фруктовый. Преобладают нюансы мяты, табака, чёрного шоколада, чернослива. Вкус вина свежий, минеральный с выраженной, но сбалансированной кислотностью, крепкой структурой и &quot;мягкими&quot;, шелковистыми танинами. В послевкусии преобладают пряные нотки, выражены нюансы шоколада, табака, белого перца.<br>', '<p>Подавать при температуре 16 -18 С.<br></p>', 'Divnomorskoe West Hill Blend 2012', 51, 20, 17850, 53, 2, 1, 13.5, 2012, 0.75, 1, 100, 'wines/June2020/дивноморское/West Hill Blend.png', 'vino-Divnomorskoe-zapadnyi-sklon-Blen-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(154, 'Совиньон Блан', 2300, 'Вино Усадьба Дивноморское Совиньон Блан белое сухое 2012', '', 'Ферментация в стали при контролируемой температуре. Выдержка на тонком осадке. Батонаж. Вино приготовлено в Европейском стиле.<br>', '<p>Вино превосходно в качестве аперитива, как сопровождение холодных блюд и закусок из рыбы, морепродуктов.  <br></p>', 'Цвет вина серебристо-соломенный. Аромат элегантный с выраженными специфическими тонами, присущими данному сорту: лист чёрной смородины, крыжовник, грейпфрут. Вкус вина абсолютно сухой, свежий с яркой &quot;живой&quot; кислотностью и доминантой грейпфрута. Послевкусие повторяет его ароматическте особенности, достаточно долгое, яркое .<br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'Divnomorskoe Sauvignon Blan 2012', 51, 68, 7600, 34, 2, 2, 13.6, 2012, 0.75, 1, 100, 'wines/June2020/дивноморское/СовиньонБлан.png', 'vino-Divnomorskoe-Sauvignon-Blan-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(155, 'Новый Свет Пино Фран', 900, 'Игристое вино Новый Свет Пино Фран розовое брют 2014', '', 'Лучшее моносортовое розовое игристое вино Крыма последних лет, созданного с соблюдением полного цикла классической технологии выдержки в бутылке.<br>', '<p>Отличное сочетание с морепродуктами и сырами.  <br></p>', 'Цвет вина в бокале - это цвет крымского персика. В первом же носе ощутим аромат свежих красных фруктов. Послевкусие насыщенное, с оттенками красной смородины и крыжовника. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet Pino Fran brut 2014', 68, 49, 7800, 45, 4, 3, 12, 2014, 0.75, 2, 99, 'wines/June2020/новый свет/3.1 ВИВ брют розовое НС Пино Фран.png', 'vino-New-Svet-Pino-Fran-brut-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(156, 'Траминер', 2300, 'Вино Усадьба Дивноморское Траминер белое сухое 2012', '', 'Охлаждение, мойка и сушка гроздей винограда перед обработкой. Ферментация в стали при контролируемой температуре.<br>', '<p>Вино превосходно в сопровождении к мясу птицы и морепродуктам.  <br></p>', 'Цвет вина серебристо-соломенный. Аромат яркий сортовой с преобладанием тонов цветущей розы, зрелых тропических фруктов, средиземноморских специй: тимьяна, орегано и розового перца. Вкус вина интенсивный, плотный, округлый, маслянистый, обволакивающий. Послевкусие с преобладанием нюансов мёда, экзотических фруктов.<br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'Divnomorskoe Traminer 2012', 51, 73, 6300, 53, 2, 2, 13.2, 2012, 0.75, 1, 99, 'wines/June2020/дивноморское/Траминер.png', 'vino-Divnomorskoe-Traminer-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(158, 'Сира', 2300, 'Вино Усадьба Дивноморское Сира красное сухое 2012', '', 'Брожение с постферментационой мацерацией. Выдержка во французском дубе 18 месяцев. <br>', '<p>Сопровождение к красному мясу, дичи и твёрдым сырам. <br></p>', 'Цвет вина тёмно-рубиновый. В аромате прослеживаются нюансы кожи, кофе и специй. Вкус сложный, плотный, с крепкой танинной структурой, с преобладанием нюансов сушёного чернослива. Послевкусие длительное, с приятными кофейными нотками.<br>', '<p>Подавать при температуре 16 -18 С.<br></p>', 'Divnomorskoe Syrah 2012', 51, 66, 5870, 53, 2, 1, 14.5, 2012, 0.75, 1, 100, 'wines/June2020/дивноморское/Сира.png', 'vino-Divnomorskoe-Syra-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(159, 'Марселан', 2300, 'Вино Усадьба Дивноморское Марселан красное сухое 2012', '', 'Вино выдержано 24 месяца во французском дубе. <br>', '<p>Сопровождение к стейкам, дичи и твёрдым сырам. <br></p>', 'Цвет вина глубокий красный с чернильными оттенками. В аромате доминируют нюансы лесной фиалки, черники, ежевики, какао. Вкус яркий, интенсивный, округлый, сбалансированный, маслянистый со &quot;сладкими&quot; шёлковыми, мягкими, но ощутимыми танинами. В послевкусии прослеживается приятный оттенок черничного джема.<br>', '<p>Подавать при температуре 16 -18 С.<br></p>', 'Divnomorskoe Marselan 2012', 51, 30, 14000, 53, 2, 1, 14.9, 2012, 0.75, 1, 99, 'wines/June2020/дивноморское/Марселан.png', 'vino-Divnomorskoe-Marselan-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(160, 'Блан Де Нуар', 2900, 'Игристое вино Усадьба Дивноморское Блан Де Нуар белое брют 2012', '', 'Насыщенное игристое, приготовленное из одного сорта винограда, по классической технологии с бутылочной выдержкой на осадке более 24 месяцев. Часть сусла ферментировалась во французском баррике. Яблочно-молочное брожение. <br>', 'Прекрасное сопровождение для блюд из морепродуктов, лобстеров, крабов, устриц.<br>', 'Цвет соломенный с телесным оттенком. В букете отчётливо ощущаются яркие тона нектарина, цитрусовые оттенки, нотки бриошей. Вкус яркий, осежающий, с гармоничной кислотностью, в котором преобладают нюансы свежей груши и айвы. Послевкусие долгое, приятное, со сливочным оттенком. <br>', 'Подавать при температуре 08 - 10С.<br>', 'Divnomorskoe Brut Blanc De Noir 2012', 51, 47, 9900, 53, 2, 2, 11, 2012, 0.75, 2, 99, 'wines/June2020/дивноморское/Blanc de Noir.png', 'vino-Divnomorskoe-Brut-Blanc-De-Noir-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(161, 'Блан Де Блан', 3500, 'Игристое вино Усадьба Дивноморское Блан Де Блан белое экстра брют 2012', '', 'Насыщенное игристое, приготовленное из одного сорта винограда, по классической технологии бутылочной выдержкой на осадке более 24 месяцев. Часть сусла ферментировалась во французском баррике. Яблочно-молочное брожение. <br>', 'Прекрасное сопровождение для блюд из морепродуктов, а также прекрасное дополнение к десертам.<br>', 'Лёгкий, воздушный, прозрачный перляж, зеленовато-золотое платье. Обладает сложным, элегентным букетом, в котором переплетаются ароматы полевых цветов , личи, акации, мушмулы, цитрусовых, с оттенками сдобной выпечки и кремовыми нюансами. Прекрасно структурированное, с выраженной благородной кислотностью, превосходной чистоты и гармонии, с оттенками мёда, акации и фейхоа - во вкусе, и лёгкими нотками цукатов, выпечки и белой смородины в элегантном послевкусии.<br>', 'Подавать при температуре 08 - 10 С .<br>', 'Divnomorskoe Brut Blanc De Blancs 2012', 51, 81, 5390, 53, 2, 2, 11, 2012, 0.75, 11, 100, 'wines/June2020/дивноморское/Blanc de Blanc.png', 'vino-Divnomorskoe-Brut-Blanc-De-Blancs-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(163, 'Мерло', 450, 'Вино Кубань-Вино Высокий Берег Мерло крансое сухое 2016', '', 'Вино изготовлено из отборного винограда, фементировано в стальных ёмкостях, кратковременная выдержка в дубовой бочке.<br>', '<p>Прекрасно сочетается с блюдами из мяса высокой степени прожарки, овощами на гриле и молодыми сырами.</p>', '<p>Цвет вина от красно-рубинового до тёмно-гранатового. В аромате оттенки вишни в сочетании с лёгкими древесными нотами. Вкус чистый, полный, свежий, гармоничный, сортовой.<br></p>', 'Подавать при температуре 12-14 С .<br>', 'Kuban-Vino Merlot Vysoki Bereg 2016', 48, 31, 40000, 41, 2, 1, 12, 2016, 0.75, 1, 99, 'wines/June2020/кубань вино/Высокий Берег Мерло Красное Сухое 2016 0,75л.jpg', 'vino-Kuban-Vino-Merlo-Vysoki-Bereg-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(164, 'Каберне Совиньон', 450, 'Вино Кубань-Вино Высокий Берег Мерло красное сухое 2016', '', 'Вино изготовлено из отборного винограда, фементировано в стальных ёмкостях, кратковременная выдержка в дубовой бочке.<br>', '<p>Станет идеальной парой для мясных блюд, запечённого картофеля, фаршированного овечьей брынзой, а также пасты с ветчиной и острым сыром.</p>', '<p>Цвет вина от тёмно-красного с рубиновым оттенком, до гранатового. В аромате чёрные фрукты, тона паслёна и чёрной смородины, лёгкие табачные ноты, древесные оттенки. Вкус полный, мягкий, терпковатый, с тонкими нотами дерева.<br></p>', 'Подавать при температуре 12-14 С .<br>', 'Kuban-Vino KS Vysoki Bereg 2016', 48, 20, 40000, 41, 2, 1, 12, 2016, 0.75, 1, 100, 'wines/June2020/кубань вино/Высокий Берег Каберне-Совиньон Красное Сухое 2016 0,75л.jpg', 'vino-Kuban-Vino-caberne-Vysoki-Bereg-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(165, 'Сира', 450, 'Вино Кубань-Вино Высокий Берег Сира красное сухое 2016', '', 'Вино изготовлено из отборного винограда, фементировано в стальных ёмкостях, кратковременная выдержка в дубовой бочке.<br>', '<p>Прекрасно сочетается с блюдами из красного мяса, дичи, сырами с пикантным вкусом.</p>', '<p>Цвет вина от рубинового до тёмно-рубинового с гранатовым оттенком. В аромате сложный, терпкий, с нотами специй и табака. Вкус, полный, терпкий, бархатистый.<br></p>', 'Подавать при температуре 12-14 С .<br>', 'Kuban-Vino Syrah Vysoki Bereg 2016', 48, 66, 40000, 41, 2, 1, 12, 2016, 0.75, 1, 100, 'wines/June2020/кубань вино/Высокий Берег Сира Красное Сухое 2016 0,75л.jpg', 'vino-Kuban-Vino-Syrah-Vysoki-Bereg-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 28),
(166, 'Цвайгельт', 450, 'Вино Кубань-Вино Высокий Берег Цвайгельт розовое сухое 2016', '', 'Настой в прессах с предварительным охлаждением мезги. Флотация периодического действия. Брожение при контролируемой температуре 16 - 18С.<br>', '<p>Вино прекрасно в качестве аперитива, а также с лёгкими закусками и молодыми сырами.</p>', '<p>Цвет вина от светло-розового до малинового. В аромате фруктовый, свежий. Вкус терпковатый, лёгкий, сочный.<br></p>', 'Подавать при температуре 10-12 С .<br>', 'Kuban-Vino Tsvagelt Vysoki Bereg 2016', 48, 76, 40000, 41, 2, 3, 12, 2016, 0.75, 1, 100, 'wines/June2020/кубань вино/Высокий Берег Цвайгельт Розовое Сухое 2016 0,75л.jpg', 'vino-Kuban-Vino-Tsvagelt-Vysoki-Bereg-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(167, 'Каберне Резерв', 750, 'Вино Кубань-Вино Каберне Совиньон Резерв крансое сухое 2015', '', 'Вино изготовлено из отборного винограда, брожение сусла на мезге, выдержка в французском дубе 12 месяцев.<br>', '<p>Прекрасно показывает себя в паре с дичью, красным мясом, сырами, мясными деликатесами и пастой.</p>', '<p>Цвет вина от красно-рубинового до рубинового с гранатовым оттенком. Великолепное сочетание чернослива, сафьяна, прекрасного дуба и шоколада, подчёркнутые тонами топлёного молока. Во вкусе черносливовые тона, подчёркнутыми округлыми, сладкими танинами и гармоничной терпкостью. Послевкусие приятное и долгое.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Kuban-Vino Cabernet rezerv 2015', 48, 20, 40000, 41, 2, 1, 12, 2015, 0.75, 1, 99, 'wines/June2020/кубань вино/ШТР Каберне Сухое Красное 2015 0,75л.jpg', 'vino-Kuban-Vino-Caberne-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(168, 'Грюнер Вельтлинер', 450, 'Вино Кубань-Вино Высокий Берег Грюнер Вельтлинер белое сухое 2016', '', 'Настой в винификаторах, с предварительным охлаждением мезги. Флотация периодического действия. Брожение при контролируемой температуре 16 - 18С, в том числе частично с дубовой бочке.<br>', '<p>Вино прекрасно сочетается с морепродуктами, блюдами из птицы, сёмги в икорном соусе, лазаньи с грибами и сыром.</p>', '<p>Цвет вина от светло-соломенного с зеленоватым оттенком до соломенного. Аромат минерально-фруктовый с лёгкими пряными нотами. Вкус свежий, гармоничный.<br></p>', 'Подавать при температуре 10-12 С .<br>', 'Kuban-Vino Gruner Vysoki Bereg 2016', 48, 17, 40000, 41, 2, 2, 12, 2016, 0.75, 1, 100, 'wines/June2020/кубань вино/Высокий Берег Грюнер Вельтлинер Белое Сухое 2016 0,75л.jpg', 'vino-Kuban-Vino-Gruner-Vysoki-Bereg-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(170, 'Мюллер Тургау', 450, 'Кубань-Вино Высокий Берег Мюллер Тургау Вельтлинер 2016', '', 'Настой в прессах, с предварительным охлаждением мезги. Флотация периодического действия. Брожение при контролируемой температуре 16 - 18С, в том числе частично с дубовой бочке.<br>', '<p>Вино прекрасно сочетается с зелёными салатами, морепродуктами, пастой с креветками и сёмгой, сёмгой на гриле с овощами, лазаньей с грибами и сыром..</p>', '<p>Цвет вина от светло-соломенного с зеленоватым оттенком до соломенного. Аромат минерально-фруктовый с цветочными нотами. Вкус свежий, гармоничный.<br></p>', 'Подавать при температуре 10-12 С .<br>', 'Kuban-Vino Muller Vysoki Bereg 2016', 48, 38, 40000, 41, 2, 2, 12, 2016, 0.75, 1, 99, 'wines/June2020/кубань вино/Высокий Берег Мюллер Тургау Белое Сухое 2016 0,75л.jpg', 'vino-Kuban-Vino-Muller-Vysoki-Bereg-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(174, 'Каберне Совиньон', 1300, 'Вино Бюрнье Каберне Совиньон красное сухое 2008', '', 'Вино изготовлено из отборного винограда. Выдержка 18 месяцев в бочках из французского дуба.<br>', 'Идеально сопровождает дичь, красное мясо, мясо на гриле, сыры с терпким вкусом. Перед употреблением необходимо декантировать.<br>', '<p>Цвет глубокий, тёмно-рубиновый. Полное, сложное вино с ароматами специй и чёрного перца. Ярко выраженный сортовой вкус. Терпкое вино с танинами и долгим послевкусием.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Burnie cabernet 2008', 43, 20, 10000, 10, 2, 1, 14, 2008, 0.75, 1, 99, 'wines/June2020/бюрнье/Каберне совиньон 2008.png', 'vino-Burnie-cabernet-2008', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 33),
(175, '100 оттенков Саперави', 1700, 'Фанагория 100 оттенков красного Саперави красное сухое 2013', '', 'Брожение в пижажных винификаторах. Яблочно-молочное брожение на мезге. Выдержка в бочках из французского дуба в течение одного года. Выдержка в бутылке не менее 6 месяцев.<br>', '<p>Вино станет прекрасной парой для блюд из дичи, мяса приготовленного на гриле и для мягких сыров.</p>', '<p>Цвет вина тёмно-рубиновый. В аромате переливаются сырно-молочные и шоколадные оттенки с нюансами вишнёвой косточки, чернослива, шелковицы и фиалки. Вкус - бархатистый, экстрактивный, плотный с приятной терпкостью, ванильными и ягодными оттенками, шоколадным послевкусием.<br></p>', 'Подавать при температуре 16 - 18 С .<br>', 'Fanagoria 100 Saperavi 2013', 56, 61, 5670, 1, 2, 1, 13, 2013, 0.75, 1, 97, 'wines/June2020/фаногория/100 оттенков красного саперави.png', 'vino-fanagoria-100-ottenkov-krasnogo-saperavi-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(177, '100 оттенков Каберне', 1500, 'Вино Фанагория 100 оттенков красного Каберне красное сухое 2013', '', 'Вино выдерживается не менее одного года во французской дубовой бочке, а после розлива - 6 месяцев непосредственно в бутылке.<br>', '<p>Станет идеальной парой для мясных блюд, твёрдых сыров. Особенно хорошо для говяжьих рёбрышек.</p>', '<p>Вино обладает тёмно-рубиновым цветом. В сложном букете доминирует аромат специй, ванили на общем фоне &quot;дымки&quot; дубовой бочки. Вкус вина классический, сортовой с оттенками чёрной смородины, табака, чернослива, кожи. Отличная структура и хорошо сбалансированность делают вино необычайно изысканным.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Fanagoria 100 Cabernet 2013', 56, 20, 5670, 1, 2, 1, 13, 2013, 0.75, 1, 100, 'wines/June2020/фаногория/100 оттеноков красного каберне.png', 'vino-fanagoria-100-ottenkov-belogo-kaberne-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(178, '100 оттенков Шардоне', 1500, 'Вино Фанагория 100 оттенков белого Шардоне белое сухое 2011', '', 'Ферментация при контролируемой температуре. Выдержка не менее одного года в дубовой бочке, а после розлива - ещё не менее 6 месяцев непосредственно в бутылке.<br>', '<p>Идеально для жульена из лисичек и перлотто с грибами. А также сочетается с сырами с белой плесенью.</p>', '<p>Вино соломенного цвета. В букете гармонично сочетаются тона спелых фруктов, цитрусов и цветов с благородными сливочными оттенками. Вкус полный, гармоничный, с хорошим уровнем кислотности, минеральными, фруктово-цитрусовыми оттенками и лёгкими древесными нюансами. Послевкусие длительное, сливочно-фруктовое с переливами цветочных и цитрусовых нот.</p>', 'Подавать при температуре 10 - 12 С .<br>', 'Fanagoria 100 Chardonnay 2011', 56, 81, 5670, 1, 2, 2, 13, 2011, 0.75, 1, 100, 'wines/June2020/фаногория/100 оттенков белого шардоне.png', 'vino-fanagoria-100-ottenkov-belogo-shardone-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(179, 'Блан Де Блан', 600, 'Игристое вино Фанагория Блан Де Блан брют белое 2016', '', 'Произведено методом Шарма с дополнительной выдержкой на дрожжевом осадке, из фанагорийского винограда сорта Шардоне.<br>', '<p>Подойдет в качестве аперитива, а также к мясу птицы и особенно для запечённого яблока с солёной карамелью.</p>', '<p>Цвет вина золотистый. Аромат фруктовый с оттенками груши, яблока, персика. Вкус мягкий, свежий с нюансами спелых летних фруктов, сдобной булочки в обрамлении пряных и медовых оттенков.</p>', 'Подавать при температуре 06 - 08 С .<br>', 'Fanagoria Blan De Blancs brut', 56, 81, 20000, 1, 2, 2, 13, 2016, 0.75, 2, 100, 'wines/June2020/фаногория/Blanc de Blancs брют.png', 'vino-Fanagoria-Blan-De-Blancs-brut-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 46),
(180, 'Блан Де Нуар', 600, 'Игристое вино Фанагория Блан Де Нуар брют белое 2016', '', 'Произведено методом Шарма с дополнительной выдержкой на дрожжевом осадке, из фанагорийского винограда сорта Пино Нуар. Вино изготовлено \"по белому способу\" - прессование предшествует брожению.<br>', '<p>Подойдет в качестве аперитива, а также к морепродуктам, рыбе.</p>', '<p>Цвет платиновый. Аромат свежий, цветочный с нотами спелых фруктов. Вкус сдержанный, строгий, с отличной кислотностью, цитрусовыми, сливочными и ореховыми оттенками. Послевкусие длительное, цитрусовое.</p>', 'Подавать при температуре 05 - 07 С .<br>', 'Fanagoria Blan De Noir brut 2016', 56, 47, 20000, 1, 2, 2, 13, 2016, 0.75, 2, 99, 'wines/June2020/фаногория/Blanc de Noirs брют.png', 'vino-Fanagoria-Blan-De-Noir-brut-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 46),
(181, 'Блан Де Блан', 850, 'Вино игристое Усадьба Перовских Блан Де Блан брют 2016', '', 'Брожение в стали при низких температурах и вторичное брожение в бутылке на дрожжевом осадке в течение 36 месяцев.<br>', '<p>Подойдет в качестве аперитива. Сочетается с морепродуктами и мягкими сырами с белой плесенью.</p>', '<p>Вино золотистого цвета с зеленоватым отблеском. В аромате тона белых фруктов: яблоко, груша, персик. Раскрывается оттенками свежей сдобы и нюансами ванили. Во вкусе лёгкое, живое, со сбалансированной кислотностью. Чувствуются ноты яблок, лёгкий косточковый тон и минеральные оттенки. Вино со свежим и длительным послевкусием.</p>', 'Подавать при температуре 08 - 10 С .<br>', 'Perovsky Blan De Blancs brut', 71, 81, 20000, 56, 4, 2, 13, 2016, 0.75, 2, 99, 'wines/June2020/dopolnitelno/Blanc De Blancs.jpg', 'vino-perovskih-blan-de-blan-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(182, 'Новый Свет Пино Фран', 900, 'Игристое вино Новый Свет Пино Фран розовое полусладкое 2014', '', 'Лучшее моносортовое розовое игристое вино Крыма последних лет, созданного с соблюдением полного цикла классической технологии выдержки в бутылке.<br>', '<p>Отличное подойдёт как к фруктам, так и к мясным блюдам, приготовленным на гриле.  <br></p>', 'Цвет вина в бокале - это цвет крымского персика. В первом же носе ощутим аромат свежих красных фруктов. Послевкусие насыщенное, с оттенками красной смородины и крыжовника. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet Pino Fran semisweet 2014', 68, 49, 7800, 45, 4, 3, 12, 2014, 0.75, 8, 98, 'wines/June2020/новый свет/3.3 ВИВ псладкое розовое НС Пино Фран.png', 'vino-New-Svet-Pino-Fran-polusladkoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(183, 'Новый Свет Пино Фран', 900, 'Игристое вино Новый Свет Пино Фран розовое полусухое 2014', '', 'Лучшее моносортовое розовое игристое вино Крыма последних лет, созданного с соблюдением полного цикла классической технологии выдержки в бутылке.<br>', '<p>Это игристое подойдёт к сырам, орешкам и отлично сочетается с грушей, абрикосом и красными садовыми ягодами.  <br></p>', 'Цвет вина в бокале - это цвет крымского персика. В первом же носе ощутим аромат свежих красных фруктов. Послевкусие насыщенное, с оттенками красной смородины и крыжовника. <br>', '<p>Подавать при температуре 08 -10 С.<br></p>', 'New Svet Pino Fran semidry 2014', 68, 49, 7800, 45, 4, 3, 12, 2014, 0.75, 9, 100, 'wines/June2020/новый свет/3.2 ВИВ псухое розовое НС Пино Фран.png', 'vino-New-Svet-Pino-Fran-polusuhoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(184, 'Новый Свет Крымское красное', 900, 'Игристое вино Новый Свет Пино Фран красное полусладкое 2014', '', 'Лучший образец игристого среди красных вин, созданного с соблюдением полного цикла классической технологии выдержки в бутылке.<br>', '<p>Игристое станет приятной изюминкой десертного стола. <br></p>', 'Цвет вина тёмно-гранатовый. В аромате ощутима вишнёвая косточка и паслёновый тон. Насыщенное, но в то же время лёгкое, питкое вино, обладает ярким вкусом с богатой гаммой сухофруктов и чёрной смородины, молочного шоколада. Длительное бархатное послевкусие. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet red semisweet 2014', 68, 20, 7800, 45, 4, 1, 12, 2014, 0.75, 8, 100, 'wines/June2020/новый свет/3.5 ВИВ псладкое красное НС Крымское игристое.png', 'vino-New-Svet-red-polusladkoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(185, 'Новый Свет Пино Нуар', 950, 'Игристое вино Новый Свет Пино Фран Нуар выдержанное розовое брют 2012', '', 'Игристое приготовлено \"по-белому\" методу из красного сорта Пино Нуар, по классической технологии, с выдержкой в бутылке более 9 месяцев.<br>', '<p>Это великолепный аперитив. Отлично сочетается с морепродуктами, твёрдыми сырами, мясными блюдами, дичью.  <br></p>', 'Цвет вина с лёгкой розовинкой. Аромат вина сложный, гармоничный, фруктовый с плодовыми тонами. Вкус насыщенный, округлый, бархатистый, с леденцовым оттенком монпасье. Послевкусие насыщенное, длительное. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet Pino Noir brut  2012', 68, 47, 8000, 45, 4, 3, 12, 2012, 0.75, 2, 100, 'wines/June2020/новый свет/2.1 РШВ брют розовое НС ПН.png', 'vino-New-Svet-Pino-Noir-brut-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(187, 'Терруар Красный Клён', 1400, 'Вино Гай-Кодзор Терруар красный клён красное сухое 2015', '', 'Виноград собирается вручную. Ферментация проходит в терморегулируемых ёмкостях из нержавеющей стали при температуре 18 - 25С, в течение 10 - 12 дней. Выдержка в французском дубе 14 месяцев и далее 6 месяцев в бутылке.<br>', 'Отличное сопровождение к террину из дичи, тушённому кролику, говядине по-бургундски, сырам.<br>', '<p>Вино насыщенного рубинового цвета. Аромат открытый, яркий, фруктовые тона спелой вишни и сливы дополняются оттенками специй. Вкус насыщенный, густой. Гармоничное, объёмное вино с прекрасной структурой, плотными, но мягкими и округлыми танинами и длительным послевкусием. <br></p>', 'Подавать при температуре 14 - 16С.<br>', 'Gai-Kodzor terroir red 2015', 55, 66, 10000, 30, 2, 1, 13, 2015, 0.75, 1, 100, 'wines/June2020/гай кодзор/Terroir Red Klen de Gai-Kodzor.png', 'vino-gai-kodzor-krasnyi-klen-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(188, 'Каберне Совиньон Резерв', 3500, 'Вино Бельбек Каберне Резерв Совиньон красное сухое 2015', '', '<p>Вино приготовлено по классической технологии красных вин. Выдержка 12 месяцев в бочках из французского дуба.Окончательное созревание вина происходит в бутылках в течение 6 месяцев.</p>', '<p>Лучше всего сочетается с жирными продуктами. Яркий аромат уравновешивает «остроту» и\r\nвысвечивает его ягодные ноты. Подойдёт классическому\r\nговяжьему стейку, грибной пицце с томатным соусом, тушеным ребрышкам и к\r\nбефстроганов под грибным соусом.</p>', '<p>Смолисто бордовый цвет с фиолетовым оттенком.\r\nНасыщенный аромат зрелой вишни, черных ягод и густых пряностей. Удаленная нота\r\nфиалки. Вкус полнотелый, с заметным содержанием танинов, с насыщенными\r\nфруктовыми, слегка цветочными, а так же острыми нотами – от болгарского до\r\nчерного перца.</p>', '<p>Подавать при температуре 18 - 19С.</p>', 'Belbek Cabernet Sauvignon 2015', 34, 20, 1200, 9, 4, 1, 14, 2015, 0.75, 1, 95, 'wines/June2020/belbek/belbek-ks-2015.jpg', 'vino-Belbek-Caberne-Sauvignon-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 37);
INSERT INTO `wines` (`id`, `title`, `price`, `meta_title`, `description`, `production_feature`, `combination`, `feature`, `innings`, `model`, `winery_id`, `grape_sort_id`, `edition`, `manufacturer_id`, `region_id`, `color_id`, `fortress`, `year`, `volume`, `sugar_id`, `count`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`, `excerpt_id`) VALUES
(189, 'Новый Свет Кюве коллекционное', 1000, 'Игристое вино Новый Свет Кюве белое экстра брют 2010', '', 'Игристое приготовлено по классической технологии выдержки в бутылке минимум 3 года.<br>', '<p>Игристое подают к белому мясу, различным твёрдым пикантным сырам, морепродуктам.  <br></p>', 'Вино светло-соломенного цвета с живым блеском и завораживающей игрой пузырьков. В аромате присутствуют букет цветущего винограда и полевых цветов. Вкус свежий, лёгкий, с характерными тонами минеральности. Возможна пикантная горчинка. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet Cuve brut  2010', 68, 47, 8000, 45, 4, 2, 12, 2010, 0.75, 11, 99, 'wines/June2020/новый свет/1.9 РШК экстра брют белое НС КЮВЕ.png', 'vino-New-Svet-Cuve-brut-2010', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(190, 'Новый Свет Пино Нуар коллекционное', 950, 'Игристое вино Новый Свет Пино Фран Нуар розовое полусухое 2014', '', 'Игристое приготовлено \"по-белому\" методу из красного сорта Пино Нуар, по классической технологии, с выдержкой в бутылке более 3 лет.<br>', '<p>Великолепный аперитив. Сочетается с паштетом из лосося, десертом из трюфеля, запечённой олениной, омарами. Отлично подойдёт к десертам, гармонирует с дыней, грушей и тропическими фруктами.  <br></p>', 'Цвет вина с лёгкой розовинкой. Аромат вина сложный, гармоничный, фруктовый с плодовыми тонами. Вкус насыщенный, округлый, бархатистый, с леденцовым оттенком монпасье. Послевкусие насыщенное, длительное. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet Pino Noir semidry  2014', 68, 47, 8000, 45, 4, 3, 12, 2011, 0.75, 9, 100, 'wines/June2020/новый свет/1.6 РШК псух розовое НС ПН.png', 'vino-New-Svet-Pino-Noir-polusuhoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(191, 'Новый Свет Коллекционное', 900, 'Игристое вино Новый Свет Пино коллекционное белое полусухое 2014', '', 'Игристое приготовлено по классической технологии, с выдержкой в бутылке более 3 лет.<br>', '<p>Великолепный аперитив. Замечательный тандем с десертами из ягод и мороженным.  <br></p>', 'Цвет вина светло-соломенный. Аромат вина плодово-фруктовый, а вкус мягкий, сбалансированный с медовыми оттенками, тонами жёлтых лепестков цветущего подсолнечника. Послевкусие насыщенное, длительное. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet collection semidry 2011', 68, 47, 8000, 45, 4, 2, 12, 2011, 0.75, 9, 99, 'wines/June2020/новый свет/1.4 РШК псух белое НС 0,75.png', 'vino-New-Svet-collection-polusuhoe-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(192, 'Новый Свет Коллекционное', 900, 'Игристое вино Новый Свет коллекционное белое сухое 2011', '', 'Игристое приготовлено по классической технологии, с выдержкой в бутылке более 3 лет.<br>', '<p>Вино прекрасно сочетается с дарами моря, рыбой в холодном и горячем виде. <br></p>', 'Цвет вина бледно-лимонный. В аромате переплетаются яблочно-лимонные нотки с оттенками тропических фруктов. Продолжительная бутылочная выдержка придаёт вину тонкий, изящный аромат ванили, ореха и пряностей. Вкус лёгкий, минеральный. Игристое деликатное с долгим послевкусием, объёмное, но воздушное.<br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet collection dry 2011', 68, 81, 8000, 45, 4, 2, 12, 2011, 0.75, 1, 100, 'wines/June2020/новый свет/1.3 РШК сухое белое НС.png', 'vino-New-Svet-collection-suhoe-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(193, 'Новый Свет Коллекционное', 900, 'Игристое вино Новый Свет коллекционное белое брют 2011', '', 'Игристое приготовлено по классической технологии, с выдержкой в бутылке более 3 лет.<br>', '<p>Превосходный аперитив. Идеальное сочетание с сёмгой, красной или чёрной икрой. Отлично сопровождает блюда из рыбы, молодой телятины, дичи, мясо домашней птицы и фрукты.  <br></p>', 'Цвет вина светло-соломенный, с изумрудным полутоном. Аромат весенного цветения сада, к которому подключаются нотки полевого разнотравья. Великолепное питкое игистое с лёгкими тонами дикой черешни. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet collection brut 2011', 68, 4, 8000, 45, 4, 2, 12, 2011, 0.75, 2, 100, 'wines/June2020/новый свет/1.2 РШК брют белое НС.png', 'vino-New-Svet-collection-brut-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(194, 'Новый Свет Коллекционное', 900, 'Игристое вино Новый Свет коллекционное белое экстра брют 2011', '', 'Игристое приготовлено по классической технологии, с выдержкой в бутылке более 3 лет.<br>', '<p>Отличным сочетанием станут полумягкие сыры из козьего или коровьего молока, твёрдые сыры, а также вино изысканно подчёркивает вкус форели, палтуса и моллюсков.  <br></p>', 'Цвет вина светло-соломенный, с лёгкими изумрудными бликами. Завораживает ароматом цветущей виноградной лозы. Вкус игристого тонкий, освежающий, с целым комплексом ароматов - с отенками яблок, белых цветов. Пикантная минеральность и долгое послевкусие. <br>', '<p>Подавать при температуре 10 -12 С.<br></p>', 'New Svet collection extra brut 2011', 68, 4, 8000, 45, 4, 2, 12, 2011, 0.75, 11, 100, 'wines/June2020/новый свет/1.1 РШК экстра брют белое НС.png', 'vino-New-Svet-collection-extra-brut-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(195, 'Мысхако розовое', 350, 'Шампанское Мысхако розовое полусухое 2017', '', 'Игристое приготовлено резервуарным методом.<br>', 'Прекрасное дополнение к лёгким блюдам, фруктам, десертам.<br>', 'Цвет нежно розовый. Аромат развитый, тонкий, с лёгкими вишнёвыми нотами. Вкус полный, гармоничный, ягодный с нежным приятным послевкусием.<br>', '<p>Подавать при температуре 10 - 12 С.<br></p>', 'Myshako rose semidry 2017', 69, 47, 20000, 44, 2, 3, 12.4, 2017, 0.75, 9, 98, 'wines/June2020/balka/Мысхако полусухое розовое.png', 'vino-Myshako-rose-semidry-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 46),
(196, 'Эссе Шардоне', 1100, 'Вино Сатера Эссе Шардоне белое сухое 2015', '', 'Выдержка во французских пьесах не менее 12 месяцев.<br>', '<p>Сопровождение блюд из рыбы, морепродуктов и белого мяса.<br></p>', '<p>Золотисто-соломенный цвет. В аромате ваниль, тропические фрукты, акация. Полнотелое, с хорошей сбалансированной кислотностью и долгим, приятным послевкусием.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'esse chardonnay 2015', 35, 81, 20000, 49, 4, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/эссе/Ессе Шардоне.png', 'vino-esse-chardonnay-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(197, 'Бастардо Массандра', 790, 'Вино Массандра Бастардо красное десертное 2013', '', '<p>Вино производится по специальной технологии с 1966 года. Выдержка в дубовом буте не менее двух лет. Сахар 200 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино интенсивного тёмно-гранатового цвета. Букет вина яркий, сложный с кофейно-шоколадными тонами. Вкус полный, мягкий, бархатистый с оттенками какао, шоколада.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra bastardo 2013', 40, 8, 20000, 43, 4, 1, 16, 2013, 0.75, 4, 98, 'wines/June2020/массандра/Бастардо Массандра ВЗГУ (2013).png', 'vino-Massandra-bastardo-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(198, 'Эссе Рислинг', 1200, 'Вино Сатера Эссе Рислинг белое сухое 2015', '', 'Ферментация вина в ёмкостях из нержавеющей стали, при контролируемой температуре.<br>', '<p>Сопровождение для морепродуктов и белого мяса, идеально для устриц.<br></p>', '<p>Вино светло-зелёного цвета. Яркое, свежее, южный сортовой аромат. Во вкусе тропические фрукты, пикантаная кислотность.<br></p>', 'Подавать при температуре 09 - 11 С .<br>', 'esse risling 2016', 35, 56, 20000, 49, 4, 2, 12, 2016, 0.75, 1, 99, 'wines/June2020/сатера/рислинг.png', 'vino-esse-risling-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(199, 'Эссе Каберне Совиньон', 1100, 'Вино Сатера Эссе Каберне Совиньон красное сухое 2015', '', 'Выдержка во французских барриках не менее 12-14 месяцев.<br>', '<p>Сопровождение блюд из красного мяса, особенно хорошо для стейков.<br></p>', '<p>Вино насыщенного красного цвета. В аромате тона чёрной смородины, сливы. Питкое, деликатные танины. Во вкусе спелая вишня.<br></p>', 'Подавать при температуре 14 - 18 С .<br>', 'esse cabernet 2015', 35, 20, 20000, 49, 4, 1, 13, 2014, 0.75, 1, 100, 'wines/June2020/эссе/Ессе Каберне.png', 'vino-esse-caberne-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(200, 'Эссе Мерло', 1100, 'Вино Сатера Эссе Мерло красное сухое 2015', '', 'Выдержка во французских барриках не менее 12 месяцев.<br>', '<p>Сопровождение блюд из красного мяса, твёрдых сыров.<br></p>', '<p>Вино светло-вишнёвого цвета. В аромате оттенки свежих ягод, ежевики, красной сливы. Среднетелое, питкое, с тонами красных ягод. <br></p>', 'Подавать при температуре 14 - 18 С .<br>', 'esse merlot 2015', 35, 31, 20000, 49, 4, 1, 13, 2015, 0.75, 1, 100, 'wines/June2020/эссе/Ессе Мерло.png', 'vino-esse-merlot-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(201, 'Эссе Рубиновый', 1750, 'Вино Сатера Эссе Рубиновый красное сухое 2015', '', 'Выдержка во французских барриках не менее 12 месяцев.<br>', '<p>Сопровождение блюд из красного мяса, особенно мясо на гриле.<br></p>', '<p>Вино тёмно-рубинового цвета. В аромате ежевика, пряности, джемовые ноты. Вкус танинный с тонами спелой чёрной смородины, клюквы. Послевкусие длительное.<br></p>', 'Подавать при температуре 14 - 18С.<br>', 'esse ruby 2015', 35, 20, 20000, 49, 4, 1, 13, 2015, 0.75, 1, 100, 'wines/June2020/сатера/рубиновый.png', 'vino-esse-ruby-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(202, 'Эссе Сира', 1600, 'Вино Сатера Эссе Сира красное сухое 2015', '', 'Выдержка во французских барриках не менее 12 - 14 месяцев.<br>', '<p>Сопровождение блюд из красного мяса, приготовленного на гриле, блюдам из дичи под ягодными соусами.<br></p>', '<p>Вино насыщенного гранатового цвета с пурпурным оттенком. Аромат пряный, с тонами спелой вишни, ежевики, чёрной смородины и горького шоколада. Вкус сочный с хорошо структурированными яркими танинами, оттенками кофе и спелых слив в послевкусии.<br></p>', 'Подавать при температуре 14 - 18 С .<br>', 'esse syrah 2015', 35, 66, 20000, 49, 4, 1, 13, 2015, 0.75, 1, 100, 'wines/June2020/сатера/сира.png', 'vino-esse-syrah-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(203, 'Эссе Красное', 1000, 'Вино Сатера Эссе Красное Сухое 2016', '', 'Ферментация в стальных ёмкостях, мерло выдержано в дубовой бочке. Из года в год структура купажа меняется.<br>', '<p>Сопровождение блюд из красного мяса, особенно мясо на гриле.<br></p>', '<p>Вино вишнёво-красного цвета. Питкое, гармоничное. Во вкусе мягкие танины.<br></p>', 'Подавать при температуре 16 - 18 С .<br>', 'esse red 2016', 35, 20, 20000, 49, 4, 1, 12.5, 2016, 0.75, 1, 100, 'wines/June2020/эссе/Ессе сух кр.png', 'vino-esse-red-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 27),
(204, 'Эссе Гевюрцтраминер', 1400, 'Вино Сатера Эссе Гевюрцтраминер белое сухое 2016', '', 'Ферментация вина в ёмкостях из нержавеющей стали, при контролируемой температуре .<br>', '<p>Сопровождение для морепродуктов, сырам с белой плесенью.<br></p>', '<p>Вино светло-жёлтого цвета. Интенсивный аромат грейпфрута, цитрусовых. Во вкусе лайм, спелый грейпфрут. Приятное длительное послевкусие с пряным окончанием. <br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'esse gevurcstraminer 2016', 35, 14, 20000, 49, 4, 2, 13, 2016, 0.75, 1, 100, 'wines/June2020/сатера/гевюрц — копия.png', 'vino-esse-gevurcstraminer-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(205, 'Эссе Белое', 850, 'Вино Сатера Эссе Белое Сухое 2016', '', 'Ферментация вина в ёмкостях из нержавеющей стали, при контролируемой температуре. Из года в год структура купажа меняется.<br>', '<p>Сопровождение для морепродуктов и белого мяса, идеально для устриц.<br></p>', '<p>Вино золотисто-соломенного цвета. Питкое, свежее, гармоничное.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'esse white 2016', 35, 81, 20000, 49, 4, 2, 13, 2016, 0.75, 1, 100, 'wines/June2020/эссе/Ессе сух бел.png', 'vino-esse-white-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(206, 'Эссе Мускат', 900, 'Вино Сатера Эссе Мукат белое сухое 2016', '', 'Ферментация вина в ёмкостях из нержавеющей стали, при контролируемой температуре.<br>', '<p>Сопровождение для морепродуктов, фруктов, десертов.<br></p>', '<p>Вино светло-зелёного цвета с лимонным оттенком. В аромате белые цветы, чайная роза. Вкус свежий, с тонами цитрусовых и приятной кислотностью.<br></p>', 'Подавать при температуре 10 - 21 С .<br>', 'esse muscat 2016', 35, 34, 20000, 49, 4, 2, 11.5, 2016, 0.75, 1, 100, 'wines/June2020/эссе/Ессе Мускат.png', 'vino-esse-muscat-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(207, 'Эссе Совиньон Блан', 1000, 'Вино Сатера Эссе Совиньон Блан белое сухое 2015', '', 'Ферментация вина в ёмкостях из нержавеющей стали, при контролируемой температуре .<br>', '<p>Сопровождение для морепродуктов и белого мяса, идеально для устриц.<br></p>', '<p>Вино светло-зелёного цвета. Яркое, свежее, южный сортовой аромат. Во вкусе тропические фрукты, пикантаная кислотность.<br></p>', 'Подавать при температуре 09 - 11 С .<br>', 'esse sovignon blan 2016', 35, 68, 20000, 49, 4, 2, 13, 2016, 0.75, 1, 100, 'wines/June2020/эссе/Ессе Совиньон блан.png', 'vino-esse-sovignon-blan-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(208, 'Эссе Кокур', 1000, 'Вино Сатера Эссе Кокур белое сухое 2016', '', 'Ферментация вина в ёмкостях из нержавеющей стали, при контролируемой температуре .<br>', '<p>Сопровождение для морепродуктов и белого мяса, идеально для устриц.<br></p>', '<p>Вино светло-зелёного цвета. Яркое, свежее, южный сортовой аромат. Во вкусе тропические фрукты, пикантаная кислотность.<br></p>', 'Подавать при температуре 09 - 11 С .<br>', 'esse kokur 2016', 35, 23, 20000, 49, 4, 2, 12, 2016, 0.75, 1, 100, 'wines/June2020/сатера/кокур.png', 'vino-esse-kokur-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(209, 'Эссе Бастардо', 1400, 'Вино Сатера Эссе Бастардо красное сухое 2016', '', 'Виноград собирается вручную в небольшие ящики 9-10 кг. Вино выдерживается в маленьких дубовых бочках.<br>', 'Вино прекрасно подойдёт к различным блюдам из мяса, дичи, пасте и сырам.<br>', '<p>Вино глубокого гранатового цвета. Вино демонстрирует щедрый аромат, сотканный из нот чёрной смородины, сливы и вишни, которые дополняются лёгкими шоколадными нюансами.<br></p>', 'Подавать при температуре 16 - 18С.<br>', 'esse bastardo 2016', 35, 8, 20000, 49, 4, 1, 13, 2016, 0.75, 1, 100, 'wines/June2020/сатера/бастардо.png', 'vino-esse-bastardo-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(210, 'Эссе Розе', 1000, 'Вино Сатера Эссе Розе розовое сухое 2015', '', 'Вино приготовлено \"по-белому\" из красного сорта Каберне Совиньон. Ферментация в ёмкостях при контролируемой температуре.<br>', '<p>Сопровождение блюд из красного мяса, особенно хорошо для десертов и фруктов.<br></p>', '<p>Вино розового цвета. В аромате землянично-молочные ноты. Вкус ягодный, свежий с пикантной кислинкой.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'esse rose 2015', 35, 20, 20000, 49, 4, 3, 13.5, 2015, 0.75, 1, 100, 'wines/June2020/эссе/Ессе розе.png', 'vino-esse-rose-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(211, 'Кефесия', 2600, 'Вино Массандра Кефесия красное сухое 2015', '', '<p>Тщательная сортировка ягод, применение холодной мацерации, брожение сусла на мезге и выдержка на мезге. Выдержка в барриках от 6 до 18 месяцев. Кефесия - автохтонный, крымский сорт винограда.</p>', '<p>Сопровождение блюд из красного мяса, дичи, птицы, барбекю, овощей на гриле, выдержанным сырам. Но самое лучшее сочетание с блюдами крымской национальной кухни. Лагман, плов, сарма, юфахаш, кубете, самса.<br></p>', '<p>Нарядные, глубокие переливы благородного рубина притягивают и будят воображение. Вино дарит богатую ароматику. Насыщенные тона смородины, спелая ежевика, шелковица и нотки дикой малины тонко и живо переплетаются с приятными танинами.</p>', 'Подавать при температуре 17 - 18 С .<br>', 'massandra kefesia 2015', 40, 22, 20000, 43, 4, 1, 13, 2015, 0.75, 1, 97, 'wines/June2020/массандра/Кефесия.png', 'vino-massandra-kefesia-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(212, 'Гевюрцтраминер', 900, 'Вино Гай-Кодзор Гевюрцтраминер белое полусухое 2015', '', '<p>Ручной сбор винограда. Перед ферментацией проводится дебурбаж (отстаивание сусла) в течение 48 часов. Затем вино выдерживается в терморегулируемых ёмкостях из нержавеющей стали в течение 7 месяцев.<br></p>', 'Подавать к закускам и салатам, морепродуктам, рыбе, белому мясу, десертам с грушей.<br>', '<p>Вино светло-соломенного цвета. В аромате оттенки тропических плодов, медовой груши, нежные ноты розовых лепестков и личи. Вкус сбалансированный, с приятной кислотностью, в нём заметны тона сладких цитрусовых и спелых экзотических фруктов.<br></p>', '<p>Подавать при температуре 10 - 12С.<br></p>', 'Gai-Kodzor gevurch 2015', 55, 14, 5000, 30, 2, 2, 12, 2015, 0.75, 9, 100, 'wines/June2020/гай кодзор/9910_Gewurztraminer de Gai-Kodzor.jpg', 'vino-Gai-Kodzor-gevurch-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(213, 'Мерло - Гренаш', 560, 'Вино Гай-Кодзор мерло гренаш крансое сухое 2014', '', '<p>Ручной сбор винограда. Ферментация проходит в ёмкостях из нержавеющей стали, при температуре 18 - 25 С в течение 10 - 12 дней, затем вино выдерживается в аналогичных ёмкостях в течение 9 месяцев.<br></p>', 'Подавать к мясным закускам, блюдам из красного мяса.<br>', '<p>Вино тёмно-рубинового цвета. В аромате яркий и фруктовый, с выраженными оттенками лесных ягод и земляники. Вкус насыщенный и достаточно густой. Структуру этому вину придает Мерло, в ассамбляже также ярко проявляются сортовые особенности Гренаша - фруктовость и лёгкость. Гармоничное и сбалансированное вино с умеренно терпкими танинами в послевкусии.<br></p>', '<p>Подавать при температуре 16 - 18 С.<br></p>', 'Gai-Kodzor merlot grenache 2014', 55, 16, 20000, 30, 2, 1, 12, 2014, 0.75, 1, 98, 'wines/June2020/гай кодзор/9904_Gai-Kodzor Merlot Grenache.jpg', 'vino-Gai-Kodzor-merlot-grenache-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(214, 'Семильон', 900, 'Вино Гай-Кодзор Семильон белое сладкое 2016', '', '<p>Перед ферментацией проводится дебурбаж (отстаивание сусла) в течение 48 часов. Затем вино выдерживается в терморегулируемых ёмкостях из нержавеющей стали в течение 7 месяцев. Остаточный природный сахар 45 г/л.</p>', 'Подавать к умеренно-сладким десертам, фруктовым десертам.<br>', '<p>Вино светло-соломенного цвета. В первый год после розлива тяготеет больше к свежести, минеральности и лёгким ароматам нектарина и яблока. Хоть вино и не выдерживалось бочках, оно прекрасно стареет, по мере выдержки в бутылке в аромате проявляются нотки айвового конфитюра, груши и мёда, тело приобретает густоту и маслянистость. Потенциал хранения 4 - 5 лет.<br></p>', '<p>Подавать при температуре 08 - 10 С.<br></p>', 'Gai-Kodzor semillion 2016', 55, 63, 2600, 30, 2, 2, 12, 2016, 0.75, 10, 100, 'wines/June2020/гай кодзор/9915_Семийон Гай-Кодзор.png', 'vino-gai-kodzor-semilon-sladkoe-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(215, 'Мурведр - Сира', 800, 'Вино Гай-Кодзор Мурведер - Сира красное сухое 2014', '', '<p>Ручной сбор винограда. Ферментация проходит в ёмкостях из нержавеющей стали, при температуре 18 - 25 С в течение 10 - 12 дней, затем вино выдерживается в аналогичных ёмкостях в течение 9 месяцев.<br></p>', 'Вино подают к мясу на гриле, мясным закускам, дичи и твёрдым сырам.<br>', '<p>Яркий пурпурный цвет с лёгкими фиолетовыми оттенками. Ароматы лесных ягод переплетаются с нотками специй. Иногда проявляются специфические ноты &quot;скотного двора&quot;. Вкус вина среднетелый, хорошо структурированный, с бархатистыми танинами и перечными оттенками в длительном насыщенном послевкусии. <br></p>', '<p>Подавать при температуре 16 - 18 С.<br></p>', 'Gai-Kodzor mourvedre-Syrah 2014', 55, 32, 20000, 30, 2, 1, 13, 2014, 0.75, 1, 100, 'wines/June2020/гай кодзор/9902_Mourvedre-Syrah de Gai-Kodzor.jpg', 'vino-Gai-Kodzor-murvedre-Syra-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(216, 'Мускат', 900, 'Вино Гай-Кодзор Мускат белое сладкое 2014', '', '<p>Перед ферментацией проводится дебурбаж (отстаивание сусла) в течение 48 часов. Затем вино выдерживается в терморегулируемых ёмкостях из нержавеющей стали в течение 7 месяцев. Остаточный природный сахар 80 г/дм.</p>', '<p>Вино станет гастрономическим сопровождением десертам, особенно из яблок и груш.</p>', '<p>Вино золотистого цвета. Аромат мощный, с характерными, легкоузнаваемыми мускатными тонами, оттенками апельсиновых цукатов и тропических фруктов.<br></p>', '<p>Подавать при температуре 08 - 10 С.<br></p>', 'Gai-Kodzor muscat 2014', 55, 33, 5000, 30, 2, 2, 12, 2014, 0.75, 10, 98, 'wines/June2020/гай кодзор/9909_Muscat de Gai-Kodzor.jpg', 'vino-gai-kodzor-muscat-sladkoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(217, 'Розе', 950, 'Вино Гай-Кодзор Розе розовое сухое 2016', '', '<p>Ручной сбор винограда. Перед ферментацией проводится дебурбаж (отстаивание сусла) в течение 48 часов. Затем вино выдерживается в терморегулируемых ёмкостях из нержавеющей стали в течение 7 месяцев.<br></p>', '<p>Вино отлично сочетается с различными салатами, закусками, пиццей, блюдами азиатской кухни.</p>', '<p>Вино нежно-розового цвета. Тонкий, элегантный, фруктовый аромат, с тонами смородины и малины, лесных ягод, граната и лёгкими цветочными нотами. Вкус хорошо сбалансированный, с тонами спелых ягод, освежающей кислотностью и минеральным послевкусием.<br></p>', '<p>Подавать при температуре 08 - 10 С.<br></p>', 'Gai-Kodzor rose 2016', 55, 16, 5000, 30, 2, 3, 13, 2016, 0.75, 1, 87, 'wines/June2020/гай кодзор/9911_Rose de Gai-Kodzor.png', 'vino-Gai-Kodzor-rose-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(218, 'Пино Нуар', 800, 'Вино Гай-Кодзор Пино Нуар красное сухое 2014', '', '<p>Ручной сбор винограда. Ферментация проходит в ёмкостях из нержавеющей стали, при температуре 18 - 25 С в течение 10 - 12 дней, затем вино выдерживается в аналогичных ёмкостях в течение 9 месяцев.<br></p>', 'Хорошо сочетается с красным мясом, дичью, грибами и сырами.<br>', '<p>Вино ярко-рубинового цвета. Пряный букет вина состоит из красивых вишнёвых и черносмородиновых нот и оттенков дуба. Вкус этого изящного пряного вина обладает приятной округлостью, мягкими танинами и освежающим фруктовым послевкусием.<br></p>', '<p>Подавать при температуре 16 - 18 С.<br></p>', 'Gai-Kodzor pinot noir 2014', 55, 47, 10000, 30, 2, 1, 12, 2014, 0.75, 1, 100, 'wines/June2020/гай кодзор/9902_Mourvedre-Syrah de Gai-Kodzor.jpg', 'vino-Gai-Kodzor-pino-noir-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(219, 'Руссан', 850, 'Вино Гай-Кодзор Руссан белое сухое 2015', '', '<p>Ручной сбор винограда. Перед ферментацией проводится дебурбаж (отстаивание сусла) в течение 48 часов. Затем вино выдерживается в терморегулируемых ёмкостях из нержавеющей стали в течение 7 месяцев.<br></p>', '<p>Вино станет гастрономическим сопровождением к салатам с креветками, блюдам на гриле, блюдам из морской рыбы с овощами, к рагу из индейки с соусом из белых грибов.</p>', '<p>Вино светло-соломенного цвета. Выразительный аромат, в нём раскрываются нотки полевых цветов, мёда, спелых абрикосов. Вкус объёмный, обволакивающий, хорошо сбалансированный. Фруктовые мотивы переплетены с цветочными нюансами. Изящное и чувственное вино. <br></p>', '<p>Подавать при температуре 10 - 12 С.<br></p>', 'Gai-Kodzor roussanne 2015', 55, 59, 5000, 30, 2, 2, 13.5, 2015, 0.75, 1, 100, 'wines/June2020/гай кодзор/9908_Roussanne de Gai-Kodzor.jpg', 'vino-Gai-Kodzor-roussanne-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(220, 'Терруар Блан', 750, 'Вино Гай-Кодзор Терруар Блан белое сухое 2016', '', '<p>Ручной сбор винограда в ящики по 10 кг. Винификация в пятитонных танках из нержавеющей стали. Кратковременная выдержка в чанах из нержавеющей стали.<br></p>', '<p>Вино превосходно будет сочетаться с блюдами из белого мяса, рыбой на гриле.</p>', '<p>Вино светло-соломенного цвета. В сбалансированном глубоком аромате тона цветов, полевых трав, мёда, белых фруктов. Вкус травянисто-медовый, с цветочным послевкусием. Достаточно высокая кислотность, мягко интегрирована в вино, вкус чистый, свежий, минеральный. Вино прекрасно пьётся в молодом возрасте, а также, как ожидается, сохранит свои качества в течение 3 - 4 лет с момента розлива в бутылку.<br></p>', '<p>Подавать при температуре 10 - 12 С.<br></p>', 'Gai-Kodzor terroir blanc 2015', 55, 63, 3500, 30, 2, 2, 13, 2016, 0.75, 1, 100, 'wines/June2020/гай кодзор/9920_Terroir Blanc de Gai-Codzor.png', 'vino-Gai-Kodzor-terroir-blan-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(221, 'Шираз', 560, 'Вино Гай-Кодзор Шираз красное сухое 2014', '', '<p>Ручной сбор винограда. Ферментация проходит в ёмкостях из нержавеющей стали, при температуре 18 - 25С в течение 10 - 12 дней, затем вино выдерживается в аналогичных ёмкостях в течение 9 месяцев.<br></p>', 'Подавать к мясным закускам, блюдам из красного мяса.<br>', '<p>Вино пурпурного цвета с фиолетовым оттенком. Аромат насыщенный, с нотами спелых чёрных ягод и специй. Вкус приятный, с насыщенными тонами ежевики. Достаточно мягкое и долгое послевквусие завершается приятной бархатистой терпкостью.<br></p>', '<p>Подавать при температуре 16 - 18 С.<br></p>', 'Gai-Kodzor shiraz 2014', 55, 82, 20000, 30, 2, 1, 13, 2014, 0.75, 1, 100, 'wines/June2020/гай кодзор/9906_Gai-Kodzor Shiraz.jpg', 'vino-Gai-Kodzor-shiraz-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(222, 'Вионье', 850, 'Вино Гай-Кодзор Мускат белое сухое 2014', '', '<p>Ручной сбор винограда. Перед ферментацией проводится дебурбаж (отстаивание сусла) в течение 48 часов. Затем вино выдерживается в терморегулируемых ёмкостях из нержавеющей стали в течение 7 месяцев.<br></p>', '<p>Вино станет гастрономическим сопровождением для летних салатов, мидий, карпаччо из морских рыб.</p>', '<p>Вино зеленовато-золотистого цвета. Аромат яркий, насыщенный, в нём заметны лёгкие цитрусовые тона, оттенки барбариса, груши, персика и спелых жёлтых фруктов. Вкус насыщенный, великолепно сбалансированный, с тонами персика и манго, ярко выраженной палитрой фруктовых оттенков.<br></p>', '<p>Подавать при температуре 10 - 12 С.<br></p>', 'Gai-Kodzor viognier 2015', 55, 12, 10000, 30, 2, 2, 12.5, 2015, 0.75, 1, 99, 'wines/June2020/гай кодзор/9907_Viognier de Gai-Kodzor.jpg', 'vino-Gai-Kodzor-viognier-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(223, 'Алиготе', 450, 'Вино Алиготе Элеганс Юбилейная белое полусухое 2016', '', '<p>Приготовлено по технологии остановки брожения холодом.</p>', '<p>Вино станет гастрономическим сопровождением белому мясу, рыбе, десертам.</p>', '<p>Вино светло-соломенного цвета. Аромат интенсивный, комплексный, с оттенками белых цветов, грейпфрута, зелёного яблока, тропических фруктов, лёгкой сливочной нотой и яркой минеральностью. Во вкусе полнотелое, умеренно-округлое, свежее с нотами зелёного яблока, скошенной травы, минеральными тонами и лёгкой грейпфрутовой горчинкой.<br></p>', '<p>Подавать при температуре 10 - 12 С.<br></p>', 'Ubileinay aligote 2016', 52, 4, 92000, 62, 2, 2, 12.6, 2016, 0.75, 9, 100, 'wines/June2020/aubilein/АЛИГОТЕ ЭЛЕГАНС.jpg', 'vino-Ubileinay-aligote-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(224, 'Ангелы и демоны Каберне', 420, 'Вино Кубань-вино Ангелы и демоны Каберне красное сухое 2016', '', 'Брожение на мезге с дображиванием в резервуарах. Яблочно-молочное брожение.<br>', '<p>Великолепно подходит к блюдам из дичи, сырам, плову, пицце, лазанье, мясу на гриле.</p>', '<p>Вино тёмно-красного цвета с гранатовым оттенком. В аромате преобладание тонов чернослива и паслёна в сочетании с лёгкими нотами табака и дуба. Вкус чистый, полный, свежий, гармоничный.</p>', 'Подавать при температуре 12 - 14С .<br>', 'Kuban-vino angel red 2016', 48, 20, 5670, 41, 2, 1, 13, 2016, 0.75, 1, 100, 'wines/June2020/кубань вино/Ангелы и Демоны Красное Каберне 0,75L.jpg', 'vino-Kuban-vino-angel-krasnoe-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(225, 'Крю Лермонт Каберне', 850, 'вино Фанагория Крю Лермонт Каберне Совиньон красное сухое 2014', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдержка в дубовой бочке из французского дуба в течение 12 месяцев.<br>', '<p>Идеально для блюд из телятины, ягнятины, баранины, дичи.</p>', '<p>Вино насыщенного рубинового цвета. Сложный объёмный аромат с оттенками ванили, джемовыми нотами чёрной смородины, чернослива и вишни. Вкус бархатистый, хорошо структурированный, с мягкими танинами, оттенками пряностей, шоколада, увяленных чёрных ягод и сафьяна. Послевкусие длительное, ягодно-перечное. </p>', 'Подавать при температуре 16 - 18С .<br>', 'Fanagoria cru lermont cabernet 2014', 56, 20, 10000, 1, 2, 1, 13, 2014, 0.75, 1, 100, 'wines/June2020/фаногория/Каберне Совиньон.png', 'vino-Fanagoria-cru-lermont-caberne-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(226, 'Ангелы и демоны Каберне', 420, 'Вино Кубань-Вино Ангелы и демоны Каберне белое сухое 2016', '', 'Основой технологии производства этого белого вина является контроль времени контакта сока с виноградной кожицей, мало кто производит именно белые образцы, обычно применяя эту технологию в производстве розовых вин.<br>', '<p>Великолепно подходит к блюдам из светлого мяса, утиной и гусиной печени, рыбе, морепродуктам, различным сырам, орехам.</p>', '<p>Вино светло-соломенного цвета с платиновым оттенком. Тонкий, с фруктово-ягодными нотами, с преобладанием белой сливы и крыжовника. Вкус чистый, полный, свежий, гармоничный.</p>', 'Подавать при температуре 10 - 12С .<br>', 'Kuban-vino angel white 2016', 48, 20, 5670, 41, 2, 2, 13, 2016, 0.75, 1, 100, 'wines/June2020/кубань вино/Ангелы и Демоны Белое Каберне 0,75L.jpg', 'vino-Kuban-vino-angel-white-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(227, 'Крю Лермонт Саперави', 850, 'Вино Фанагория Крю Лермонт Саперави красное сухое 2015', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдержка в дубовой бочке из французского дуба в течение 12 месяцев.<br>', '<p>Идеально для мясо-гриль, блюд из баранины и говядины, дичи, выдержанных сыров.</p>', '<p>Вино глубокого рубинового цвета. Букет интенсивный, развитый с тонами кизила, черноплодной рябины, чёрной смородины, вишни, чернослива, кожи в обрамлении молочно-сырных и табачно-пряных нот. Вкус полный, округлый, экстрактивный с широкой гаммой оттенков чёрных ягод, с яркими нотками пряностей. Длительное шоколадно-сливочное послевкусие. </p>', 'Подавать при температуре 17 - 18С .<br>', 'Fanagoria cru lermont saperavi 2015', 56, 61, 10000, 1, 2, 1, 12, 2015, 0.75, 1, 99, 'wines/June2020/фаногория/Саперави.png', 'vino-Fanagoria-cru-lermont-saperavi-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(228, 'Крю Лермонт Пино Нуар', 850, 'Вино Фанагория Крю Лермонт Пино Нуар красное сухое 2015', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдержка в дубовой бочке из французского дуба в течение 12 месяцев.<br>', '<p>Идеально для лёгких мясных закусок, рыбы-гриль, дичи, грибов на темпуре, пасты.</p>', '<p>Вино гранатового цвета с терракотовыми тонами. В букете доминируют оттенки осеннего леса, лесных ягод. Вкус мягкий, бархатистый, с нотами вишни, пряностей. Послевкусие длительное.</p>', 'Подавать при температуре 16 - 18С .<br>', 'Fanagoria cru pinot noir 2015', 56, 47, 10000, 1, 2, 1, 13, 2015, 0.75, 1, 98, 'wines/June2020/фаногория/Пино Нуар.png', 'vino-Fanagoria-cru-pino-noir-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(229, 'Крю Лермонт Мерло', 850, 'Вино Фанагория Крю Лермонт Мерло красное сухое 2014', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдержка в дубовой бочке из французского дуба в течение 12 месяцев.<br>', 'Сочетается с стейками, блюдами из баранины и ягнятины с пряными травами, тёплыми салатами и холодными закусками, пастой с насыщенными соусами, пиццей, выдержанными сырами.<br>', '<p>Вино тёмно-гранатового цвета. Аромат с нотами красных лесных ягод, болгарского перца, ванили, замши и специй. Вкус мягкий, с бархатистыми танинами, оттенками спелых чёрных ягод и фруктов. Послевкусие длительное, пряно-ягодное.  </p>', 'Подавать при температуре 16 - 17С .<br>', 'Fanagoria cru lermont merlot 2014', 56, 31, 10000, 1, 2, 1, 13, 2014, 0.75, 1, 100, 'wines/June2020/фаногория/Мерло.png', 'vino-Fanagoria-cru-lermont-merlo-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(230, 'Крю Лермонт Рислинг', 850, 'Вино Фанагория Крю Лермонт Рислинг белое сухое 2015', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдержка вина проводилась в нейтральной ёмкости из нержавеющей стали в течение 24 месяцев.<br>', '<p>Идеально для блюд из рыбы, морепродуктам, устрицам.</p>', '<p>Аристократическое вино светло-соломенного цвета. В сложном аромате преобладают тона выдержки, яркие петрольные и минеральные ноты в сочетании с оттенками розового грейпфрута, цедры лимона и луговых трав. Вкус строгий со звенящей кислотностью и длительным минеральным послевкусием.</p>', 'Подавать при температуре 10 - 12С .<br>', 'Fanagoria cru lermont risling 2015', 56, 56, 10000, 1, 2, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/фаногория/Рислинг.png', 'vino-Fanagoria-cru-lermont-risling-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(231, 'Крю Лермонт Совиньон Блан', 850, 'Вино Фанагория Крю Лермонт Совиньон Блан белое сухое 2015', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдержка вина проводилась в нейтральной ёмкости из нержавеющей стали в течение 18 месяцев.<br>', '<p>Идеально для морепродуктов, овощных салатов, горячим блюдам из курицы, рыбе со сливочным соусом.</p>', '<p>Цвет светло-соломенный. В букете свежая зелень, трава, белые цветы, зелёное яблоко, крыжовник, тропические фрукты, лёгкая минеральность. Вкус освежающий, бодрящий и вместе с тем сбалансированный, гармоничный.</p>', 'Подавать при температуре 12 - 13С .<br>', 'Fanagoria cru lermont sovignon 2015', 56, 68, 10000, 1, 2, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/фаногория/Совиньон Бланк.png', 'vino-Fanagoria-cru-lermont-sovignon-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(232, 'Крю Лермонт Шардоне', 850, 'Вино Фанагория Крю Лермонт Шардоне белое сухое 2015', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдерживалось вино в бочке из французского дуба, в течение 12 месяцев.<br>', '<p>Идеально для морепродуктов, пасты с сливочным соусом, сложных салатов, блюд из рыбы или курицы, сыров.</p>', '<p>Вино светло-золотистого цвета. В букете развитые тона выдержки в дубе с оттенками белых фруктов и ванили. Вкус полный, мягкий, гармоничный с длительным приятным послевкусием.</p>', 'Подавать при температуре 12 - 13 С .<br>', 'Fanagoria cru lermont chardonnay 2015', 56, 81, 10000, 1, 2, 2, 13, 2015, 0.75, 1, 99, 'wines/June2020/фаногория/Шардоне.png', 'vino-Fanagoria-cru-lermont-chardonnay-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(233, 'Крю Лермонт Пино Гриджио', 850, 'Вино Фанагория Крю Лермонт Пино Гриджио белое сухое 2015', '', 'После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдержкивалось вино в бочке из французского дуба, в течение 12 месяцев.<br>', '<p>Идеально для морепродуктов, омаров, лобстеров.</p>', '<p>Вино светло-соломенного цвета. Букет изящный, с тонами жёлтых нарциссов и цветущей сирени, белых фруктов и цитрусовых. Вкус хорошо сбалансированный с длительным ягодно-минеральным послевкусием. </p>', 'Подавать при температуре 12 - 13С .<br>', 'Fanagoria cru lermont pinot gridgio 2015', 56, 45, 10000, 1, 2, 2, 13, 2015, 0.75, 1, 99, 'wines/June2020/фаногория/Пино Гриджио.png', 'vino-Fanagoria-cru-lermont-pino-gridgio-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(234, 'Шардоне', 1700, 'Вино Лефкадия Шардоне белое сухое 2014', '', '<p>После тщательной сортировки ягоды подвергаются ферментации при низкой температуре. Выдерживалось вино в бочке из французского дуба, в течение 8 месяцев. Вино обладает хорошим потенциалом к выдержке.</p>', '<p>Отлично подойдёт к блюдам из рыбы и домашней птицы, а также к различным сырам, лобстерам.</p>', '<p>Вино соломенного цвета с зеленоватыми отблесками. Аромат сливочный, с минеральными нюансами, по мере развития, в бокале появляются оттенки тропических фруктов, цитрусовых, лёгкие нотки миндаля. Вкус мягкий, сливочный, со свежей кислотностью и деликатной горчинкой в послевкусии.</p>', 'Подавать при температуре 10 - 12С.<br>', 'Lefkadia chardonnay 2014', 7, 81, 15000, 42, 2, 2, 14, 2014, 0.75, 1, 99, 'wines/June2020/dopolnitelno/Лефкадия Шардоне 2014.png', 'vino-lefkadia-chardonnay-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(235, 'Кагор Партенит', 590, 'Вино Массандра Кагор Партенит красное десертное 2013', '', '<p>Ликёрное вино производится по традиционной технологии. Сахар 160 г/дм.<br></p>', 'Прекрасный аперитив.<br>', '<p>Вино интенсивного тёмно-рубинового цвета. Аромат оригинальный, характерный для данного типа вина, с тонами сливок и чёрной смородины, полный и бархатистый вкус с мягкой терпкостью.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra kagor partenit 2013', 40, 8, 20000, 43, 4, 1, 16, 2015, 0.75, 4, 100, 'wines/June2020/массандра/Кагор Партенит ВЗГУ (2015).png', 'vino-Massandra-kagor-partenit-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(236, 'Кагор Южнобережный', 790, 'Вино Массандра Кагор Южнобережный красное десертное 2012', '', '<p>Вино производится по специальной технологии с 1933 года, с нагреванием и настаиванием дроблённого винограда перед сбраживанием. Выдержка в дубе не менее трёх лет. Сахар 180 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино интенсивного тёмно-гранатового цвета. Букет вина сложный, сортовой с тонами сливок и чёрной смородины. Вкус полный, экстрактивный с мягкой терпкостью и очень долгим приятным послевкусием.</p>', 'Подавать при температуре 16 - 18 С.<br>', 'Massandra kagor ugnoberegniy 2012', 40, 61, 20000, 43, 4, 1, 16, 2012, 0.75, 4, 100, 'wines/June2020/массандра/Кагор Южнобережный ВЗГУ (2012).png', 'vino-Massandra-kagor-ugnoberegniy-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(237, 'Мускат белый Красного Камня', 900, 'Вино Массандра Мускат белый Красного Камня белое десертное 2013', '', '<p>Вино производится по специальной технологии с 1944 года. Выдержка в дубовом буте не менее двух лет. Сахар 230 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино светло-янтарного цвета. Букет вина яркий, утончённый, насыщенный ароматом мускатных ягод с медовыми оттенками цветов и трав крымской яйлы, тонами чайной розы, персика и апельсиновой корочки. Лёгкий цитрон во вкусе делает это вино неповторимым, изысканно-нежным и благородным.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra muskat red stone 2013', 40, 34, 20000, 43, 4, 2, 13, 2013, 0.75, 4, 100, 'wines/June2020/массандра/Мускат белый красного камня ВЗГУ (2013).png', 'vino-Massandra-muskat-krasnogo-kamnya-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(238, 'Мадера Массандра', 790, 'Вино Массандра мадера Массандра белое крепкое 2011', '', '<p>Вино производится по классической технологии с 1892 года. Выдержано в неполных дубовых бочках на открытых солнечных площадках не менее пяти лет. Сахар 30 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино нарядного золотистого цвета. Характерный букет с орехово-коньячными тонами и полный, гармоничный, чуть жгучий вкус с тонами калёного орешка в послевкусии.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Madera Massandra 2011', 40, 64, 20000, 43, 4, 2, 19.5, 2011, 0.75, 5, 100, 'wines/June2020/массандра/Мадера Массандра ВЗГУ (2011).png', 'vino-Massandra-madera-massandra-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(239, 'Мадера Крымская', 650, 'Вино Мадера Крымская крепкое белое 2008', '', '<p>Вино производится по классической технологии с 1951 года. Выдержано в неполных дубовых бочках на открытых солнечных площадках не менее 4 - х лет. Сахар 40 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Цвета вина от золотистого до тёмно-янтарного. Яркий букет, сложный, своеобразный. Вкус полный, гармоничный с яркими мадерными тонами и привкусом калёного орешка.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra-madera krymskay 2008', 40, 80, 20000, 43, 4, 2, 19, 2008, 0.75, 5, 100, 'wines/June2020/массандра/Мадера Крымская ВЗГУ (2008).png', 'vino-Massandra-madera-krymskay-2008', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(240, 'Седьмое небо князя Голицына', 590, 'Виное Мадера Массандра белое десертное 2011', '', '<p>Вино производится по классической технологии с 1880 года. Выдержано в дубовых бутах не менее двух лет. Сахар 190 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино нарядного золотисто-янтарного цвета. Букет вина сложный, яркий, с преобладанием медовых тонов, тонов персика, айвы и мушмулы. Пряный букет в сочетании с полным, гармоничным, мягким, маслянистым вкусом. </p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra 7 nebo knyza Golitsyna 2010', 40, 23, 20000, 43, 4, 2, 16, 2010, 0.75, 4, 99, 'wines/June2020/массандра/Седьмое небо князя Голицына ВЗГУ (2010).png', 'vino-Massandra-7-nebo-knyza-Golitsyna-2010', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(241, 'Кокур десертный Сурож', 590, 'Вино Массандра Кокур десертный Сурож белое десертное 2014', '', '<p>Вино производится по специальной технологии с 1945 года. Выдержка в дубовом буте не менее двух лет. Сахар 160 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино золотисто-янтарного цвета. Оригинальный букет с цветочными медово-пряными оттенками дополняет мягкий, гармоничный вкус.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra kokur surog 2014', 40, 23, 20000, 43, 4, 2, 16, 2014, 0.75, 4, 100, 'wines/June2020/массандра/Кокур десертный Сурож ВЗГУ (2014).png', 'vino-Massandra-kokur-surog-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(242, 'Мускат белый Массандра', 590, 'Вино Массандра Мускат белый Массандра белое десертное 2010', '', '<p>Вино производится по специальной технологии с 1891 года. Выдержка в дубовом буте не менее двух лет. Сахар 170 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино светло-золотистого цвета. Букет вина яркий, сортовой, с тонами выдержки. Вкус полный, гармоничный, мягкий.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra muskat white massandra 2010', 40, 34, 20000, 43, 4, 2, 16, 2010, 0.75, 4, 99, 'wines/June2020/массандра/Мускат белый Массандра ВЗГУ (2010).png', 'vino-Massandra-muskat-beloe-massandra-2010', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(243, 'Портвейн красный Ливадия', 790, 'Вино Массандра портвейн красный Ливадия красное крепкое 2011', '', '<p>Вино производится по специальной технологии с 1891 года. Выдержка в дубовом буте не менее трёх лет. Классический тип портвейна. Сахар 80 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино интенсивного тёмно-красного цвета. Букет вина яркий, сортовой с тонкими сафьяновыми тонами. Вкус полный, гармоничный с яркими тонами вишнёвой косточки и долгим приятным послевкусием.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra portvein red livadia 2011', 40, 20, 20000, 43, 4, 1, 18.5, 2011, 0.75, 5, 100, 'wines/June2020/массандра/Портвейн красный Ливадия ВЗГУ (2011).png', 'vino-Massandra-portvein-krasnoe-livadia-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(244, 'Портвейн красный Южнобережный', 790, 'Вино Массандра портвейн красный Южнобережный красное крепкое 2012', '', '<p>Вино производится по специальной технологии с 1944 года. Выдержка в дубе не менее трёх лет. Сахар 110 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино интенсивно красного цвета. Букет вина яркий, сложный, плодовый, пряный с оттенком чернослива. Вкус сложный, полный с тонами вишнёвой косточки, столь характерной для классических портвейнов.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra portvein red ygnyi 2012', 40, 3, 20000, 43, 4, 1, 18, 2012, 0.75, 5, 97, 'wines/June2020/массандра/Портвейн красный Южнобережный ВЗГУ (2012).png', 'vino-Massandra-portvein-krasnoe-ygnyi-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(245, 'Портвейн красный Массандра', 790, 'Вино Массандра портвейн красный Массандра красное крепкое 2012', '', '<p>Вино производится по специальной технологии с 1894 года. Выдержка в дубовом буте не менее трёх лет. Сахар 80 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино интенсивного тёмно-красного цвета. Букет вина развитый, с ярко выраженными особенностями сорта. Вкус полный, гармоничный, мужественный.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra portvein red massandra 2012', 40, 32, 20000, 43, 4, 1, 18.5, 2012, 0.75, 5, 99, 'wines/June2020/массандра/Портвейн красный Массандра ВЗГУ (2012).png', 'vino-Massandra-portvein-krasnoe-massandra-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(246, 'Портвейн белый Сурож', 590, 'Вино Массандра портвейн белый Сурож белое крепкое 2011', '', '<p>Вино производится по специальной технологии с 1836 года. Выдержка в дубовом буте не менее трёх лет. Сурож - древнеславянское название города Судак. Сахар 95 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино золотисто-янтарного цвета. Букет сортовой с токайским оттенком в сочетании с плодово-медовыми тонами. Вкус полный, гармоничный, запоминающийся.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra portvein white surog 2011', 40, 23, 20000, 43, 4, 2, 17.5, 2011, 0.75, 5, 100, 'wines/June2020/массандра/Портвейн белый сурож ВЗГУ (2011).png', 'vino-Massandra-portvein-beloe-surog-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(247, 'Херес Массандра', 800, 'Вино Массандра Херес Массандра 2014', '', '<p>Вино производится по специальной технологии с 1944 года. Выдержка в дубе не менее 4 - х лет. Особенность приготовления - выдержка вина под плёнкой хересных дрожжей. Сахар 25 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино золотистого цвета с зеленоватым оттенком. За внешнюю красоту и нарядность херес сравнивают с жёлтым нарциссом, уснувшем в серебряной чаше. Букет сложный, тонкий, с ярко выраженным характерным хересным тоном. Вкус гармоничный с лёгким тоном горького миндаля, калёного орешка и солоноватостью в послевкусии.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra heres massandra 2014', 40, 64, 20000, 43, 4, 2, 19.5, 2014, 0.75, 5, 98, 'wines/June2020/массандра/Херес Массандра ВЗГУ (2014).png', 'vino-Massandra-heres-massandra-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(248, 'Мускат белый Ливадия', 590, 'Вино Массандра Мускат белый Ливадия белое десертное 2013', '', '<p>Вино производится по специальной технологии с 1892 года. Выдержка в дубовом буте не менее двух лет. Сахар 270 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино янтарно-золотистого цвета. Букет вина яркий, сортовой, с тонким медовым ароматом лугов крымской яйлы и тонами выдержки. Вкус полный, маслянистый, гармоничный с приятным изюмным оттенком.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra muskat white livadia 2013', 40, 34, 20000, 43, 4, 2, 13, 2013, 0.75, 4, 81, 'wines/June2020/массандра/Мускат белый Ливадия ВЗГУ (2013).png', 'vino-Massandra-muskat-beloe-livadia-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(249, 'Пино Гри Южнобережное', 790, 'Вино Массандра Пино гри южнобережное белое десертное 2012', '', '<p>Вино производится по специальной технологии с 1991 года. Выдержка в дубовом буте не менее двух лет. Сахар 200 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино золотого цвета. Букет вина яркий, сортовой, с тонами выдержки. Вкус полный, маслянистый, гармоничный, с тонами ржаной корочки.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra pinot gri ygnoberegnoe 2012', 40, 48, 20000, 43, 4, 2, 16, 2012, 0.75, 4, 100, 'wines/June2020/массандра/Пино-Гри Южнобережное ВЗГУ (2012).png', 'vino-Massandra-pinot-gri-ygnoberegnoe-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7);
INSERT INTO `wines` (`id`, `title`, `price`, `meta_title`, `description`, `production_feature`, `combination`, `feature`, `innings`, `model`, `winery_id`, `grape_sort_id`, `edition`, `manufacturer_id`, `region_id`, `color_id`, `fortress`, `year`, `volume`, `sugar_id`, `count`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`, `excerpt_id`) VALUES
(250, 'Токай Южнобережный', 700, 'Вино Массандра Токай Южнобережный белое крепкое 2012', '', '<p>Марочное десертное вино производится по специальной технологии с 1932 года. Выдержка в дубовом буте не менее трёх лет. Вино несёт в себе солнце. Сахар 200 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино золотисто-янтарного цвета. Букет вина очень сложный, тонкий, с тонами корочки свежего ржаного хлеба и айвы. Вкус гармоничный, полный, маслянистый.</p>', 'Подавать при температуре 12 - 14С.<br>', 'Massandra tokai ygnoberegnyi 2012', 40, 75, 20000, 43, 4, 2, 16, 2012, 0.75, 5, 88, 'wines/June2020/массандра/Токай Южнобережный ВЗГУ (2012).png', 'vino-Massandra-tokai-ygnoberegnyi-2012', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(251, 'Токай Массандра', 700, 'Вино Массандра Токай Массандра белое крепкое 2013', '', '<p>Марочное десертное вино производится по специальной технологии с 1905 года. Выдержка в дубовом буте не менее двух лет. Сахар 160 г/дм.</p>', 'Прекрасный аперитив.<br>', '<p>Вино тёмно-золотого цвета. Букет вина яркий, зрелый, сложный с медовыми и айвово-грушёвыми тонами. Вкус полный, мягкий, гармоничный с удивительно долгим и приятным послевкусием айвы и груши.</p>', 'Подавать при температуре 12 - 14 С.<br>', 'Massandra tokai massandra 2013', 40, 75, 20000, 43, 4, 2, 16, 2013, 0.75, 5, 91, 'wines/June2020/массандра/Токай Массандра ВЗГУ (2013).png', 'vino-Massandra-tokai-massandra-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(252, 'Сира', 2600, 'Вино Массандра Сира красное сухое 2015', '', '<p>Тщательная сортировка ягод, применение холодной мацерации, брожение сусла на мезге и выдержка на мезге. Выдержка в барриках от 6 до 18 месяцев.<br></p>', 'Мощная структура и насыщенность Сира - это вызов еде, которая должна быть такой же яркой и насыщенной. Сочетается с твёрдыми выдержанными пряными сырами. Идеальное сочетание это мясо на гриле, шашлык, перчёная салями.<br>', '<p>Вино дразнит живой кислотностью, терпкостью и сочностью зёрен граната, отчётливой нотой специй. Цвет рубиновый, глубокий, насыщенный. В сложном букете меняются нюансы и оттенки, переливаясь редкими нотами кожи и трюфеля, перца и кардамона, чёрной смородины и ежевики.</p>', 'Подавать при температуре 17 - 18 С .<br>', 'massandra syrah 2015', 40, 66, 20000, 43, 4, 1, 12, 2015, 0.75, 1, 100, 'wines/June2020/массандра/Сира.png', 'vino-massandra-syra-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(253, 'Санджовезе', 2600, 'Вино Массандра Санджовезе красное сухое 2015', '', '<p>Тщательная сортировка ягод, применение холодной мацерации, брожение сусла на мезге и выдержка на мезге. Выдержка в барриках от 6 до 18 месяцев.<br></p>', '<p>Санджовезе потрясающе акцентирует вкус говяжьего стейка и жареной утки. Его подают к пастам с мясом и рыбой, томатным соусам и пицце, к острым сырам с плесенью, мягким сырам и копчёным сырам. Морепродукты, приготовленные на гриле, щедро приправленные пряными травами, составляют прекрасную пару этому вину, равно как и мясные, рыбные, овощные кулебяки и десерты.</p>', '<p>Вино шелковистое и богатое, полнотелое с живой кислотностью, настойчивыми танинами и землянично-вишнёвым ароматом. Сочное кисло-сладкое.</p>', 'Подавать при температуре 17 - 18С.<br>', 'massandra sandgoveze 2015', 40, 60, 20000, 43, 4, 1, 13, 2015, 0.75, 1, 100, 'wines/June2020/массандра/Санджовезе.png', 'vino-massandra-sandgoveze-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 5),
(254, 'Кефесия', 1400, 'Вино Валерий Захарьин Кефесия красное сухое 2014', '', '<p>Брожение сусла на мезге и выдержка на мезге. Выдержка в бутылке 6 месяцев после розлива. Кефесия - автохтонный, крымский сорт винограда.</p>', 'Сочетается с сочной телятиной, птицей, выдержанными сырами.<br>', 'Цвет вина рубиновый. Аромат ежевики, шелковицы, сливы. Вкус приятный с оттенками чёрных ягод, тостов, специй. <br>', 'Подавать при температуре 16 - 18 С .<br>', 'zaharin kefesia 2014', 18, 22, 13000, 36, 4, 1, 11, 2014, 0.75, 1, 82, 'wines/June2020/dopolnitelno/Кефесия.jpg', 'vino-zaharin-kefesia-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(255, 'Сары Пандас', 2600, 'Вино Массандра Сары Пандас белое сухое 2015', '', '<p>Применение холодной мацерации в течение суток, брожение при строго контролируемой температуре. Сорт Сары Пандас издавна культивируют в Крыму, что даёт основание считать его крымским сортом.</p>', '<p>Вино достаточно универсальное в плане гастрономических сочетаний. Лучшим сопровождением к этому вину станет черноморская камбала либо местные морепродукты под сливочным соусом.</p>', '<p>Цвет вина соломенно-жёлтый. Аромат раскрывается нежным сочетанием экзотических фруктов - ананаса, манго, папайи с лёгкими нотками цветущей акации, мёда и пряностей. Вкус - минеральный, хорошо сбалансированный, плотной текстуры, с тонами жёлтой черешни, алычи и абрикосов. Послевкусие с пикантной горчинкой мягко объединяет аромат и вкус.</p>', 'Подавать при температуре 10 - 12 С .<br>', 'Massandra sary pandas 2015', 40, 62, 20000, 43, 4, 2, 13, 2015, 0.75, 1, 100, 'wines/June2020/массандра/Сары Пандас.png', 'vino-Massandra-sary-pandas-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(256, 'Каберне Совиньон', 2600, 'Вино Массандра Каберне Совиньон красное сладкое 2015', '', '<p>Тщательная сортировка ягод, при брожении строгий контроль температуры, остановка брожения холодом. Остаточный сахар 110 г/дм.</p>', 'Вино прекрасно как аперитив, а также сочетается с десертами из ягод и мороженым.<br>', '<p>Вино насыщенного рубинового цвета с выразительным фруктовым ароматом спелой вишни и чёрных ягод. Вкус мягкий, богатый, с палитрой фруктово-ягодных оттенков: чёрной смородины, ежевики, чернослива и тонкой бисквитной нотой. Послевкусие долгое, обволакивающее с переплетающимися джемовыми нотками вишни и чернослива.</p>', 'Подавать при температуре 17 - 18 С .<br>', 'massandra Cabernet Sauvignon sweet 2015', 40, 20, 20000, 43, 4, 1, 9, 2015, 0.75, 10, 99, 'wines/June2020/массандра/Каберне Совиньон.png', 'vino-massanra-caberne-sladkoe-2015', '', '', 'ACTIVE', 1, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(258, 'Мускат', 1600, 'Вино Массандра Мускат белое сладкое 2015', '', '<p>Тщательная сортировка ягод, холодная мацерация в течение одних суток, медленное брожение при низкой температуре, остановка брожения холодом. Сахар 100 г/дм.</p>', 'Вино прекрасно как аперитив, сочетается с десертами из ягод и мороженым.<br>', '<p>Цвет светло-соломенный. Аромат выразительный, сортовой с оттенками цветов, экзотических фруктов и леденцовой нотой. Вкус гармоничный, нежный, с лёгкой цитронной нотой в послевкусии. Едва уловимое покалывание во вкусе обусловлено особенностями технологии.</p>', 'Подавать при температуре 10 - 12 С .<br>', 'massandra muscat sweet 2015', 40, 33, 20000, 43, 4, 2, 8, 2015, 0.75, 10, 100, 'wines/June2020/массандра/Мускат Жемчужное.png', 'vino-en/Massandra-muscat-sladkoe-2015', '', '', 'ACTIVE', 1, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(259, 'Мускат позднего сбора', 2800, 'Вино Массандра Мускат позднего сбора белое сладкое 2015', '', '<p>Тщательная сортировка ягод, холодная мацерация в течение одних суток, медленное брожение при низкой температуре, остановка брожения холодом. Сахар 200 г/дм.</p>', 'Вино прекрасно как аперитив, сочетается с десертами из ягод и мороженым.<br>', '<p>Цвет солнечно-золотистый с многообразием оттенков. Аромат яркий, сложный, мускатный, с нотами персикового джема, медовых сот, разнообразными оттенками пряностей и леденцовой карамели. Вкус нежный, насыщенный оттенками экзотических фруктов, медовых персиков и абрикосов, уравновешенный гармоничной кислотностью и приятным долгим цитронным послевкусием.</p>', 'Подавать при температуре 08 - 10 С .<br>', 'Massandra muscat sweet pozdy sbor 2015', 40, 33, 20000, 43, 4, 2, 10, 2015, 0.375, 10, 98, 'wines/June2020/массандра/Мускат позднего сбора.png', 'vino-Massandra-muscat-sladkoe-pozdy-sbor-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(260, 'Каберне Фран Резерв', 3500, 'Бельбек Каберне Фран Резерв красное сухое 2014', '', 'Выдержка в французском дубе 18 месяцев. Окончательное созревание вина происходит в бутылке, в течение 6 месяцев.<br>', 'Имеет плотную структуру, что придаёт прекрасное сочетание с различными блюдами из жаренного мяса, включая мясо диких животных. Идеально подходит к выдержанным сырам.<br>', '<p>Вино тёмно-бордового цвета с фиолетовым оттенком. Богатый и сложный аромат с нотками свежей листвы смородины и малины, молодого не острого перца. Вкусовые сочные ягодные оттенки комфортно сочетаются с лёгкой шершавостью гравия.<br></p>', 'Подавать при температуре 18 - 19 С . <br>', 'Belbek Cabernet Fran 2014', 34, 21, 400, 9, 4, 1, 14, 2014, 0.75, 1, 89, 'wines/June2020/бельбек/КабернеФранРезерв2014.jpg', 'vino-Belbek-Caberne-Fran-2014', 'Винодельня Сергей Бескоровайный и его Каберне Фран 2014', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 38),
(261, 'Мерло', 1500, 'Бельбек Бескоровайный Сергей Мерло красное сухое 2018', '', 'Выдержка в французском дубе 12 месяцев. Окончательное созревание вина происходит в бутылке, в течение 6 месяцев.<br>', 'Имеет хорошую и шелковистую структуру вкуса, прекрасно сочетается практически с любым меню. Мясо - начиная от холодной ветчины и заканчивая рёбрышками-гриль, овощное рагу, бобы, острые и полутвёрдые сыры, любая птица и дичь (особенно пернатая).<br>', '<p>Вино красного цвета с лёгким медным отсветом. Округлый аромат со спелой ежевикой, вишней и сливой в букете, с угадывающимися нотами сигар и старой выделанной кожи. Во вкусе преобладают те же сортовые оттенки, что и в аромате, а так же ноты ванили и горького шоколада.<br></p>', 'Подавать при температуре 18 - 19С. <br>', 'Belbek merlot 2018', 34, 31, 1500, 9, 4, 1, 13.9, 2018, 0.75, 1, 99, 'wines/June2020/belbek/belbek-merlo-2018.jpg', 'vino-Belbek-merlo-2018', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 37),
(262, 'Пино Нуар', 1500, 'Бельбек Пино Нуар красное сухое 2018', '', 'Выдержка в французском дубе 12 месяцев. Окончательное созревание вина происходит в бутылке, в течение 6 месяцев.<br>', 'Имея сбалансированную структуру с освежающим фруктово-минеральным вкусом, превосходно сочетается с красным мясом и дичью, пастой, сытными пирогами и плотной рыбой (типа тунец). Кроме того идеальная пара к некоторым сырам и блюдам Ближнего Востока и мароканской кухни.<br>', '<p>Вино рубиново-красного цвета с высокой степенью прозрачности. Интенсивный аромат с явно выраженными травянистыми тонами, красными ягодами, табака, черёмухи, белого перца и коры дерева. Вкус среднетелый, сдержанный, с хорошей кислотностью. Вкусовые тона свежих фруктов и чуть подвяленных ягод, тёрна, черноплодной рябины, гармонично сочетаются друг с другом. Послевкусие долгое ягодное, с пикантной еле заметной горчинкой. <br></p>', 'Подавать при температуре 18 - 19С. <br>', 'Belbek pinot noir 2018', 34, 47, 1200, 9, 4, 1, 13.8, 2018, 0.75, 1, 96, 'wines/June2020/belbek/belbek-pino-2017.jpg', 'vino-Belbek-pino-noir-2018', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 37),
(263, 'Мускат', 900, 'Бельбек Мускат белое сухое 2018', '', 'Ферментация проходит в ёмкостях из нержавеющей стали, при контролируемой температуре. Окончательное созревание вина происходит в бутылке, в течение 3 месяцев.<br>', 'Отлично возбуждает аппетит, удачное предложение для аперитива. Изумительно подчёркивает лёгкие блюда на основе сырых или приготовленных овощей, нежного холодного белого мяса и морских моллюсков.<br>', '<p>Вино соломенного цвета. Яркий, округлый аромат с нотками личи, акации, цитрусовых. Во вкусе преобладают те же сортовые оттенки, что и в аромате, с долгим цитрусовым послевкусием.<br></p>', 'Подавать при температуре 10 - 12С. <br>', 'Belbek muscat 2019', 34, 33, 1500, 9, 4, 2, 11.5, 2019, 0.75, 1, 89, 'wines/June2020/belbek/belbek-muscat-2018.jpg', 'vino-Belbek-muscat-2019', 'Бельбек - винодельня Сергея Бескоровайного в Крыму. Бельбек мускат - белое сухое вино.', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(264, 'Рислинг', 900, 'Бельбек Рислинг белое сухое 2019', '', 'Ферментация проходит в ёмкостях из нержавеющей стали, при контролируемой температуре. Окончательное созревание вина происходит в бутылке, в течение 3 месяцев.<br>', 'Имея относительно лёгкое тело и выразительную структуру, прекрасно сочетается с самыми разнообразными блюдами. Он великолепно подходит к белой рыбе и свинине. Также, способен устоять перед натиском пряных блюд тайской и китайской кухонь, и наконец, он может хорошо аккомпанировать даже очень солёным блюдам.<br>', '<p>Практически прозрачное вино, с лёгким соломенным отсветом. Аккуратный цветочный аромат с активными нотками зелёных фруктов и цитрусовых. Вкус минеральный, среднетелый, с ярко выраженной кислотностью и её равновесием между свежестью и сладостью.</p>', 'Подавать при температуре 07 - 09С. <br>', 'Belbek risling 2019', 34, 56, 1300, 9, 4, 2, 13.5, 2019, 0.75, 1, 80, 'wines/June2020/belbek/belbek-risling-2018.jpg', 'vino-Belbek-risling-2019', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(265, 'Авторский Купаж', 1000, 'Вино Усадьба Перовских Авторский купаж белое сухое 2016', '', '<p>Сбор винограда с лоз возрастом 35 лет. Ферментация и выдержка проходит в ёмкостях из нержавеющей стали, при контролируемой низкой температуре.<br></p><p><br></p>', 'Подходит к лёгким салатам, рыбным блюдам, морепродуктам, различным сырам.<br>', '<p>Вино светло-соломенного цвета. Насыщенный, сбалансированный вкус с оттенками жёлтых фруктов, мёда, абрикосов.<br></p>', 'Подавать при температуре 10 - 12 С . <br>', 'Perovsky avtorsky kupag 2016', 71, 4, 10000, 56, 4, 2, 13, 2016, 0.75, 1, 94, 'wines/June2020/dopolnitelno/Авторский купаж.jpg', 'vino-perovsky-avtorsky-kupag-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(266, 'Совиньон Блан', 900, 'Бельбек Сергей Бескоровайный Совиньон Блан белое сухое 2017', '', 'Ферментация проходит в ёмкостях из нержавеющей стали, при контролируемой температуре. Окончательное созревание вина происходит в бутылке, в течение 3 месяцев.<br>', 'Составит удачное сочетание с овощными блюдами, богатыми травами и специями. Уместно сопроводит блюда из птицы, а так же свиную отбивную. Любые морепродукты, в том числе крабы и моллюски, позволят составить идеальную гастрономическую пару с вином.<br>', 'Типичный сортовой светло-жёлтый цвет с лёгким солнечным отблеском. Элегантный аромат цветущего крымского персика и свежих листьев смородины, крыжовника. Вдалеке - сочные тропические фрукты. Вкус среднетельный, мягкий и маслянистый, с теме же сортовыми оттенками, что и в аромате.<br>', 'Подавать при температуре 10 - 12С. <br>', 'Belbek sovignon blan 2019', 34, 69, 1500, 9, 4, 2, 13, 2019, 0.75, 1, 77, 'wines/June2020/belbek/belbek-sovignon-2019.jpg', 'vino-Belbek-sovignon-blan-2019', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(267, 'Шардоне', 1500, 'Вино Бельбек Шардоне белое сухое 2015', '', 'Ферментация проходит в ёмкостях из нержавеющей стали, при контролируемой температуре. Окончательное созревание вина происходит в бутылке, в течение 3 месяцев.<br>', 'Имея нежную маслянистую структуру, часто подаётся к тёплым закускам. Прекрасно сочетается с горячими блюдами из рыбы и птицы, включая мясо пернатой дичи.<br>', '<p>Солнечно-золотистый цвет вина. Яркий, интенсивный ягодный аромат с явно выраженными нотами алычи и персика. В полнотелом вкусе угадываются цитрусовые, жёлтое яблоко, с заметным дынным оттенком. Долгое приятное послевкусие.</p>', 'Подавать при температуре 08 - 10 С . <br>', 'Belbek chardonnay 2015', 34, 81, 1500, 9, 4, 2, 14, 2015, 0.75, 1, 100, 'wines/June2020/бельбек/Шардоне2015.jpg', 'vino-Belbek-chardonnay-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(268, 'Розе', 900, 'Бельбек Розе розовое сухое 2019', '', 'Вино приготовлено по - белому методу из красных сортов винограда.<br>', 'Рекомендуется подавать к блюдам французской и азиатской кухни, к белому мясу, рыбе, свинине и мясным закускам, морепродуктам, мясу птицы, крем-супу, кролику под соусом и молодым сырам. <br>', '<p>Вино сочного розового цвета с клубнично-малиновым оттенком. Не сложный, но интересный ярко-ягодный аромат. Вкус освежающий с шелковистой текстурой, приятной кислотностью и элегантным и приятным послевкусием с фруктово-ягодными нюансами.<br></p>', 'Подавать при температуре 08 - 10С. <br>', 'Belbek rose 2019', 34, 20, 700, 9, 4, 3, 13.4, 2019, 0.75, 1, 93, 'wines/June2020/belbek/belbek-rose-2018.jpg', 'vino-Belbek-rose-2019', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(269, 'Мерло', 1600, 'Вино Усадьба Перовских Мерло красное сухое 2014', '', 'Выдержка в американском дубе 18 месяцев. Окончательное созревание вина происходит в бутылке, в течение 6 месяцев.<br>', 'Подавать к стейкам, дичи, выдержанным сырам.<br>', '<p>Вино насыщенного рубинового цвета. В аромате чёрные ягоды и спелая вишня. Лёгкий, сбалансированный вкус с преобладанием тонов вишни и округлыми танинами. Продолжительное послевкусие с джемовыми нотками.<br></p>', 'Подавать при температуре 16 - 18 С . <br>', 'Perovsky merlot 2014', 71, 31, 10000, 56, 4, 1, 12.5, 2014, 0.75, 1, 100, 'wines/June2020/dopolnitelno/Мерло лимитированая серия.jpg', 'vino-Perovsky-merlo-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 38),
(270, 'Алиготе', 1300, 'Вино Бельбек Алиготе белое сухое 2015', '', 'Ферментация проходит в ёмкостях из нержавеющей стали, при контролируемой температуре. Окончательное созревание вина происходит в бутылке, в течение 3 месяцев.<br>', 'Обладает фантастическими гастрономическими возможностями. Отлично сочетается с любыми блюдами из морепродуктов, домашней птицы, нежной телятины. Подчёркивает вкус не острых сыров, овощей-гриль и даже пиццы.<br>', '<p>Блестящий, светло-жёлтый цвет с малахитово-зеленоватым оттенком. Выделяется индивидуальным и самобытным ароматом с преобладанием светлых фруктов, айвы и свежих луговых трав. Ярко выраженный вкус соответсвующий аромату, с гармоничной кислотностью и тонкой горчинкой лайма.</p>', 'Подавать при температуре 10 - 12 С . <br>', 'Belbek aligote 2015', 34, 4, 900, 9, 4, 2, 14, 2015, 0.75, 1, 100, 'wines/June2020/бельбек/мускат2016.jpg', 'vino-Belbek-aligote-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(271, 'Рислинг Резерв', 1200, 'Вино Усадьба Перовских Рислинг Резерв белое сухое 2016', '', 'Ферментация проходит в ёмкостях из нержавеющей стали, при контролируемой температуре. Выдержка во французском дубе 2 месяца.<br>', 'Сопровождает рыбные блюда, морепродукты, азиатскую кухню, лёгкие салаты и различные сыры.<br>', '<p>Вино насыщенного золотистого цвета с зеленоватыми оттенками. Пряный аромат с цветочными нотками и оттенками ванили. Богатый, насыщенный вкус с нотками жёлтых фруктов. Сбалансированная, мягкая кислотность.</p>', 'Подавать при температуре 10 - 12 С . <br>', 'Perovsky risling 2016', 71, 56, 13000, 56, 4, 2, 13, 2016, 0.75, 1, 100, 'wines/June2020/dopolnitelno/Рислинг.jpg', 'vino-Perovsky-risling-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(272, 'Фран', 1500, 'Бельбек Фран 2014', '', 'Выдержка в французском дубе 12 месяцев. Окончательное созревание вина происходит в бутылке, в течение 6 месяцев.<br>', 'Гармонично с широким спектром блюд благодаря сбалансированной кислотности и среднему содержанию танинов. Идеально гармонирует с блюдами, в которых есть травы и специи. Если мясо, то это жареная свинина, рагу, карри, индейка с клюквой, ягнёнок. Если овощи, то это чёрная чечевица, красная фасоль, перец на гриле, грибы, томаты, баклажаны, лук порей, шпинат, руккола.<br>', '<p>Вино обладает глубоким вишнёвым цветом с чернильным отливом. Полный, густой и многогранный аромат чёрной смородины, ежевики и чёрного перца. Очень насыщенный вкус, с полными суховатыми танинами, в котором раскрываются древесные тона, специи и чёрные ягоды.<br></p>', 'Подавать при температуре 18 - 19 С . <br>', 'Belbek Fran 2014', 34, 21, 1500, 9, 4, 1, 14, 2014, 0.75, 1, 100, 'wines/June2020/бельбек/Фран2014.jpg', 'vino-Belbek-Fran-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 37),
(274, 'Красностоп', 4500, 'Бюрнье Красностоп красное сухое 2010', '', 'Вино изготовлено из отборного винограда. Выдержка 18 месяцев в бочках из французского дуба.<br>', 'Прекрасно сопровождает дичь и баранину. Перед употреблением необходимо декантировать.<br>', '<p>Цвет глубокий, тёмно-рубиновый. Богатый вкус с хорошей сахаристостью и ароматами спелых фруктов, тонами изюма, чернослива.<br></p>', 'Подавать при температуре 15 - 17С.<br>', 'Burnie krasnostop 2010', 43, 25, 10000, 10, 2, 1, 14, 2010, 0.75, 1, 92, 'wines/June2020/бюрнье/Красностоп 2010.png', 'vino-Burnie-krasnostop-2010', 'Бюрнье винодельня и ее вина Красностоп, Каберне Фран, Люблю, Мерло', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 33),
(275, 'Каберне Фран', 1300, 'Вино Бюрнье Каберне Фран красное сухое 2011', '', 'Вино изготовлено из отборного винограда. Выдержка в эмалированных ёмкостях.<br>', '<p>Подходит к нежирному нежному красному мясу и птице с ягодным соусом, а также к овощам и сыру.</p><p> Перед употреблением необходимо декантировать.<br></p>', '<p>Красивый цвет тёмной черешни. Мягкое, бархатистое вино с хорошей полнотой и средней терпкостью. Фруктовый букет с деликатными тонами красной сливы, малины, фиалки, листьев чёрной смородины.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Burnie fran 2013', 43, 21, 10000, 10, 2, 1, 14, 2011, 0.75, 1, 97, 'wines/June2020/бюрнье/Каберне Фран 2011.png', 'vino-Burnie-fran-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 34),
(276, 'Мерло', 900, 'Вино Бюрнье Мерло красное сухое 2011', '', 'Вино изготовлено из отборного винограда. Выдержка в ёмкостях из нержавеющей стали.<br>', 'Идеально подходит к блюдам из красного и белого мяса, а также к острым сырам. Перед употреблением необходимо декантировать.<br>', '<p>Цвет тёмно-красный. Полное, гибкое вино с присутсвием танинов и с хорошей структурой. Во вкусе присутствуют ароматы вишни и жареных зёрен какао. Имеет ярко выраженнный сортовой вкус.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Burnie merlot 2011', 43, 31, 10000, 10, 2, 1, 14, 2011, 0.75, 1, 95, 'wines/June2020/бюрнье/Мерло 2011.png', 'vino-burnie-merlo-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(277, 'Вионье', 1100, 'Вино Бюрнье Вионье белое сухое 2013', '', 'Вино изготовлено из отборного винограда. Выдержка в ёмкостях из нержавеющей стали.<br>', 'Идеально подходит к блюдам из домашней птицы, морской рыбы, а также к мясу краба, козьему сыру.<br>', '<p>Цвет глубокий, золотисто-жёлтый. Полное, богатое вино с уникальным ароматом сухофруктов, нежными тонами абрикосов, мёда, белой акации.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'Burnie vionier 2013', 43, 12, 10000, 10, 2, 2, 14, 2013, 0.75, 1, 100, 'wines/June2020/бюрнье/Вионье 2013.png', 'vino-Burnie-vionier-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(278, 'Шардоне', 1100, 'Вино Бюрнье Шардоне белое сухое 2013', '', 'Вино изготовлено из отборного винограда. Выдержка в ёмкостях из нержавеющей стали.<br>', 'Идеально сочетается с морепродуктами, морской рыбой, домашней птицей, спаржей.<br>', '<p>Вино золотистого цвета. Гармоничное, хорошо сбалансированное, сложное, с фруктово-цветочными оттенками мёда, персика, груши, ананаса, грейпфрута, цветка апельсина и белой акации. Приятное длительное послевкусие.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'Burnie chardonnay 2013', 43, 81, 15000, 10, 2, 2, 14, 2013, 0.75, 1, 100, 'wines/June2020/бюрнье/Шардоне 2013.png', 'vino-Burnie-chardonnay-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(279, 'Люблю', 750, 'Вино Бюрнье Люблю белое сухое 2011', '', 'Вино изготовлено из отборного винограда. Выдержка в ёмкостях из нержавеющей стали.<br>', 'Идеально может сопровождать морскую рыбу, чёрную икру, фуа-гра, птицу, спаржу.<br>', '<p>Красивый золотистый цвет. Цветочный букет с фруктовыми и деликатными медовыми тонами. Вино богатое, насыщенное, хорошо сбалансированное, имеет минеральные оттенками.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'Burnie lublu 2011', 43, 81, 15000, 10, 2, 2, 13.5, 2011, 0.75, 1, 99, 'wines/June2020/бюрнье/Люблю 2011.png', 'vino-Burnie-lublu-2011', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(280, 'Для Тебя (Шардоне-Пино Блан)', 750, 'Вино Бюрнье для тебя белое сухое 2013', '', 'Вино изготовлено из отборного винограда. Выдержка в ёмкостях из нержавеющей стали.<br>', 'Идеально сочетается с морепродуктами, морской рыбой, домашней птицей, спаржей.<br>', '<p>Вино золотистого цвета. Гармоничное, хорошо сбалансированное, сложное, с фруктово-цветочными оттенками мёда, персика, груши, ананаса, грейпфрута, цветка апельсина и белой акации. Приятное длительное послевкусие.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'Burnie dlya tebya 2013', 43, 81, 15000, 10, 2, 2, 14, 2013, 0.75, 1, 100, 'wines/June2020/бюрнье/Для тебя.png', 'vino-burnie-dlya-tebya-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(281, 'Для Меня (Каберне-Мерло)', 750, 'Вино Бюрнье Для Меня красное сухое 2013', '', 'Вино изготовлено из отборного винограда. Выдержка в ёмкостях из нержавеющей стали.<br>', 'Идеально сочетается с морепродуктами, морской рыбой, домашней птицей, спаржей.<br>', '<p>Вино золотистого цвета. Гармоничное, хорошо сбалансированное, сложное, с фруктово-цветочными оттенками мёда, персика, груши, ананаса, грейпфрута, цветка апельсина и белой акации. Приятное длительное послевкусие.<br></p>', 'Подавать при температуре 10 - 12 С .<br>', 'Burnie dlya menya 2013', 43, 31, 15000, 10, 2, 1, 14, 2013, 0.75, 1, 99, 'wines/June2020/бюрнье/Для меня.png', 'vino-burnie-dlya-menya-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(282, 'Кокур', 450, 'Вино Солнечная Долина Кокур белое сухое 2015', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке.</p>', 'Идеально к греческому салату, крабам и лангустинам, осетрине на зеркале, сёмге на гриле, лазанье и пасте под белым соусом, сашими и суши, сырам с промытой корочкой.<br>', '<p>Вино бледно-соломенного цвета. С ароматами майского мёда, полевых цветов, мандариновой цедры и лимонной полыни. Вкус яркий, выразительный, с лёгкой горчинкой и освежающим финалом.</p>', 'Подавать при температуре 10 - 12 С.<br>', 'Solnechnay dolina kokur 2015', 44, 23, 20000, 52, 4, 2, 13, 2015, 0.75, 1, 91, 'wines/June2020/сол долина/КОКУР.png', 'vino-Solnechnay-dolina-kokur-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(283, 'Меганом белое', 450, 'Вино Солнечная Долина Меганом белое сухое 2016', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке.</p>', 'Подойдёт к салатам с тёплой печенью, холодным закускам из рыбы и сыра, жюльену с грибами, стейками из красной рыбы, жареной кефали, курице с черносливом, тушёному катрану, паэлье и пасте.<br>', '<p>Вино бледно-соломенного цвета, с зеленоватыми бликами. Нежный аромат с нотками утренних цветов, сухих трав и минеральными нюансами. В мягком вкусе ощутим вкус жёлтого грейпфрута и горного мёда.</p>', 'Подавать при температуре 10 - 12 С.<br>', 'Solnechnay dolina meganom white 2016', 44, 23, 20000, 52, 4, 2, 13, 2016, 0.75, 1, 96, 'wines/June2020/сол долина/Меганом Алиготе.png', 'vino-Solnechnay-dolina-meganom-beloe-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(284, 'Совиньон', 450, 'Вино Солнечная Долина Совиньон  белое сухое 2017', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке.</p>', 'Прекрасный аперитив. Подойдёт к салатам, устрицам, улиткам по-буогундски, кутабам по-мегрельски, строганине из муксуна и нельмы, лососине на гриле и козьим сырам.<br>', '<p>Вино светло-лимонного цвета. Деликатный аромат с оттенками лайма, почек смородины, зелёного перца и лёгкие табачные нюансы. Вкус свежий, дразнящий, с нотами груши дюшес и тонким послевкусием.</p>', 'Подавать при температуре 10 - 12 С.<br>', 'S dolina sovignon 2017', 44, 67, 20000, 52, 4, 2, 13, 2017, 0.75, 1, 100, 'wines/June2020/сол долина/СОВИНЬОН.png', 'vino-S-dolina-sovignon-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(285, 'Солдайя', 500, 'Вино Солнечная Долина Солдайя белое сухое 2017', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке. Необычное, сложное вино приготовленное из автохтонных крымских сортов. Возраст лозы 30 - 35 лет.</p>', 'Подойдёт к салатам с тёплой печенью, холодным закускам из рыбы и сыра, жюльену с грибами, стейками из красной рыбы, жареной кефали, курице с черносливом, тушёному катрану, паэлье и пасте.<br>', '<p>Вино бледно-соломенного цвета, с зеленоватыми бликами. Нежный аромат с нотками утренних цветов, сухих трав и минеральными нюансами. В мягком вкусе ощутим вкус жёлтого грейпфрута и горного мёда.</p>', 'Подавать при температуре 10 - 12 С.<br>', 'S dolina soldaya 2017', 44, 70, 20000, 52, 4, 2, 13, 2017, 0.75, 1, 99, 'wines/June2020/сол долина/СОЛДАЙЯ.png', 'vino-S-dolina-soldaya-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(286, 'Меганом красное', 550, 'Меганом Солнечная Долина красное сухое 2017', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке.</p>', 'Подойдёт к холодным и горячим мясным закускам, шашлыку из баранины, долме с мацони, пожарской котлете, а также молодому адыгейскому сыру со свежей зеленью.<br>', '<p>Вино насыщенного рубинового цвета. Аромат богат оттенками спелой вишни, черноплодной рябины, смородины, фруктового йогрута и сафьяна. Вкус свежий, прекрасно гармонирующий с ароматом, экстрактивный, бархатистый, с мягкими гранатовыми танинами, кофейно-ванильными тонами. Послевкусие длительное и пикантное.</p>', 'Подавать при температуре 14 - 16С.<br>', 'Solnechnay dolina meganom red 2017', 44, 8, 20000, 52, 4, 1, 13, 2017, 0.75, 1, 93, 'wines/June2020/сол долина/Меганом Бастаро.png', 'vino-Solnechnay-dolina-meganom-krasnoe-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(287, 'Каберне', 500, 'Вино Солнечная Долина Каберне красное сухое 2016', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке.</p>', 'Подойдёт к говяжьему стейку, бараньим рёбрышкам на гриле, утке, фазану с кизиловым соусом, а также к пармской ветчине с дыней и зрелым сырам.<br>', '<p>Вино рубинового цвета с лиловым отблеском. Аромат яркий, сортовой, с оттенками ежевики, чёрной смородины и лесной фиалки. Вкус экстрактивный, барахатистый, с умеренной танинностью, насыщенными фруктовыми нотками и острыми нюансами чёрного перца. Послевкусие мягкое, пикантное, долгое.</p>', 'Подавать при температуре 14 - 16 С.<br>', 'Solnechnay dolina cabernet 2016', 44, 20, 20000, 52, 4, 1, 13, 2016, 0.75, 1, 100, 'wines/June2020/сол долина/КАБЕРНЕ.png', 'vino-Solnechnay-dolina-caberne-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(288, 'Каберне Совиньон', 700, 'Вино Усадьба Перовских Каберне Совиньон красное сухое 2014', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее частичная выдержка в бочках из американского дуба.</p>', 'Подавать к холодным мясным деликатесам, тушёной говядине с зеленью, свинине на гриле.<br>', '<p>Вино элегантного пурпурного цвета. В аромате преобладают тона чёрных фруктов, ежевики, чёрной смородины. Во вкусе сбалансированное, с хорошей структурой, дружелюбными танинами и с продолжительным послевкусием, с нюансами черносмородинового джема и пряностей. </p>', 'Подавать при температуре 14 - 16 С.<br>', 'Perovsky cabernet 2014', 71, 20, 40000, 56, 4, 1, 13, 2014, 0.75, 1, 100, 'wines/June2020/dopolnitelno/Каберне Совиньон.jpg', 'vino-Perovsky-caberne-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 1),
(289, 'Пти Вердо', 600, 'Вино Солнечная Долина Пти Вердо красное сухое 2017', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в бутылке.</p>', 'Сопровождение к бефстроганову, стейку, тёплой фуа-гра с брусничным джемом, жареному стейку, а также к хамону и зрелым сырам.<br>', '<p>Вино густого гранатового цвета с лиловыми бликами. Аромат вина щедрый, открытый. В нём преобладают ноты тёрна, смоквы, лесной ежевики и пиона. Вкус плотный, бархатистый, с умеренной танинностью, насыщенными нотами войлочной вищни, с акцентом на кислотность. Послевкусие мягкое, долгое, с лёгкими специями.</p>', 'Подавать при температуре 14 - 16 С.<br>', 'Solnechnay dolina pti verdo 2017', 44, 51, 20000, 52, 4, 1, 13, 2017, 0.75, 1, 97, 'wines/June2020/сол долина/ПТИ ВЕРДО.png', 'vino-Solnechnay-dolina-pti-verdo-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 2),
(290, 'Меганом резерв', 900, 'Меганом резерв Солнечная Долина красное сухое 2017', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в дубе, в течение 12 месяцев.<br></p>', '<p>Подойдёт к стейку, шашлыку из баранины, а также к выдержанному сыру .<br></p>', '<p>Вино насыщенного рубинового цвета. Аромат богат оттенками спелой вишни, черноплодной рябины, смородины, фруктового йогрута и сафьяна. Вкус свежий, прекрасно гармонирующий с ароматом, экстрактивный, бархатистый, с мягкими гранатовыми танинами, кофейно-ванильными тонами. Послевкусие длительное и пикантное.</p>', '<p>Подавать при температуре 14 - 16С.</p>', 'Solnechnay dolina meganom rezerv red 2014', 44, 8, 20000, 52, 4, 1, 13, 2017, 0.75, 1, 58, 'wines/June2020/сол долина/20 Меганом выдерж кр.png', 'vino-Solnechnay-dolina-meganom-rezerv-krasnoe-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(291, 'Солнечная Долина', 800, 'Вино Солнечная Долина Солнечная Долина выдержанное белое десертное 2000', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в дубовых бочках не менее трёх лет. Остаточный сахар 160 г/дм.</p>', 'Идеально к тирамису, к выпечке без шоколада, лимонному сорбету, фруктовому салату, солёным фисташкам, фуа-гра, мороженому и голубым сырам.<br>', '<p>Вино янтарно-золотистого цвета. Букет сложный, медово-цветочный, с богатой палитрой ароматов экзотических фруктов и ноткой мускатного ореха и пряностей. Вкус насыщенный, мягкий, щедрый, с привкусом вяленой дыни, инжира, цукатов, персика, шиповника, айвы, с фруктовой кислотностью. Послевкусие долгое, приятное, обволакивающее.</p>', 'Подавать при температуре 16 - 18 С.<br>', 'Solnechnay dolina white desertnoe 2000', 44, 23, 20000, 52, 4, 2, 16, 2000, 0.75, 4, 99, 'wines/June2020/сол долина/11 СД мароч.png', 'vino-Solnechnay-dolina-white-desertnoe-2000', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(293, 'Портвейн Крымский', 750, 'Вино Солнечная Долина Портвейн Крымский выдержанное белое 2010', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в дубовых бочках не менее трёх лет. Остаточный сахар 95 г/дм.</p>', 'Идеально к тирамису, горячей выпечке без шоколада, фруктовому салату, гусиному фуа-гра, цукатам и голубым сырам.<br>', '<p>Вино янтарно-золотистого цвета. Имеет соблазнительный букет с тонами кайсы, цукатов, ванили, орехов и нотками рансьо. Вкус насыщенный, полный, с пряно-медовыми тонами, оттенками сухофруктов и ржаной корочки. Послевкусие длительное с хорошо выраженными тонами выдержки.</p>', 'Подавать при температуре 16 - 18 С.<br>', 'Solnechnay dolina portwein 2010', 44, 23, 20000, 52, 4, 2, 17.5, 2010, 0.75, 4, 100, 'wines/June2020/сол долина/13 Порт Крымский.png', 'vino-Solnechnay-dolina-portwein-2010', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(294, 'Чёрный Доктор', 1700, 'Вино Солнечная Долина Чёрный доктор выдержанное красное десертное 2007', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в дубовых бочках не менее двух лет. Остаточный сахар 160 г/дм.</p>', 'Сочетается с горьким шоколадом, профитролями, фруктовым салатом, ванильным мороженым, а также с фисташками и голубыми сырами. Вино прекрасно дополняет сигары.<br>', '<p>Вино гранатового цвета с рубиновыми бликами. Букет сложный и предопределяет его полноту вкуса. Яркие тона чернослива, шоколада, замши, которые дополняются оттенками ванили, кизила и терна. Вкус полный, бархатистый, с выраженными оттенками сушёной груши, молочных сливок, сафьяна цареградской шелковицы. Вино с пикантной терпкостью, но благодаря двухлетней выдержке в дубе, мягкое и слаженное. В послевкусии присутствуют оттенки какао и сливок. </p>', 'Подавать при температуре 16 - 18 С.<br>', 'Solnechnay dolina black doctor 2007', 44, 83, 20000, 52, 4, 1, 16, 2006, 0.75, 4, 98, 'wines/June2020/сол долина/7 Чер Док.png', 'vino-Solnechnay-dolina-black-doctor-2007', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(295, 'Чёрный Полковник', 750, 'Вино Солнечная Долина Чёрный Полковник выдержанное красное 2007', '', '<p>Ферментация в ёмкостях из нержавеющей стали. Далее выдержка в дубовых бочках не менее трёх лет. Остаточный сахар 110 г/дм.</p>', 'Идеально с молочным шоколадом, горячей выпечкой, фруктовым салатом, миндальным сорбетом, а также с фисташками и голубым сыром.<br>', '<p>Вино густого тёмно-рубинового цвета. Букет сложный с тонами молочного ириса, шоколада, чернослива и кофе мокко. Вкус экстрактивный, насыщенный, полный, с тонами молочного шоколада. Интенсивное, приятное, очень долгое тёплое послевкусие. </p>', 'Подавать при температуре 16 - 18 С.<br>', 'Solnechnay dolina black polkovnik 2007', 44, 83, 20000, 52, 4, 1, 17.5, 2007, 0.75, 4, 97, 'wines/June2020/сол долина/9 чер полк.png', 'vino-Solnechnay-dolina-black-polkovnik-2007', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(296, 'Кагор', 600, 'Вино Солнечная Долина Кагор Солнечной Долины красное десертное 2015', '', '<p>Ликёрное вино производится по традиционной технологии. Сахар 170 г/дм.<br></p>', 'Подавать к шоколаду, выпечке, фруктовому салату, солёным фисташкам.<br>', '<p>Вино интенсивного тёмно-рубинового цвета. В букете присутствуют тона чернослива, изюма, смоквы. Пикантность придают ноты горького шоколада и дымка. Маслянистый, сочный, обволакивающий вкус оставляет сладковато-пряное послевкусие с оттенками джема и чёрной смородины.</p>', 'Подавать при температуре 16 - 18 С.<br>', 'Solnechay dolina kagor 2015', 44, 8, 20000, 52, 4, 1, 16, 2015, 0.75, 4, 100, 'wines/June2020/сол долина/15 Кагор.png', 'vino-Solnechay-dolina-kagor-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 7),
(297, 'Красностоп Резерв', 750, 'Вино Кубань-Вино Красностоп Резерв красное сухое 2015', '', 'Вино изготовлено из отборного винограда, брожение сусла на мезге, выдержка в французском и американском дубе 12 месяцев.<br>', '<p>Подойдёт к овощам на гриле, утке с яблоками, картофельной запеканке.</p>', '<p>Цвет вина тёмно-рубиновый с гранатовым оттенком. Аромат сложный, с гармоничным сочетание фруктовых тонов. Вкус полный, свежий, экстрактивный с тонами чёрной смородины и чернослива. Послевкусие приятное и долгое.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Kuban-Vino Krasnostop rezerv 2015', 48, 24, 40000, 41, 2, 1, 12, 2015, 0.75, 1, 99, 'wines/June2020/кубань вино/ШТР Красностоп Сухое Красное 2015 0,75л.jpg', 'vino-Kuban-Vino-Krasnostop-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(298, 'Мерло Резерв', 750, 'Вино Кубань-Вино Мерло Резерв красное сухое 2015', '', 'Вино изготовлено из отборного винограда, брожение сусла на мезге, выдержка в дубе 12 месяцев.<br>', '<p>Идеально для блюд из утки и гуся. Прекрасно сочетается с любыми видами мяса, а также острыми сырами и хорошим хлебом.</p>', '<p>Цвет вина от красного до рубиново-красного. В аромате преобладание красных фруктов (вишня, кизил) в сочетании со сливочными тонами подчёркунутые благородным деревом. Вкус мягкий с округлыми сладкими танинами и долгим приятным послевкусием.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Kuban-Vino Merlot rezerv 2015', 48, 31, 40000, 41, 2, 1, 12, 2015, 0.75, 1, 100, 'wines/June2020/кубань вино/ШТР Мерло Сухое Красное 2015 0,75л.jpg', 'vino-Kuban-Vino-Merlo-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(299, 'Саперави Резерв', 750, 'Вино Кубань-Вино Саперави шато Тамань Резерв красное сухое 2015', '', 'Вино изготовлено из отборного винограда, брожение сусла на мезге, выдержка в дубе 12 месяцев.<br>', '<p>Неизменный спутник кавказской кухни, а также любых жирных мясных блюд, в том числе шашлыка и плова.</p>', '<p>Цвет вина от рубинового до тёмно-рубинового с гранатовым оттенком. Оригинален своим неповторимым сочетанием чёрных фруктов и ягод с сырно-молочными тонами, оттенками хорошо выделанной кожи и тёплого дерева. Вкус приятно терпкий, насыщенный, глубокий с развивающимся долгим послевкусием.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Kuban-Vino Saperavi rezerv 2015', 48, 61, 40000, 41, 2, 1, 12, 2015, 0.75, 1, 99, 'wines/June2020/кубань вино/ШТР Саперави Сухое Красное 2015 0,75л.jpg', 'vino-Kuban-Vino-Saperavi-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(300, 'Премьер Руж Резерв', 750, 'Вино Кубань-Вино Премьер Руж Шато Тамань Резерв красное сухое 2015', '', 'Премьер Руж - это союз международных и автохтонных сортов винограда. Вино изготовлено из отборного винограда, брожение сусла на мезге, выдержка в дубе 12 месяцев.<br>', 'Прекрасно дополнит блюда из птицы, мяса, сырное ассорти.<br>', '<p>Цвет тёмно-красный с гранатовым оттенком и рубиновыми искрами. Аромат насыщен тонами красных и чёрных фруктов на фоне благородного зрелого дуба с оттенком сливочного крема. Вкус полный, бархатистый, со сладкими гармоничными танинами, приятно терпкий и долгий.<br></p>', 'Подавать при температуре 14 - 16 С .<br>', 'Kuban-Vino Premier rouge rezerv 2015', 48, 31, 40000, 41, 2, 1, 12, 2015, 0.75, 1, 100, 'wines/June2020/кубань вино/ШТР Премьер Руж Сухое Красное 2015 0,75л.jpg', 'vino-Kuban-Vino-Premier-rouge-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(301, 'Мерло Семигорье Резерв', 2300, 'Вино Вилла Виктория Мерло Семигорье Резерв красное сухое 2015', '', 'Вино изготовлено из отборного винограда, 20 дней ферментации на мезге в стали, выдержка в кавказском дубе 6 месяцев, бутылочная выдержка 8 месяцев.<br>', '<p>Подавать к мясным блюдам на костре, грилю, шашлыкам, купатам из баранины. Вино требует аэрации.</p>', 'Вино глубокого рубинового цвета с лёгкими фиолетовыми тонами. Яркий аромат красных и чёрных ягод, в сочетании с ванильными нотками и нюансами кофе, специй. Во вкусе вино сбалансированное, сочное, яркое с прекрасной кислотностью. Полнотелое, с крепкой танинной структурой и нюансами табака. Послевкусие продолжительное, фруктовое с лёгкой пикантной горчинкой.<br>', 'Подавать при температуре 17 С .<br>', 'Villa Victoria Merlot rezerv 2015', 9, 31, 3000, 11, 2, 1, 14.5, 2015, 0.75, 1, 99, 'wines/June2020/dopolnitelno/Мерло Семигорье Резерв.jpg', 'vino-Villa-Victoria-Merlo-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 15),
(302, 'Премьер Блан Резерв', 750, 'Вино Кубань-Вино Премьер Блан Шато Тамань Резерв белое сухое 2015', '', 'Премьер Руж - это союз Шардоне и Рислинга. Вино изготовлено из отборного винограда, настой сусла на мезге в прессах с предварительным охлаждением, выдержка в дубе 12 месяцев.<br>', 'Выгодно подчеркнёт зелёные салаты и салаты с морепродуктами, канапе с креветками и сёмгой, сёмгу на пару с овощами.<br>', '<p>Цвет от светло-соломенного до соломенного с зеленоватым оттенком. В аромате гармоничное сочетание луговых трав и спелых фруктов, дополненное лёгким тоном дерева. Вкус приятно свежий, полный с долгим развивающимся послевкусием.<br></p>', 'Подавать при температуре 12 - 14 С .<br>', 'Kuban-Vino Premier blanc rezerv 2015', 48, 81, 40000, 41, 2, 2, 12, 2015, 0.75, 1, 100, 'wines/June2020/кубань вино/ШТР Премьер Блан Сухое Белое 2015 0,75л.jpg', 'vino-Kuban-Vino-Premier-blan-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(303, 'Рислинг Резерв', 750, 'Вино Кубань-Вино Рислинг Шато Тамань Резерв белое сухое 2015', '', 'Вино изготовлено из отборного винограда, настой сусла на мезге с предварительным охлаждением, брожение при контролируемой температуре 16 - 18С. Выдержка в дубе 12 месяцев.<br>', 'Придаст особое звучание горячим рыбным блюдам и коктейлям из морепродуктов. Сочетается с блюдами из белого мяса и потрохов.<br>', '<p>Цвет от светло-соломенного до соломенного. Букет насыщен фруктовыми нотами, гармонично сочетающимися с благородным дубом. Вкус полный, гармонирующий с букетом.<br></p>', 'Подавать при температуре 12 - 14 С .<br>', 'Kuban-Vino Risling rezerv 2015', 48, 56, 40000, 41, 2, 2, 12, 2015, 0.75, 1, 99, 'wines/June2020/кубань вино/ШТР Рислинг Сухое Белое 2015 0,75л.jpg', 'vino-Kuban-Vino-Risling-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(304, 'Шардоне Резерв', 750, 'Вино Кубань-Вино Шардоне Шато Тамань Резерв белое сухое 2015', '', 'Вино изготовлено из отборного винограда, настой сусла на мезге с предварительным охлаждением, брожение при контролируемой температуре 16 - 18С. Выдержка в дубе 12 месяцев.<br>', 'Подчеркнёт вкус блюд из телятины, морской рыбы и морепродуктов. Незаменимо для блюд с приправой карри.<br>', '<p>Цвет от светло-соломенного до золотистого. В аромате свежие фруктовые ноты, гаррмонично сочетающиеся с благородным дубом. Вкус полный, мягкий, с пикантной бархатистой терпкостью и свежей кислотностью.<br></p>', 'Подавать при температуре 12 - 14 С .<br>', 'Kuban-Vino Chardonnay rezerv 2015', 48, 81, 40000, 41, 2, 2, 12, 2015, 0.75, 1, 100, 'wines/June2020/кубань вино/ШТР Шардоне Сухое Белое 2015 0,75л.jpg', 'vino-Kuban-Vino-Chardonnay-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10),
(305, 'Шато Тамань Резерв', 550, 'Кубань-Вино Шато Тамань Резерв белое экстра брют выдержанное 2015', '', 'Вино приготовлено из отборного винограда, по классической технологии вторичного брожения в бутылке с выдержкой не менее 9 месяцев.<br>', 'Наиболее изысканный вариант сопровождения: икра и миндальные орехи.<br>', '<p>Цвет светло-соломенного с оттенками от зеленоватых до золотистых. Аромат развитый с выраженными тонами луговых цветов, цветов подсолнечника, подчёркнутый цитронной свежестью. Вкус изысканный, поражает своей мягкостью и свежестью, лёгкой пряной терпкостью и долгим послевкусием. <br></p>', 'Подавать при температуре 06 - 08С.<br>', 'Kuban-Vino chato taman white extra brut rezerv 2015', 48, 56, 40000, 41, 2, 2, 12, 2015, 0.75, 11, 100, 'wines/June2020/кубань вино/ШТР Игристое Белое Экстра Брют 2015 0,75л.jpg', 'vino-Kuban-Vino-chato-taman-beloe-extra-brut-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(306, 'Шато Тамань Резерв', 550, 'Кубань-Вино Шато Тамань Резерв розовое экстра брют выдержанное 2015', '', 'Вино приготовлено из отборного винограда, по классической технологии вторичного брожения в бутылке с выдержкой не менее 9 месяцев.<br>', 'Идеально для фуршетов с лёгкими закусками. Прекрасно сопровождает морепродукты.<br>', '<p>Цвет розовый, от малинового до брусничного оттенка. Аромат яркий, слаженный, сочетающий в себе ягодную доминанту и сливочно-молочные тона, подчёркнутые тонами шампанизации. Вкус насыщенный и изысканный с долгим, развивающимся послевкусием и приятной терпкостью.  <br></p>', 'Подавать при температуре 06 - 08 С .<br>', 'Kuban-Vino chato taman roze extra brut rezerv 2015', 48, 56, 40000, 41, 2, 3, 12, 2015, 0.75, 11, 100, 'wines/June2020/кубань вино/ШТР Игристое Розовое Экстра Брют 2015 0,75л.jpg', 'vino-Kuban-Vino-chato-taman-roze-extra-brut-rezerv-2015', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 45),
(307, 'Шато Тамань Молодое', 350, 'Кубань-Вино Шато Тамань Молодое красное сухое 2016', '', 'Брожение целыми гроздями винограда Саперави в ёмкостях для производства Божоле. Проведение яблочно-молочного брожения после окончания спиртового брожения.<br>', 'Хорошо себя показывает в тандеме с мясными закусками, копчёностями и сёмгой в сливочно-икорном соусе.<br>', '<p>Цвет вина ярко рубиновый. Аромат свежий, фруктовый. Вкус мягкий, с приятной бархатистой терпкостью, гармоничный.<br></p>', 'Подавать при температуре 09 - 11 С .<br>', 'Kuban-Vino Saperavi molodoe 2016', 48, 61, 40000, 41, 2, 1, 12, 2016, 0.75, 1, 100, 'wines/June2020/кубань вино/ШТ Молодое Красное Сухое 2016 0,75л.jpg', 'vino-Kuban-Vino-Saperavi-molodoe-2016', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 3),
(308, 'Саперави Резерв', 2900, 'Дзитоев Саперави Резерв крансое сухое 2013', '', 'Вино изготовлено из отборного винограда, брожение в бочке, выдержка в дубе 12 месяцев. После выдержки вино отдыхает в бутылке около года.<br>', '<p>Неизменный спутник кавказской кухни, а также любых жирных мясных блюд и сыров.</p>', '<p>Цвет вина насыщенный гранатовый. Яркий сложный букет с тонами сливы и вишни. Округлое, мягкое, бархатистое во вкусе, с гармоничной кислотностью и мягким послевкусием.<br></p>', 'Подавать при температуре 16С.<br>', 'KD Saperavi rezerv 2013', 15, 61, 300, 40, 8, 1, 14, 2013, 0.75, 1, 95, 'wines/June2020/dzitoev/Дзитоев  саперави2013.JPG', 'vino-dzitoev-saperavi-rezerv-2013', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 10);
INSERT INTO `wines` (`id`, `title`, `price`, `meta_title`, `description`, `production_feature`, `combination`, `feature`, `innings`, `model`, `winery_id`, `grape_sort_id`, `edition`, `manufacturer_id`, `region_id`, `color_id`, `fortress`, `year`, `volume`, `sugar_id`, `count`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`, `excerpt_id`) VALUES
(309, 'Мускатное Молодое', 450, 'Игристое вино Кубань-Вино Шато Тамань Мускатное Молодое белое полусухое 2017', '', 'Кратковременный настой в прессах с предварительным охлаждением мезги винограда. Производство игристого вина за одно брожение из свежего виноградного сусла.<br>', 'Подавать сильно охлажденным с бисквитами и тирамису.<br>', '<p>Цвет светло-соломенный с оттенками от зеленоватых до золотистых. Яркий сортовой букет с доминирующей цитронно-мускатной нотой. Вкус свежий, пикантный.<br></p>', 'Подавать при температуре 06 - 08 С .<br>', 'Kuban-Vino muscatnoe molodoe 2017', 48, 33, 40000, 41, 2, 2, 12, 2017, 0.75, 9, 100, 'wines/June2020/кубань вино/ШТ Молодое Мускатное Белое Полусухое 2017 0,75л.jpg', 'vino-Kuban-Vino-muscatnoe-molodoe-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 3),
(310, 'Красностоп Молодое', 450, 'Игристое вино Кубань-Вино Шато Тамань Красностоп Молодое розовое полусухое 2017', '', 'Кратковременный настой в прессах с предварительным охлаждением мезги винограда. Производство игристого вина за одно брожение из свежего виноградного сусла.<br>', 'Идеальной парой станут чернослив с орехами под сливочным соусом, клубника с ванильным мороженым.<br>', '<p>Цвет розовый от малинового до нежно брусничного оттенка. Аромат с цветочным оттенком и преобладанием фруктово-ягодных тонов. Вкус яркий, слаженный, с преобладанием фруктово-ягодных тонов с цветочными оттенками.<br></p>', 'Подавать при температуре 06 - 08 С .<br>', 'Kuban-Vino krasnostop molodoe 2017', 48, 24, 40000, 41, 2, 3, 12, 2017, 0.75, 9, 99, 'wines/June2020/кубань вино/ШТ Молодое Красностоп Розовое Полусухое 2017 0,75л.jpg', 'vino-Kuban-Vino-krasnostop-molodoe-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 3),
(311, 'Рислинг Молодое', 450, 'Игристое вино Кубань-Вино Шато Тамань Рислинг Молодое белое брют 2017', '', 'Кратковременный настой в прессах с предварительным охлаждением мезги винограда. Производство игристого вина за одно брожение из свежего виноградного сусла.<br>', 'Подавать сильно охлаждённым в тандеме с холодными закусками из рыбы и белого мяса.<br>', '<p>Цвет светло-соломенный с оттенками от зеленоватых до золотистых. Яркий сортовой букет сочетающий в себе аромат луговых трав и цветов. Вкус богатый, насыщенный.<br></p>', 'Подавать при температуре 06 - 08 С .<br>', 'Kuban-Vino risling molodoe brut 2017', 48, 56, 40000, 41, 2, 2, 12, 2017, 0.75, 2, 100, 'wines/June2020/кубань вино/ШТ Молодое Рислинг Белое Брют 2017 0,75л.jpg', 'vino-Kuban-Vino-risling-molodoe-brut-2017', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 3),
(312, 'Траминер', 600, 'Вино Кубань-Вино Шато Тамань Гранд десерт Траминер десертный выдержанный 2014', '', '<p>Охлаждение мезги. Настаивание сусла на мезге. Подбраживание сусла до накопления спирта естественного брожения 2С, внесение спирта этилового для остановки брожения и корректировки кондиций. Выдержка не менее 18 месяцев. Остаточный сахар 160 г/дм.</p>', 'Подойдёт к сырам, фруктам, ягодам и различным десертам (тортам, бисквитам, мороженому).<br>', '<p>Цвет янтарно-золотистый. Аромат чистый, с тонами лепестков чайной розы. Вкус полный, гармоничный, насыщенный с медовыми тонами и ярко выраженной десертной доминантой.<br></p>', 'Подавать при температуре 10 - 16 С .<br>', 'Kuban-Vino traminer 2014', 48, 74, 40000, 41, 0, 2, 16, 2014, 0.5, 4, 100, 'wines/June2020/кубань вино/ШТР Гранд Десерт Траминер 2014 0,5л.jpg', 'vino-Kuban-Vino-traminer-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 48),
(313, 'Нектар (Анчелотта)', 600, 'Вино Кубань-Вино Шато Тамань Гранд десерт Нектар десертный выдержанный 2014', '', '<p>Охлаждение мезги. Настаивание сусла на мезге. Подбраживание сусла до накопления спирта естественного брожения, внесение спирта этилового для остановки брожения. Выдержка не менее 18 месяцев. Остаточный сахар 160 г/дм.</p>', 'Идеально сочетается с тортами и мороженым, но также станет прекрасным завершением обеда или ужина.<br>', '<p>Цвет от интенсивно-красного до тёмно-рубинового. Аромат свойственный сорту, с преобладанием спелых фруктов на фоне сливочного крема и шоколада. Вкус полный, бархатисто-терпкий и долгий.<br></p>', 'Подавать при температуре 10 - 16 С .<br>', 'Kuban-Vino nektar 2014', 48, 6, 40000, 41, 2, 1, 16, 2014, 0.5, 4, 100, 'wines/June2020/кубань вино/ШТР Гранд Десерт Нектар 2014 0,5л.jpg', 'vino-kuban-wino-nectar-2014', '', '', 'ACTIVE', 0, '2020-06-18 08:35:03', '2020-06-18 08:35:03', 48),
